INSERT INTO `npc_text_localized` VALUES ('8321', 'deDE', 'Der Inhalt der Seiten dieses Buch scheint in ständiger Bewegung inbegriffen zu sein.Ihr fühlt Euch, als würden Euch Eure Gedanken entrissen und von dem Buch verzehrt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
