INSERT INTO `npc_text_localized` VALUES ('8333', 'deDE', '', 'Bleibt unauffällig, ich warte darauf, dass der König mir eine Audienz gewährt. Das Warten macht mir aber nichts aus, ich fühle mich zwischen all diesen stämmigen Wachen recht sicher.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8335', 'deDE', 'Seid gegrüßt, Freund! Könnte ich Euch eventuell an einigen ektoplasmischen Kondensatoren interessieren? Oder vielleicht an einem hübschen omnidirektionalen spektralen Assimilator?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
