INSERT INTO `npc_text_localized` VALUES ('8349', 'deDE', '', 'Die Blastenheimer 5000 Ultrakanone ist eine hochmoderne Abschussanlage für Einzelpersonenprojektile. Die von dieser Kanone abgefeuerte Ladung erreicht beinahe die Geschwindigkeit eines Drachen und sie', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8350', 'deDE', '', 'Man sagt, Kel\'Thuzad sei auf der Suche nach dem legendären Atiesh, dem Hohestab des Wächters.$B$BJa, wir sprechen in der Tat von dem einen Stab, der vor langer Zeit in Medivhs Besitz war.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8351', 'deDE', 'Achtet auf Euer Handeln, $R. Wir von der Bruderschaft des Lichts sind lange nicht so nachsichtig wie unsere Glaubensbrüder, wenn es darum geht, Gerechtigkeit gegenüber denen walten zu lassen, die unse', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
