INSERT INTO `npc_text_localized` VALUES ('8354', 'deDE', '', 'Die Argentumdämmerung mag vielleicht eine Bande unfähiger Idealisten sein, aber Ihr Anführer weiß, dass sie unterlegen sind. Ich wurde als ein Bote des Scharlachroten Kreuzzugs hierher entsandt... Als', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8355', 'deDE', 'Mein Titel ist irreführend. Eigentlich bin ich eine recht liebenswerte Person, wenn man mich erst einmal richtig kennengelernt hat.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8356', 'deDE', 'Beinahe hätten sie meinen Bruder Loksey zu dieser Versammlung geschickt. Dabei schafft er es kaum auf seine Hunde aufzupassen, wie sollte er dann den Scharlachroten Kreuzzug in angemessener Weise repr', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8357', 'deDE', 'Ich habe Euch erwart...$B$B<Rimblat hustet.>$B$BEntschuldigt, alte Gewohnheiten, wisst Ihr. Ich bin Rimblat vom Irdenen Ring.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8358', 'deDE', '', 'Seid gegrüßt, $C. Bitte entschuldigt mich, da ich noch ein Wörtchen mit Rimblat zu reden habe. Er ist der Meinung, dass selbst diese Länder noch gerettet werden können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8359', 'deDE', 'Was wollt Ihr von mir, Narr? Geht mir aus den Augen, bevor ich mich vergesse und ich Euch aus erster Hand darlege, wieso man mich den Zornwirker nennt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8366', 'deDE', 'Ich bin, was man gewöhnlich als einen Kapitalisten bezeichnet, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8374', 'deDE', 'Oh, hallo... äh, wie war gleich Euer Name?$B$BEgal, was bringt Euch in meine zweite Heimat, hier draußen auf dem wunderschönen Lande?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
