INSERT INTO `npc_text_localized` VALUES ('8390', 'deDE', '', 'Es ist eine Geschichte, sie ist Teil einer Legende. Doch nun habe ich weder genug Zeit, noch neige ich dazu, sie Euch in aller Ausführlichkeit zu erzählen. Ihr müsst nur wissen, dass Medivh der letzte', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8391', 'deDE', '', 'Die Verderbnis eines gefallenen Titanen. Die korrumpierte Macht des Zerstörers. Letzten Endes wurde der Stab aus der Saat des Hasses geformt. Das ist es, was mich hierher gebracht hat...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8392', 'deDE', '', 'Nach dem Niedergang von Karazhan entsandten die Kirin Tor eine Magierabordnung, um den Turm zu durchsuchen und alle Gegenstände von Wert mitzunehmen... um sie später \"erforschen\" zu können. Eben diese', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8393', 'deDE', '', 'Was folgte, war eine Serie tragischer Ereignisse, in deren Verlauf jeder Magier der Kirin Tor, der mit Atiesh in Kontakt kam, verstarb.$B$BIhr seht, der Stab hat sein eigenes Bewusstsein, welches ihm ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8394', 'deDE', '', '<Angela nickt.>$B$BNach all dem Tod beschlossen die Sechs, dass es das Beste wäre, wenn man den Stab hinter einer Barriere aus Magie versiegeln würde. Gleichzeitig ernannte man eine Hüterin: Eine Wäch', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8395', 'deDE', '', 'Viel mehr gibt es eigentlich nicht zu erzählen. Dalaran war vollständig durch Archimonde zerstört worden, der Stab verloren, vermutlich durch den Schutzmechanismus der Barriere in tausend Stücke zerbr', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8396', 'deDE', '', 'Nach dem Niedergang von Dalaran verließ ich die Kirin Tor. Ich habe jahrelang über Atiesh gewacht, nur damit mir auf einen Schlag mein Platz und meine Aufgabe unter den Kirin Tor genommen wurden. Ich ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8397', 'deDE', '', 'Ich habe die Fundorte der verschiedenen Bruchstücke zurückverfolgt und bin dabei auf Kel\'Thuzads Pläne gestoßen, den Stab wieder zusammenzusetzen. Er ist bereits im Besitz der meisten Teile des Stabs,', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8398', 'deDE', 'Es ist pure Macht, Schwachkopf.$B$B<Mataus\' Augen verdunkeln sich.>$B$BDiese Narren entsenden Idioten wie Euch nach Naxxramas, um nach entweihten Rüstungsteilen Ausschau zu halten, damit sie diese läu', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8399', 'deDE', 'Ich mache Euch nichts vor, Wurm. Die Finsternis, die diese Gegenstände erfüllt, könnte uns beide mit Leichtigkeit zerstören. Dies erklärt den äußerst hohen Preis. Sollte ich sterben, so werde ich zumi', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8401', 'deDE', 'Seid gegrüßt, $GReisender:Reisende;. Man hat mich angewiesen jeden, der einen Schlüssel für Karazhan bei sich trägt, als einen geehrten Gast des Meisters zu behandeln. Falls Ihr Fragen habt, scheut Eu', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8402', 'deDE', 'Wir sind zu gnädig mit den Getriebenen. Wir sollten sie alle mit Schwertern durchbohren.$B$BIhre Dreistigkeit beim Ankerplatz der Sonnensegel ist unverzeihlich.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8403', 'deDE', '', 'Ich habe heute einen langen Tag damit verbracht, gegen den endlosen Strom der hirnlosen Diener der Geißel anzukämpfen, die die Todesschneise entlangziehen. Ich hoffe, Ihr seid hier, um mir zur Hand zu', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8404', 'deDE', 'Ah, Gäste des Meisters. Ich hoffe, Ihr achtet hier drinnen immer darauf, wo ihr hintretet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8406', 'deDE', 'Zur Hölle mit diesem Ungeziefer! Ich nehme nicht an, dass Ihr mir in dieser Sache behilflich sein könntet?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8408', 'deDE', 'Diese dreckigen Biester sind hier überall zu finden.$B$BWenn Ihr lieber im Freien jagt, findet Ihr die größte Anzahl von ihnen im Pestwald und im Schreckenstal sowie eine größere Gruppe im Schreckenst', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8409', 'deDE', 'Seid Ihr vielleicht dumm oder sowas? Habt Ihr etwa die KNOCHIGEN SKELETTMONSTER auf Eurem Weg hierher übersehen, die sich hier überall in der Landschaft herumtreiben?$B$B Wenn Ihr lieber im Freien jag', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8410', 'deDE', '', 'Ein weiterer wundervoller, trübsinniger Tag hier in Tristessa, findet Ihr nicht auch, $C?$B$BWas kann ich für Euch tun?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8411', 'deDE', 'Ah, diese Freiheit durch die Lüfte zu segeln, $C. Was kann ich heute für Euch tun?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8412', 'deDE', 'Hallo, $G mein Freund:meine Freundin;. Wie kann ich Euch heute behilflich sein?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8413', 'deDE', '', 'Seid Ihr heute Richtung Tristessa unterwegs, $C, oder kann ich Euch anderweitig behilflich sein?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8414', 'deDE', '', 'Ah, eine gute Frage.$B$BFalls Ihr lieber im Freien Elementare jagen wollt, dann seid Ihr hier in den Pestländern genau richtig, oder sucht in Azshara, der brennenden Steppe, dem Teufelswald, Silithus,', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8415', 'deDE', 'Ist das eine ernst gemeinte Frage? Ihr wisst es wirklich nicht?$B$BWenn Ihr Euch zu den Feiglingen zählt, die Angst haben und sich davor fürchten, ihre Hände schmutzig zu machen, dann empfehle ich Euc', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8416', 'deDE', '', 'Wenn Ihr gerne im Freien seid, sind die Treants im Teufelswald und die Peitscher im Krater von Un\'Goro hervorragende Quellen für Wedel.$B$BSolltet Ihr stattdessen das Risiko lieben, dann werdet Ihr im', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8417', 'deDE', '$C, seht was sie unserem geliebten Land angetan haben. Die Geißel muss vernichtet werden!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8418', 'deDE', '', 'Es wird etwas Arbeit erfordern, doch ich denke, wir können das Leben dieses Blutelfen retten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8419', 'deDE', 'Wer seid Ihr? Ich fühle... mich so... kalt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8421', 'deDE', '', 'Es ist eine Schande, wie viel wir Sin\'dorei von unserer einstigen Heimat eingebüßt haben. Doch schon bald werden wir aufs Neue erstarken und unsere südlichen Ländereien aus dem Würgegriff der Geißel b', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8422', 'deDE', 'Ich bin viel zu beschäftigt, um jetzt noch \'rate mal wer zum Essen kommt\' mit Euch zu spielen, $R. Entweder wollt Ihr etwas von mir oder Ihr verschwindet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8423', 'deDE', 'Der Hochexekutor und Dame Auriferous lassen mich warten, aber ich habe keine Zeit! Meine Freunde sind dabei, da draußen zu sterben, $C!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8424', 'deDE', '', 'Es ist schon über einen Tag her, dass ich Waldläuferin Salissa in den Süden zur Enklave und Waldläufer Lethvalin Richtung Westen nach Tristessa entsandt habe. Habt Ihr vielleicht einen von ihnen geseh', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8425', 'deDE', 'Manchmal habe ich das Gefühl, als würde ich nie mehr ein einfacher Beamter im Dienst von Dame Auriferous sein. Der Krieg lässt mich nicht los.$B$BGlücklicherweise gibt es da noch solche wie Euch, die ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8426', 'deDE', 'Ah hier ist $Gder Held:die Heldin; der Stunde! Es tut gut, Euch wiederzusehen, $N! Ich hoffe, Ihr bekämpft weiterhin die Geißel, wann immer Ihr sie findet?', 'Ah hier ist $Gder Held:die Heldin; der Stunde! Es tut gut, Euch wiederzusehen, $N! Ich hoffe, Ihr bekämpft weiterhin die Geißel, wann immer Ihr sie findet?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8428', 'deDE', 'Mir ist zu Ohren gekommen, dass es Euch gelungen ist, Waldläuferin Valanna und dem Leutnant bei Zeb\'Sora zu helfen.$B$BDanke, $C. Ich denke, ich sollte mich jetzt auf den Weg zurück zur Enklave der We', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8429', 'deDE', 'Was treibt Euch hierher, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8431', 'deDE', 'Ah, seid gegrüßt, $R. Wie Ihr sehen könnt, ist mein Geschäft noch nicht geöffnet. Nun, entschuldigt mich bitte, aber ich bin sehr beschäftigt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8432', 'deDE', 'Welch eine Freude, Euch wiederzusehen! Nein, ehrlich. Ihr seid der Grund, warum ich hier bergeweise Geld scheffle.$B$BNun, was kann ich Euch heute verkaufen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8433', 'deDE', '', 'Willkommen in Tristessa, $C. Ich muss mich für die Zustände hier entschuldigen. Wie Ihr sicherlich sehen könnt, hatten wir hier einige Schwierigkeiten, doch die Verlassenen und ihre Lady Sylvanas Wind', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8434', 'deDE', 'Die Zeit ist gekommen, nun da die Horde und die Allianz gemeinsam Richtung Norden blicken, um sich gemeinsam der Invasion der Geißel in den Weg zu stellen. In den vergangenen Tagen wurden zahlreiche O', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8435', 'deDE', 'Keine Angst vor dem Schwert, Schwächling. Es ist der Wolf im Schafspelz, vor dem Ihr Euch fürchten solltet!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8438', 'deDE', 'Schrecklich, was ihnen zugestoßen ist, meine kleinen Lasttiere... Metz wird mir das Fell über die Ohren ziehen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8439', 'deDE', '', 'Es ist von größter Wichtigkeit, dass wir herausfinden, was die Nachtelfen vorhaben, $C! Bitte konzentriert all Eure Anstrengungen auf dieses Ziel.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8440', 'deDE', '', 'Wir sind mit Euren Bestrebungen gegen die Nachtelfen sehr zufrieden. Durch das Aufdecken ihrer Verschwörung, uns mit Hilfe ihrer sogenannten Mondkristalle und der Leyliniennexi auszuspionieren, habt I', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8441', 'deDE', 'Seid Ihr die Verstärkung? Die Nachtelfen wurden in die Flucht geschlagen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8442', 'deDE', 'Gut, dass Ihr den Mondkristall bei An\'owyn deaktiviert habt, $C! Ihr seid jederzeit willkommen, wenn Ihr Euch hier für meine Untersuchungen interessiert.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8445', 'deDE', 'Wie soll ich unter diesen Bedingungen nur arbeiten?! Und all die scheußlichen Verlassenen hier! Ich weine für Tristessa und Quel\'Thalas!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8446', 'deDE', 'Ja? Was wollt Ihr, $R? Könnt Ihr nicht sehen, dass ich beschäftigt bin?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8447', 'deDE', '$R, berichtet! Welche Neuigkeiten von Bedeutung habt Ihr in Erfahrung gebracht? Macht schnell... Dame Auriferous und ich befinden uns mitten in einer strategischen Konferenz.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8448', 'deDE', '', 'Willkommen an der Kapelle des hoffnungsvollen Lichts, $GFremder:Fremde;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8452', 'deDE', 'Unsere Karawanen verkehren zweimal am Tag zwischen hier und der Front und ich will verdammt sein, aber wenn ich mich nicht täusche, schmeißt die Geißel ihnen alles entgegen was sie hat, nur um Didier ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8454', 'deDE', 'Ich habe zuviel zu tun und zu wenig Zeit um fertig zu werden, $R! Geht mir aus dem Weg.', 'Ich habe zuviel zu tun und zu wenig Zeit um fertig zu werden, $R! Geht mir aus dem Weg.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8455', 'deDE', 'Ich habe viel zu tun und nur wenig Zeit, $R! Also lasst mich bitte in Ruhe.$B$BSolltet Ihr Euch mit Madenschleim oder Ghulfäulnis infiziert haben, dann wendet Euch an mich.', 'Ich habe viel zu tun und nur wenig Zeit, $R! Also lasst mich bitte in Ruhe.$B$BSolltet Ihr Euch mit Madenschleim oder Ghulfäulnis infiziert haben, dann wendet Euch an mich.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8456', 'deDE', '<Der Blutelf erlangt rasch wieder das Bewusstsein, als Ihr ihm das Gebräu einflößt.>$B$BIch verdanke Euch mein Leben, $C. Ohne Eure Hilfe wäre ich der schmerzhaften Folter der Geißel erlegen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8457', 'deDE', '<Der Blutelf liegt in einer unnatürlichen Starre auf dem Boden.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8458', 'deDE', 'Seid gegrüßt, $R. Ich bin Botschafter Sonnenleid von der Delegation der Sin\'dorei, oder Blutelfen, wie wir allgemein genannt werden. Ich bin sehr zuversichtlich, dass diese neue Verbindung zwischen un', 'Seid gegrüßt, $R. Ich bin Botschafter Sonnenleid von der Delegation der Sin\'dorei, oder Blutelfen, wie wir allgemein genannt werden. Ich bin sehr zuversichtlich, dass diese neue Verbindung zwischen un', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8459', 'deDE', '', '<Die Verlassene liegt zusammengerollt auf dem Boden, ihr Gesicht zu einer starren Maske verzerrt. Es scheint nicht, als sei sie in der Lage, aus eigener Kraft aufzustehen.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8460', 'deDE', '', 'Ahhhh... diese Schmerzen in meinem Kopf bringen mich um.$B$BSie müssen Euch zu meiner Rettung geschickt haben. Das hat ganz schön gedauert! Habt Ihr irgendeine Vorstellung davon, was sie den Gefangene', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8461', 'deDE', '<Der Blutelf liegt schlaff auf dem Tisch. Er atmet noch, scheint aber unter dem Einfluss eines starken lähmenden Giftes zu stehen.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8462', 'deDE', 'Schön, Euch zu sehen, $R. Ich bin hier, um dem Hochexekutor jederzeit mit Rat und Tat zur Seite zu stehen. Ich bin mir sicher, dass wir die Horde mit vereinten Kräften zu etwas noch Stärkerem machen k', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8463', 'deDE', '<Varnis wird von einem heftigen Husten geschüttelt, als das Regenerationsgebräu zu wirken beginnt.>$B$BDanke, dass Ihr mich gerettet habt. Ich muss sofort zurück nach Tristessa!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8464', 'deDE', 'Die frische Luft, die von Zeit zu Zeit aus dem Immersangwald weht, verdirbt mir wirklich den Magen, wenn Ihr versteht, was ich meine.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8466', 'deDE', '', 'Wenn es eine Angelegenheit von Wichtigkeit ist, $C, wäre es möglicherweise angebracht, eine Audienz bei der Dame Auriferous zu erbitten. Doch ich muss Euch warnen, sie ist eine viel beschäftigte Frau ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8467', 'deDE', '', 'Normalerweise würde ich niemals direkt mit Euch sprechen; vielmehr würdet Ihr eine Audienz bei Mylady erbitten. Wie auch immer, ich habe Euch eine direkte Order erteilt; also sprecht, was gibt es Wich', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8468', 'deDE', '', '$C, Dame Auriferous lässt Euch durch mich ausrichten, dass sie über die Dienste, die Ihr uns in der Vergangenheit geleistet habt, äußert zufrieden ist. Es ist ihre Hoffnung, dass Ihr auch weiterhin, a', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8469', 'deDE', 'Auf diesem Schild befinden sich verschiedene Ankündigungen sowie Vermerke über Belohnungen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8470', 'deDE', '', 'Vor uns liegt eine wichtige Aufgabe, $N. Wir müssen den Feind angreifen, solange er noch unachtsam ist. Zeigen wir ihnen, dass die Sin\'dorei niemals vergessen, noch vergeben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8471', 'deDE', 'Die Fluten des Krieges sind zurückgekehrt. Aus dem kalten Norden heranrückend haben die Nekropolen des Lichkönigs begonnen, unsere Länder zu belagern. Er und sein finsterer General Khel\'Thuzad haben f', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8472', 'deDE', '', 'Nun, das ist in der Tat ein Problem! Wie soll ich bloß die Erzproben für die Magister sammeln, wenn die Minen durch Gnollabschaum überrannt wurden?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8474', 'deDE', 'Wo kann sie nur sein? Ich habe meinen Lehrling, Shatharia, vor einiger Zeit in die Grubenlichtminen im Westen geschickt. Wir brauchen die Erzproben, und zwar schnell, ansonsten wird unser Experiment f', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8475', 'deDE', 'Ohne rechtzeitige Lieferung des Grubenlichterzes wären unsere Forschungen umsonst gewesen.$B$BWartet nur, bis Shatharia wieder hier auftaucht. Ich habe einige sehr interessante Aufgaben für sie, da kö', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8476', 'deDE', '', 'Die Sin\'dorei haben in den vergangenen Jahren einfach zu viel verloren. Unser Land, unser Zuhause, selbst unsere Identität...$B$BEs ist an der Zeit, dass wir all das zurückholen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8477', 'deDE', 'Bevor wir Hindernisse wie die Geißel und die Amanitrolle überwinden können, müssen wir erst einmal mit den Problemen in unseren eigenen Reihen fertig werden. Die Getriebenen stellen schon viel zu lang', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8478', 'deDE', '', 'Glaubt Ihr, Ihr könntet mich hier herausbringen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8479', 'deDE', 'Unter den zahlreichen Aufzeichnungen der Weltenwanderer, Checklisten und andere Informationen, fällt besonders ein Stück auf. Es ist die Zeichnung eines abscheulichen Trolls, vielleicht sogar die Furc', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8481', 'deDE', 'Fürs Erste haben wir diese Gegend vom Einfluss der Geißel befreit. Ich fürchte allerdings, es ist nur eine Frage der Zeit, bevor die Truppen der Geißel zurückkehren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8482', 'deDE', 'Es scheint, als wäre die Geißel bis nach Tanaris vorgedrungen. Sie haben eine nicht unbedeutende Anzahl ihrer Nekropolen und andere Streitkräfte dorthin verlegt.$B$BVerbleibende Nekropolen: $2283W', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8484', 'deDE', 'Wenn in die brennende Steppe nicht bald Verstäkung entsand wird, fürchte ich, dass die Geißel dort eine Operationsbasis einrichten wird. Falls es in Eurer Macht steht, solltet Ihr den dortigen Verteid', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8485', 'deDE', '', 'Es kann von großem Vorteil sein, den Feind kennenzulernen. Nehmt Euch diese Lektion zu Herzen, $N.$B$BJe mehr wir über die Geißel lernen, desto besser sind wir gewappnet, um sie zu besiegen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8486', 'deDE', 'An den angegriffenen Orten hat die Geißel kleinere Stützpunkte eingerichtet. Diese stehen in ständigem Kontakt zu den über ihnen schwebenden Nekropolen, welche die Stützpunkte regelmäßig mit Nachschub', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8487', 'deDE', '', 'Bleibt solange, wie ihr möchtet, in der Enklave der Weltenwanderer, $C. Wir heißen jeden willkommen, der unsere Sache in den Geisterlanden unterstützt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8488', 'deDE', 'Verstreute arkane Kräfte können seltsame Dinge anrichten, mit dem Land, den Elementen, sogar mit den Geistern der Toten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8489', 'deDE', 'Der Hochexekutor ist ein viel beschäftigter Mann, $C. Möglicherweise kann ich Euch stattdessen weiterhelfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8490', 'deDE', 'Ihr habt hervorragende Dienste geleistet, $N. Dass sowohl die Gnolle in den Grubenlichtminen als auch die mumifizierten Trolle in den Amanikatakomben erledigt sind, hat unseren Feinden einen schweren ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8491', 'deDE', 'Es tut gut, Euch wiederzusehen, $G Kumpel : Schönheit;! Wir arbeiten gut zusammen, $G man sieht sich : und hoffentlich haben wir noch oft Gelegenheit dazu;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8492', 'deDE', 'Ich habe von diversen Quellen erfahren, wie Lady Sylvanas auf Eure Wiederentdeckung ihrer Halskette reagiert hat. Ich glaube immer noch, dass Ihr das Richtige getan habt; macht Euch über die Reaktion ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8493', 'deDE', 'Was soll ich sagen, $C? Ihr seid $G ein wahrer Held : eine wahre Heldin;! Ihr habt nicht nur zahlreiche meiner Waldläufer gerettet, sondern den Waldschattentrollen auch einen maßgeblichen Schlag verse', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8495', 'deDE', 'Eure Hilfe bezüglich der Waldschattentrolle erweist sich als unbezahlbar, $C. Macht weiter so. Vielleicht können wir uns schon bald wieder um unser Vorgehen gegen die Geißel kümmern.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8496', 'deDE', 'Ihr habt den Waldschattentrollen einen gewaltigen Schlag versetzt, $N. Ich hoffe nur, dass wir unsere Kräfte so schnell wie möglich sammeln und sie dann endgültig vernichten können, bevor sie Verstärk', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8498', 'deDE', '', 'Gewiss, $N.$B$BIhr müsst mir nur sagen, über welchen Ruf Ihr mehr erfahren möchtet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8499', 'deDE', '', 'Gewiss, $N$B$BAbenteurer, die bei der Argentumdämmerung einen wohlwollenden Ruf genießen, müssen 40 Insignien vorweisen, um Gegenstände von überragender Qualität erwerben zu können. Um Zugriff auf noc', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8500', 'deDE', '', 'Gewiss, $N$B$BAbenteurer, die bei der Argentumdämmerung einen respektvollen Ruf genießen, müssen 14 Insignien vorweisen, um Gegenstände von überragender Qualität erwerben zu können. Um Zugriff auf noc', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8501', 'deDE', 'Die Lage ist düsterer, als viele es bis jetzt glauben wollten. Die Verlassenen sind hier, um uns im Kampf gegen unseren gemeinsamen Feind, die Geißel, beizustehen.$B$BGlaubt mir, wenn ich Euch Folgend', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8502', 'deDE', '', 'Gewiss, $N$B$BAbenteurer, die bei der Argentumdämmerung einen ehrfürchtigen Ruf genießen, müssen 12 Insignien vorweisen, um Gegenstände von überragender Qualität erwerben zu können. Um Zugriff auf noc', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8503', 'deDE', '', 'Gewiss, $N$B$BAbenteurer, die bei der Argentumdämmerung einen freundlichen Ruf genießen, müssen 60 Insignien vorweisen, um Gegenstände von überragender Qualität erwerben zu können. Um Zugriff auf noch', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8504', 'deDE', 'Diese Runensteine beschützen die Länder Immersangs vor der Besudelung durch die Geißel. Der Runenstein des Westens hat schon seit langem versagt und uns dazu gezwungen, den Boden rund herum niederzubr', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8505', 'deDE', '', 'Nun, sicherlich seid Ihr nicht den ganzen Weg hierher gekommen, um bei einer Tasse Tee zu plaudern. Schauen wir doch mal, wie Ihr uns aus dieser Situation helfen könnt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8513', 'deDE', 'Meisterhandwerker Omarion verschwand eines Nachts spurlos. Er hat sich einfach so in Luft aufgelöst! Ha, und nun muss ich hier alleine zusehen, wie ich unsere Jungs an der Front mit Nachschub versorge', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8515', 'deDE', 'Die Sin\'dorei und die Verlassenen verfolgen das gleiche Ziel in den Geisterlanden, $N. Zusammen werden wir die Geißel besiegen und wer weiß... vielleicht werden sich die Verlassenen ja als wertvolle V', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8527', 'deDE', 'Die ersten Schritte unseres Kampfes gegen die Geißel waren erfolgreich. Wir konnten einige Siege verbuchen und ich hoffe, dass meine Unterstützung uns weitere erfolgreiche Schlachten bringen wird.', 'Die ersten Schritte unseres Kampfes gegen die Geißel waren erfolgreich. Wir konnten einige Siege verbuchen und ich hoffe, dass meine Unterstützung uns weitere erfolgreiche Schlachten bringen wird.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8534', 'deDE', 'Unsere Sankten entziehen den Strömungen von Leylinien, die unser Land durchziehen und mit bloßem Auge nicht sichtbar sind, arkane Macht.$B$BNach dem Verlust des Sonnenbrunnens stellen sie eine für uns', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8535', 'deDE', 'Es ist ein Sinnbild für die Unerschütterlichkeit unseres Volkes, dass Morgenluft noch steht. Wir werden der Geißel niemals wieder den Sieg überlassen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8536', 'deDE', 'Aus dem Wech, Landratte! Revilgaz hat mir den Galgen erspart, doch meine jetzige Strafe ist nicht viel besser.', 'Aus dem Wech, Landratte! Revilgaz hat mir den Galgen erspart, doch meine jetzige Strafe ist nicht viel besser.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8537', 'deDE', '', 'Ihr macht besser keinen Ärger, $N!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8538', 'deDE', 'Was kann ich für Euch tun, $N? Beeilt Euch, ich habe nicht den ganzen Tag Zeit!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8539', 'deDE', 'Was kann ich für Euch tun, $N? Nein, wartet! Was könntet Ihr für mich tun?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8548', 'deDE', 'Dar\'Khan glaubt, dass wir uns der Geißel widerstandslos ausliefern werden. Wir werden ihm das Gegenteil beweisen, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8549', 'deDE', 'Seht Ihr denn nicht, dass ich versuche, mich mit dieser Dame zu unterhalten? Oh, na gut, sie scheint bereits anderweitig beschäftigt zu sein.$B$B Ich handle ausschließlich mit den feinsten Tropfen, so', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8550', 'deDE', 'Ist das eine Liste von Saltherils Hafen? Natürlich wird Euer Lordschaft eine Flasche meines feinsten Tropfens wollen. Selbstverständlich biete ich jedem Freund von Lord Saltheril einen besonderen Prei', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8551', 'deDE', 'Wir haben in $2219W Schlachten über die Geißel gesiegt. Macht Euch bereit, $N, dieser Krieg ist noch lange nicht gewonnen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8552', 'deDE', '', 'Wenn Ihr Geld habt, habe ich Essen und Trinken, um Euren Appetit zu befriedigen. Jedes einzelne Stück Kupfer wird zum Wohle der Zuflucht verwendet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8553', 'deDE', '', 'Habt Ihr da eine Liste von Lord Saltheril? Ja, ich kann Euch eine Lieferung Appetithäppchen verkaufen, frisch aus dem Ofen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8556', 'deDE', 'Ich verkaufe allerlei Waren, $C. Seht Euch in aller Ruhe um.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8557', 'deDE', 'Eine Auflistung... wie interessant. Ah ja, die Ladung Feuerwerk für Lord Saltheril. Er hat noch nicht dafür bezahlt. Seid Ihr gekommen, um sie in seinem Namen zu erwerben, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8558', 'deDE', '', 'Wo kommen nur alle diese Untoten her? Es scheint mir, als würden für jeden Ghul und jedes Skelett, die wir töten, zwei neue erscheinen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8561', 'deDE', '$C, Ihr habt nicht zufällig einen Kurier auf Eurem Weg hierher gesehen? Wir erwarten eine Nachricht aus Silbermond und der Kurier sollte schon längst hier sein!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8564', 'deDE', 'Manchmal stehe ich hier, sehe über den Wald und den See hinweg... sehe das Land, wie es einst einmal war... grün und voller Leben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8565', 'deDE', 'Wir müssen einen besseren Weg finden, um mit diesen schmutzigen Waldschattentrollen fertig zu werden!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8566', 'deDE', 'Ich muss schon sagen, Ihr habt Euch beim Überbringen meines Gifts und beim Umgang mit den Trollen als recht rücksichtslos erwiesen, $C. Ihr habt die Natur $G eines recht talentierten Mannes : einer re', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8569', 'deDE', '', 'Wir müssen stets wachsam sein, $C! Eingekeilt zwischen Dar\'Khan und seiner Geißel sowie den durch Zul\'Aman unterstützten Waldschattentrollen, schaffen wir es so gerade uns über Wasser zu halten. Dabei', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8570', 'deDE', '', 'Ich bin froh, dass Ihr hier seid, um zu helfen, $C. Je eher wir uns um die Trolle kümmern können, desto schneller können wir die Geißel wieder dahin zurückschicken, wo sie hergekommen ist.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8571', 'deDE', '', 'Ein paar Trolle erledigt, einige mehr liegen noch vor uns. Ihr habt keine Vorstellung davon, wie dankbar ich für Eure Hilfe bin, $N! Bevor Ihr hierher kamt, hätte ich mir niemals träumen lassen, dass ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8573', 'deDE', 'Die Geißel hat nur wenige Truppen gegen die Hauptstädte Azeroths entsandt, während Ihre Hauptstreitkräfte in regelmäßigen Abständen die folgenden Gebiete angreifen:$B$BAzshara, die verwüsteten Lande, ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8575', 'deDE', '', 'Was bringt Euch her, $N? Wir sehen nicht viele Leute in dieser Gegend. Mit gutem Grund, denke ich.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8576', 'deDE', '', 'Wollt Ihr den Beruf der Schmiedekunst erlernen, oder gibt es etwas anderes, dass Ihr mir sagen wollt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8579', 'deDE', '', 'Welch Glück, dass wir am Leben sind, Ihr und ich!!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8580', 'deDE', 'Man sieht nicht allzu viele Lehrlinge hier, oder? Ich habe das Gefühl, dass die Magister uns all die gefährlichen Arbeiten verrichten lassen, die sie lieber nicht selbst machen wollen.', 'Man sieht nicht allzu viele Lehrlinge hier, oder? Ich habe das Gefühl, dass die Magister uns all die gefährlichen Arbeiten verrichten lassen, die sie lieber nicht selbst machen wollen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8582', 'deDE', 'Wir folgen dem Pfad der Reinheit und Gerechtigkeit im Namen des Lichts.', 'Wir folgen dem Pfad der Reinheit und Gerechtigkeit im Namen des Lichts.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8584', 'deDE', 'Im Licht sind wir eins, $G Bruder : Schwester;, dennoch kann ich Euch nicht ausbilden.', 'Im Licht sind wir eins, $G Bruder : Schwester;, dennoch kann ich Euch nicht ausbilden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8585', 'deDE', 'Ihr sucht nach Ausbildung, $G Jäger : Jägerin;?', 'Ihr sucht nach Ausbildung, $G Jäger : Jägerin;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8586', 'deDE', 'Ich bin Jägerlehrer. Sucht einen Lehrer auf, der Euresgleichen ausbildet, $C.', 'Ich bin Jägerlehrer. Sucht einen Lehrer auf, der Euresgleichen ausbildet, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8587', 'deDE', 'Möchtet Ihr noch mehr über den Weg des Kriegers erfahren?', 'Möchtet Ihr noch mehr über den Weg des Kriegers erfahren?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8588', 'deDE', 'Ich würde Euch ja ausbilden, aber Ihr seid kein Krieger.', 'Ich würde Euch ja ausbilden, aber Ihr seid kein Krieger.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8589', 'deDE', 'Wir Verständigen der arkanen und elementaren Wege folgen einem nie enden wollenden Pfad des Entdeckens. Seid Ihr deshalb zu mir gekommen? Ihr sucht weiterhin nach Erleuchtung?', 'Wir Verständigen der arkanen und elementaren Wege folgen einem nie enden wollenden Pfad des Entdeckens. Seid Ihr deshalb zu mir gekommen? Ihr sucht weiterhin nach Erleuchtung?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8590', 'deDE', 'Bei den Naaru, Ihr seid kein Magier! Wenn Ihr etwas lernen möchtet, dann sucht einen Lehrer auf, der Euresgleichen ausbildet, $C.', 'Bei den Naaru, Ihr seid kein Magier! Wenn Ihr etwas lernen möchtet, dann sucht einen Lehrer auf, der Euresgleichen ausbildet, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8591', 'deDE', 'Im Licht rufen wir alle die Macht des Schattens herbei, $G mein Bruder : meine Schwester;. Möchtet Ihr heute noch mehr lernen?', 'Im Licht rufen wir alle die Macht des Schattens herbei, $G mein Bruder : meine Schwester;. Möchtet Ihr heute noch mehr lernen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8592', 'deDE', 'Ich kann Euch zwar nicht ausbilden, $Gjunger:junge:c; $C, aber ich kann Euch meinen Segen verleihen. Wandelt unerschrocken im Licht!', 'Ich kann Euch zwar nicht ausbilden, $Gjunger:junge:c; $C, aber ich kann Euch meinen Segen verleihen. Wandelt unerschrocken im Licht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8616', 'deDE', 'Das Licht kennt viele Wege, $C. Ihr müsst Euch entscheiden, welchem Ihr auf dieser oder irgendeiner anderen Welt folgen wollt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8617', 'deDE', 'Je mehr Ansehen Ihr Euch in unserem Ort verschafft, desto bessere Waren kann ich Euch anbieten, $N. Da es um unser Lager nicht bestens bestellt ist, können wir nur die mit Gütern versorgen, die unser ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8618', 'deDE', 'Ich biete in unserem zerstörten Land so viel Schutz an, wie ich nur kann.', 'Ich biete in unserem zerstörten Land so viel Schutz an, wie ich nur kann.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8619', 'deDE', 'Es ist so gut Euch zu sehen, $G Bruder : Schwester;! Dieser Ort jagt mir eine Gänsehaut ein. Profitiert von den vielen Gelegenheiten, die diese Verlassenen Euch hier bieten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8620', 'deDE', 'Es ist nicht meine Schuld... ES IST NICHT MEINE SCHULD!!!$B$BHätten sie einfach gemacht, was ich ihnen gesagt hatte, wäre jetzt alles in Ordnung. Aber nein! Sie wollten ja nicht hören.$B$BJetzt sind s', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8634', 'deDE', 'Ich handle mit Komfort... vergesst Euren Tatendrang, $R. Fühlt Euch in meinem Gasthaus ganz wie zu Hause...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8638', 'deDE', '', 'Hallo, $C. Falls Ihr es nicht schon längst getan habt, solltet Ihr unbedingt mit Weltenwanderin Solanna sprechen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8645', 'deDE', '', 'Wenn Ihr ein Teil Eurer Zandalarirüstung verloren habt, kann ich Euch vielleicht weiterhelfen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8650', 'deDE', 'Ich bin Euch für Eure Hilfe äußerst dankbar, $C!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8653', 'deDE', 'Ah, Ihr seid also der Finder des Briefs. Ich schätze, ich bin Euch für diese Neuigkeiten über meine Familie zu Dank verpflichtet. Bitte, nehmt Euch ruhig von all den Speisen und Getränken.', 'Ah, Ihr seid also der Finder des Briefs. Ich schätze, ich bin Euch für diese Neuigkeiten über meine Familie zu Dank verpflichtet. Bitte, nehmt Euch ruhig von all den Speisen und Getränken.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8656', 'deDE', 'Hey! Würdet Ihr Euch bitte anstellen, ja? Das Essen der Scherbenwelt kommt mit mir nämlich so gar nicht klar.', 'Hey! Würdet Ihr Euch bitte anstellen, ja? Das Essen der Scherbenwelt kommt mit mir nämlich so gar nicht klar.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8658', 'deDE', 'Sid ist mein Name. Schön, ein paar neue Gesichter zu sehen. Was kann ich für Euch tun?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8659', 'deDE', 'Auch wenn wir in dem gelobten Land angekommen sind, $N, so erwartet uns doch eine Menge an Arbeit. Unsere Leute werden Kael\'thas nicht erreichen, wenn wir uns unüberlegt auf die Scherbenwelt stürzen.$', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8662', 'deDE', 'Was würde ich jetzt nicht alles für einen Heilkristall geben!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8664', 'deDE', '', 'Wie sollen wir nur jemals von hier fortkommen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8665', 'deDE', '', 'Danke, dass Ihr Euch um den See, die Peitscher und die Eulkin gekümmert habt, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8666', 'deDE', '', 'Keiner wird Eure Taten in Frage stellen, $C. Ihr habt getan, was getan werden musste.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8667', 'deDE', 'Es ist gut, Euch lebend und wohlauf zu sehen, $N!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8669', 'deDE', 'Vielen Dank nochmal für das Wiederaufladen unserer Heilkristalle. Ich habe gehört, dass Ihr hier überall bei der Arbeit zur Hand geht. Weiter so!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8672', 'deDE', 'Mirren und ich sind zwei der besten Jäger auf beiden Seiten des Dunklen Portals. Haltet Euch an uns und Ihr könnt vielleicht noch einiges lernen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8673', 'deDE', 'Ich hoffe Ihr seid nicht wegen Bier hier. Wir teilen nicht, basta!$B$BEs ist schließlich nicht unsere Schuld, dass außer uns niemand auf die Idee gekommen ist, Bier mit in die Scherbenwelt zu nehmen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8674', 'deDE', '', 'Bleibt nur zu hoffen, dass nicht die ganze Scherbenwelt so unfruchtbar ist, wie diese Region. Was für ein Gegensatz zu den grünen Ebenen und Wäldern von Immersang.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8675', 'deDE', 'Die dämonischen Mächte in der Scherbenwelt sind wesentlich gewaltiger als Vergleichbares in der alten Welt.$B$BWer sich vor ihnen nicht scheut, sondern stattdessen lernt, sich deren Stärke zu Nutzen z', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8676', 'deDE', 'Ich wurde hier geboren, $R. Das ist meine Welt - alles was ich kenne... Als Junge erzählten mir meine Eltern fantastische Geschichten über Eure Welt und deren unglaubliche Pracht. Wie sehr ich mich do', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8678', 'deDE', 'Wie kann ich Euch helfen?', 'Wie kann ich Euch helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8680', 'deDE', 'Die Scherbenwelt ist mit magischer Energie überflutet, man kann sie förmlich atmen. Könnt Ihr es fühlen, $N? Ich könnte mir für unser Volk keinen besseren Ort vorstellen. Die Sin\'dorei sollen schon ba', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8681', 'deDE', '', 'Mein Mann wird schon seit Tagen vermisst. Wir haben diese Pilgerfahrt zusammen begonnen und wollten sie auch gemeinsam beenden.$B$BIch bin schon halb wahnsinnig vor Sorge und greife weit häufiger zu M', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8682', 'deDE', '', 'Wenn Drachenfalken richtig aufgezogen werden, sind sie recht zahm. Findet Ihr das nicht auch faszinierend? So freundlich und anhänglich zu sein, wenn sie richtig aufwachsen, und so bösartig und tödlic', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8683', 'deDE', 'Sorgt... Euch nicht... um mich...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8684', 'deDE', '', 'Ich denke wirklich nicht, dass unsere Situation so schlecht ist. Ich bin sicher, dass alles wieder zum Rechten kommt, sobald die Kraft des Sonnenbrunnens wiederhergestellt ist. Es kann noch nicht so s', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8685', 'deDE', 'Sich mit den Sonnenwanderern anzufreunden hat sich bezahlt gemacht; Ich kann meine Untersuchungen an Exemplaren durchführen, die ich nur in der Scherbenwelt vorfinde, und sie... profitieren von meiner', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8688', 'deDE', 'Ich war noch ein Kind, als mein Vater in dieses Ödland abkommandiert wurde. Alles, was ich von ihm weiß, haben mir andere berichtet.$B$BÄndern sich Eure Träume, $N? Meine nicht. Immer und immer wieder', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8698', 'deDE', 'Ihr findet, dass ich mit diesem Wurm zu grausam bin?$B$BSie haben diesen Monat zwei meiner Männer getötet! Wir sind noch immer zu gütig mit diesen Getriebenen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8699', 'deDE', '', 'Willkommen in Morgenluft. Silbermond hat uns im hier aufgegeben, aber wir weigern uns, zu gehen. Dies ist unsere Heimat.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8700', 'deDE', '', 'Ich habe hier genau eine Aufgabe: Sicherzustellen, dass beim Westsanktum alles glatt läuft.$B$BBitte entschuldigt mein Temperament, aber wie Ihr sehen könnt, ist die Situation hier etwas außer Kontrol', '', 'Könnten Saboteure der Nachtelfen dahinter stecken? Ich bezweifle es. Bei der Last, die wir dem Sanktum des Westens auferlegt haben, war es nur eine Frage der Zeit, bis etwas schief geht.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8701', 'deDE', '', 'Nachdem die Imperatoren gefallen sind, ist die magische Barriere, die Teleportation im Inneren des Tempels verhindert, deutlich geschwächt. Wenn Ihr es wünscht, kann ich Euch direkt in den Unterschlup', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8702', 'deDE', '', 'Nachdem Ouro gefallen ist, ist der Weg zur letzten Kammer frei. Ich kann Euch sofort dort hin teleportieren, wenn Ihr wünscht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8703', 'deDE', 'Genießt Ihr das Fest, $C?$B$BWährend die Flammenwächter sich den Feuern der Gegenwart widmen, bin ich eher ein Historiker, der sich der Vergangenheit dieses Festes verschrieben hat. Natürlich dokument', 'Genießt Ihr das Fest, $C?$B$BWährend die Flammenwächter sich den Feuern der Gegenwart widmen, bin ich eher ein Historiker, der sich der Vergangenheit dieses Festes verschrieben hat. Natürlich dokument', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8712', 'deDE', '$C, falls Ihr nach Arbeit sucht, nehmt Euch eine Hacke und fangt an zu graben. Dort draußen sind Ruinen, soweit das Auge reicht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8713', 'deDE', '', 'Seid gegrüßt, $N! Ich und die anderen Söhne Lothars sind schon seit beinahe zwanzig Jahren auf dieser jämmerlichen Welt gestrandet. Wir haben so gut durchgehalten, wie wir konnten, trotzdem überrascht', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8715', 'deDE', '', 'Das Ausmaß der Korruption in der Scherbenwelt ist erstaunlich, $C. Der Zirkel des Cenarius hofft zu verstehen, wie es den heimischen Lebewesen dennoch gelungen ist, zu überleben. Das Studium dieses ge', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8716', 'deDE', 'Es ist ein Trauerspiel mit anzusehen, was diesem Land widerfahren ist. Die Expedition des Cenarius wird alles in ihrer Macht stehende tun, um dieses Land zu verstehen und seinen Schaden zu heilen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8717', 'deDE', 'Ihr seht verloren aus, $C. Bringt mich nicht dazu, Euch zu den anderen Peons an der Mauer einzuteilen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8718', 'deDE', '', 'Seid gegrüßt, $C. Eure Ankunft in Thrallmar kommt genau zur richtigen Zeit! Der Kriegshäuptling ist auf unsere Hilfe angewiesen, da er selbst nicht hier sein kann.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8719', 'deDE', 'Thrallmar braucht Euch, $C. Der Kriegshäuptling braucht Euch. Seid bereit, Eure Pflicht zu tun.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8724', 'deDE', 'Nur wenige Kämpfe wurden je durch pure Stärke gewonnen. Gerissenheit, Reflexe und das Wissen, wie man die eigene Waffe richtig führt, führen am Ende des Tages zum Sieg.', 'Nur wenige Kämpfe wurden je durch pure Stärke gewonnen. Gerissenheit, Reflexe und das Wissen, wie man die eigene Waffe richtig führt, führen am Ende des Tages zum Sieg.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8733', 'deDE', 'Noch ein Held? Das Schicksal ist heute gnädig mit uns, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8740', 'deDE', 'Ich hörte so einiges über Eure Heldentaten, $N. Ich bin sicher, dass Ihr, solange Ihr hier seid, würdige Taten vollbringen werdet, wie ein wahrer Held der Sin\'dorei!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8750', 'deDE', '', 'Ich entbiete Euch meinen Gruß, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8751', 'deDE', 'Ich habe keine Zeit für Geschwätz, $C. Ich muss zusehen, dass alle Pilger auf die bevorstehende lange Reise vorbereitet sind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8752', 'deDE', 'Willkommen in der Falkenwacht, $C. Ich kümmere mich um die Bedürfnisse der Pilger und helfe ihnen, sich auf die weitere Reise vorzubereiten. Falls Ihr Euch ebenfalls auf die Pilgerreise begeben wollt,', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8754', 'deDE', '', 'Wandelt immer im Licht, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8755', 'deDE', '', 'Willkommen in der Zuflucht der Weltenwanderer. Wir sind Waldläufer, die geschworen haben, unser Volk vor Feinden nah und fern zu verteidigen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8756', 'deDE', '', 'Ich glaube, so langsam bereue ich es, mich auf diese Pilgerreise eingelassen zu haben. Natürlich habe ich gewusst, dass die Reise anstrengend sein würde, aber auf die Beschwerlichkeiten der Scherbenwe', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8759', 'deDE', 'Oh... kenne ich Euch?', 'Oh... kenne ich Euch?', 'Bedauere, ich spreche nicht mit dem Personal.', 'Bedauere, ich spreche nicht mit dem Personal.', 'Und Ihr seid?', 'Und Ihr seid?', 'Ich fürchte, Ihr habt mich mit jemanden verwechselt, der mit Euch sprechen möchte.', 'Ich fürchte, Ihr habt mich mit jemanden verwechselt, der mit Euch sprechen möchte.', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8760', 'deDE', 'Hallo, $N! Gefällt Euch die Feier?', 'Hallo, $N! Gefällt Euch die Feier?', 'He, schaut mal, da ist $N!', 'He, schaut mal, da ist $N!', 'Neuigkeiten verbreiten sich hier schnell. Danke für das Wiederauffüllen der Vorräte!', 'Neuigkeiten verbreiten sich hier schnell. Danke für das Wiederauffüllen der Vorräte!', 'Diese Feier ist einfach spitze! Findet Ihr nicht auch?', 'Diese Feier ist einfach spitze! Findet Ihr nicht auch?', 'Ihr solltet die Bachtatzenhäppchen versuchen, eine Köstlichkeit!', 'Ihr solltet die Bachtatzenhäppchen versuchen, eine Köstlichkeit!', 'Hmm... dieser Jahrgang des Besten Sonnentropfens ist gar nicht so besonders. Findet Ihr nicht auch, $N?', 'Hmm... dieser Jahrgang des Besten Sonnentropfens ist gar nicht so besonders. Findet Ihr nicht auch, $N?', 'Ich habe ein Gerücht gehört, dass wir uns heute Abend alle die Schlacht gegen die Geißel an der Grenze ansehen werden. Wie entzückend!', 'Ich habe ein Gerücht gehört, dass wir uns heute Abend alle die Schlacht gegen die Geißel an der Grenze ansehen werden. Wie entzückend!', 'Ist es nicht eine Frechheit von Elisara Sonnensturm? Sie glaubt doch tatsächlich Lord Saltheril für sich begeistern zu können! Und habt Ihr ihre Aufmachung gesehen? Sowas von altmodisch. Es ist trauri', 'Ist es nicht eine Frechheit von Elisara Sonnensturm? Sie glaubt doch tatsächlich Lord Saltheril für sich begeistern zu können! Und habt Ihr ihre Aufmachung gesehen? Sowas von altmodisch. Es ist trauri');
INSERT INTO `npc_text_localized` VALUES ('8761', 'deDE', 'Ach... fort mit Euch!', 'Ach... fort mit Euch!', 'Hä, was? Was ist passiert?', 'Hä, was? Was ist passiert?', 'Geht weg... ich versuch\' hier zu schlafen.', 'Geht weg... ich versuch\' hier zu schlafen.', 'Umpf, mir ist etwas übel.', 'Umpf, mir ist etwas übel.', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8767', 'deDE', 'Ich habe hier ein schönes Plätzchen gefunden, findet Ihr nicht? Scharfseherin Regulkut hatte versucht, mich zu warnen, doch ich habe nicht auf sie gehört.$B$BIch kam hier herunter, um die Ruinen von Z', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8768', 'deDE', 'Ich kann Euch zwar nicht ausbilden, $Gjunger:junge:c; $C, aber ich kann Euch meinen Segen verleihen. Wandelt unerschrocken im Licht! Gibt es noch etwas?', 'Ich kann Euch zwar nicht ausbilden, $Gjunger:junge:c; $C, aber ich kann Euch meinen Segen verleihen. Wandelt unerschrocken im Licht! Gibt es noch etwas?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8769', 'deDE', '', 'Es ist von größter Wichtigkeit, dass wir herausfinden, was die Nachtelfen vorhaben, $C! Bitte konzentriert all Eure Anstrengungen darauf.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8770', 'deDE', 'Vor nicht allzu langer Zeit war dieser Ort der Schauplatz einer schrecklichen Schlacht. Die Verteidiger von Nethergarde und die Argentumdämmerung hielten gemeinsam die Dämonen in Schach, welche in sch', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8771', 'deDE', 'Die Heldentaten der Trollbannblutlinie sind legendär. Ich verehre diesen Mann zutiefst.$B$BUnd ihn lebend anzutreffen, hier in der Scherbenwelt...$B$B<Romus scheint durch Euch hindurch zu starren.>$B$', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8772', 'deDE', 'Romus berichtete mir über die Tragödien, welche meine geliebte Stadt befallen haben.$B$BIch finde keine passenden Worte für die Trauer, die ich empfand, als ich von Lordaerons Schicksal erfuhr und dem', 'Romus berichtete mir über die Tragödien, welche meine geliebte Stadt befallen haben.$B$BIch finde keine passenden Worte für die Trauer, die ich empfand, als ich von Lordaerons Schicksal erfuhr und dem', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8775', 'deDE', '', 'Dieser Emitter treibt mich zur Verzweiflung. Mir fehlen jede Menge Teile, um ihn zu reparieren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8776', 'deDE', '', '$N! Nochmal vielen Dank für die Hilfe.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8777', 'deDE', '', 'Nein, die Reparatur ist noch nicht abgeschlossen. Sollte aber nicht mehr lange dauern.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8780', 'deDE', '<Schluchzt.> Ich hasse Friedhöfe. So viele Tote. Und meine süße Luhanaa....', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8785', 'deDE', 'Vergesst niemals, den Elementen dieser Welt Euren Respekt zu zollen.', 'Vergesst niemals, den Elementen dieser Welt Euren Respekt zu zollen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8786', 'deDE', 'Stets sollten wir danach streben, das durch die Elementare dieser Welt bewahrte Gleichgewicht in seiner Gesamtheit zu verstehen.', 'Stets sollten wir danach streben, das durch die Elementare dieser Welt bewahrte Gleichgewicht in seiner Gesamtheit zu verstehen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8787', 'deDE', '', 'Hallo! Schön, Euch kennenzulernen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8788', 'deDE', '', 'Grüße. Seid Ihr hier, um zu helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8789', 'deDE', '', 'Denkt immer daran, $N, es ist in unserem Interesse, dass Ihr so viel wie möglich für diese... diese Leute tut.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8790', 'deDE', '', 'Grüße, $R. Wie geht es Euch an diesem schönen Tag?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8791', 'deDE', '', 'Gibt es Neues aus der Heimat, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8792', 'deDE', 'Kommt näher, mein Freund. Lasst Euch nicht von meinen Augen verunsichern.$B$BVielleicht mag ich mein Augenlicht vor Jahren verloren haben, doch um sehen zu können, was wirklich zählt, habe ich es nie ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8793', 'deDE', '', 'Greift Euch ein Netz, $N. Unser Volk braucht Nahrung!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8794', 'deDE', 'Hallo. Heute scheint Euer Tag zu sein, nicht?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8795', 'deDE', 'Es ist gut, $G einen Bruder : eine Schwester; der Sin\'dorei zu sehen! Ihr könnt Euch sicher vorstellen, dass man hier nicht unbedingt auf viele Gesprächspartner trifft.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8796', 'deDE', 'Es ist mir eine Ehre, Eure Bekanntschaft zu machen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8797', 'deDE', 'Danke, dass Ihr mich aufgesucht habt. Bitte, helft der Allianz so gut Ihr könnt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8798', 'deDE', 'Seid gegrüßt. Ich bin Anchoret Delan, einer der Draenei. Man könnte mich als Lehrmeister bezeichnen. Und mit wem habe ich das Vergnügen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8800', 'deDE', '', 'Seid gegrüßt, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8801', 'deDE', '', 'Ist diese Stadt nicht wunderschön, $C? Es ist schön, zu sehen, dass wir in den Nachtelfen so gute Freunde gefunden haben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8802', 'deDE', '', 'Grüße. Ich bin Verteidigerin Palanaar und reise als Paladin im Dienste des Lichts. Ich bin hier, um die Nachtelfen zu unterstützen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8803', 'deDE', '', 'Es ist meine größte Hoffnung, dass wir das Licht an die Orte im Eschental bringen können, wo es verlorengegangen ist.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8804', 'deDE', 'Ich bin viel zu beschäftigt, um meine Zeit mit sinnlosem Geplapper zu verbringen. Falls Ihr nach Arbeit als Jäger sucht, ist Diktynna genau die Richtige für Euch. Ihr findet sie am Flussufer im Osten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8805', 'deDE', '', 'Das wird ein Spaß! Denkt Ihr nicht auch, $Gmein Lieber:meine Liebe;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8806', 'deDE', 'Seht Euch die Exodar an, $Gjunger:junge:r; $R. Es ist kaum vorstellbar, dass wir eine solche Katastrophe überlebt haben.$B$B<Menelaous seufzt.>$B$BDer Prophet sprach, dass sich uns unser Schicksal an ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8808', 'deDE', '', 'Hat Balandar Sternenlicht Euch geschickt, um mich zu retten?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8809', 'deDE', '', 'Oh, hervorragend! Das war ein toller Spaß, oder? Nach und nach haben ich mehr dieser abscheulichen Verirrten mit Flüchen belegt. Bald bin ich bereit, es an den Draenei zu versuchen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8810', 'deDE', '', 'Mögen Euch die Geister leiten, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8811', 'deDE', 'Stärke den Klans, $C! Was führt Euch hierher?', 'Stärke den Klans, $C! Was führt Euch hierher?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8813', 'deDE', 'Wir heißen Euch unter Unseresgleichen willkommen, $C. Zumindest für den Moment.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8821', 'deDE', '', 'Ach, wie süß! Wie habt Ihr es aus dem Tal heraus geschafft? Vielleicht solltet Ihr umkehren, bevor sie einen Suchtrupp nach Euch aussenden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8822', 'deDE', '', 'Ich habe nichts weiter für Euch zu tun, $N. Ihr solltet wissen, dass der Schnapper, den wir geteilt haben, für mich etwas Besonderes war, das ich nie vergessen werde...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8823', 'deDE', 'Selbstverständlich habe ich eine Aufgabe für Euch! Wir sind immer auf der Suche nach fähigen Jägern. Hoffentlich schlagt Ihr Euch besser als die letzten Draenei, die wir in Holzkisten zurück zur Exoda', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8825', 'deDE', 'Kehrt zu den Draenei zurück und bittet sie, einen ihrer Schamanen zu mir zu schicken.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8828', 'deDE', 'Seid gegrüßt, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8829', 'deDE', 'Ein wundervoller Tag, um am Leben und eins mit den Elementen zu sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8830', 'deDE', 'Und wieso sollte ich mit Euch sprechen wollen, $R?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8832', 'deDE', 'Seht Euch um, Schamane. Spürt Ihr das Gleichgewicht der Elemente in der Erde, der Luft, dem Wasser und dem Feuer? Alles wurde von ihm berührt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8834', 'deDE', 'Keine Sorge... meine Greifen sind vielleicht nicht mehr so sattelfest, aber sie werden Euch noch immer getreu an Euer Ziel bringen. Also... zu welchem Turm möchtet Ihr?', 'Keine Sorge... meine Greifen sind vielleicht nicht mehr so sattelfest, aber sie werden Euch noch immer getreu an Euer Ziel bringen. Also... zu welchem Turm möchtet Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8835', 'deDE', '', 'Wenn Ihr noch nicht gelernt habt, wie man Verbände herstellt, werde ich Euch mit Freude unterrichten. Das richtige Anlegen eines Verbandes kann den Unterschied zwischen Leben und Tod bedeuten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8836', 'deDE', 'Die Rache wird mein sein!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8838', 'deDE', 'Ich bin gerade sehr beschäftigt, $Gjunger:junge:c; $C. Bitte entschuldigt mich.', 'Ich bin gerade sehr beschäftigt, $Gjunger:junge:c; $C. Bitte entschuldigt mich.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8840', 'deDE', 'Seid gegrüßt, $C. Die Exodar hat mich hierher gesandt, um die Fertigstellung der Azurwacht zu überwachen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8844', 'deDE', 'Ein Alchemist kann aus herkömmlichen Kräutern wahre Wundermittel herstellen. Haltbare Tränke der Heilung, Wasseratmung und viele mehr.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8846', 'deDE', 'Hallo. Was führt Euch heute in das Zugwindlager?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8847', 'deDE', 'Ein guten Tag wünsche ich, $C.$B$BIch habe um eine Audienz ersucht und warte jetzt schon seit längerem auf eine Antwort von einem der Berater des Obersten Than. Es war schon schwierig genug, überhaupt', 'Ein guten Tag wünsche ich, $C.$B$BIch habe um eine Audienz ersucht und warte jetzt schon seit längerem auf eine Antwort von einem der Berater des Obersten Than. Es war schon schwierig genug, überhaupt', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8848', 'deDE', '', 'Was wollt Ihr?$B$BFremde Leute einfach ohne Grund anzusprechen ist nicht gerade die feine Art, findet Ihr nicht?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8849', 'deDE', 'Hey, Landratte! Kräcka ist mein werter Name, und Kochen ist meine Kunst! Die Blutlinie der Aschebäschas hat eine ganze Reihe Köche und Händler hervorgebracht. Alle waren Meister, wenn es darum ging, d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8850', 'deDE', '', 'Braucht Ihr etwas?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8851', 'deDE', 'Das Licht sei mit Euch, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8852', 'deDE', '', 'Es sieht ganz danach aus, als sei endlich Hilfe eingetroffen! Die Omenai heißen Euch in ihrem Lager willkommen, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8853', 'deDE', 'Ikan versucht es immer und immer wieder. Ich glaube dem Lumpenpack geht es nicht anders als den Verirrten... sie sind verloren, versteht Ihr.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8854', 'deDE', 'Es wird seine Zeit brauchen, aber die Zerschlagenen werden eines Tages den Pfad des Lichts beschreiten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8855', 'deDE', 'Willkommen, $N. Ich bin Landro Fernblick, Anführer der Schwarzen Flamme. Möglicherweise habt Ihr noch nie von der Schwarzen Flamme gehört? Nun, wir arbeiten hart daran, dass das auch so bleibt. Wir wü', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8856', 'deDE', 'Ah - Seltenes und Seltsames. Wir haben uns auf Azeroths einzigartigste Waren spezialisiert.$B$BDa Ihr mit mir sprecht, solltet Ihr Euch schon darüber klar sein, was Ihr wollt. Also, $N, an welchem Geg', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8858', 'deDE', '', 'Braucht Ihr etwas, $C? Vielleicht kann ich Euch ja helfen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8860', 'deDE', 'Militärische Stärke allein ist nicht genug, um die Geisterlande zurückzugewinnen, $N. Hier kommen wir Magister ins Spiel.$B$BUnser Verstand und unser Wissen über das Arkane sind unerreicht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8861', 'deDE', '', '<Priesterin Il\'dinare beäugt Euch misstrauisch.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8862', 'deDE', '', 'Ich bin ein Nachtelf, $GFremder:Fremde;. Ihr mögt nichts von meinem Volk wissen, aber wir wissen viel über Euch und Eure Vorfahren. Ihr mögt Euch nicht Eredar nennen, aber äußerlich teilt Ihr viele Ge', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8864', 'deDE', 'Achtet darauf, wo Ihr hintretet, $G Fremder : Fremde;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8865', 'deDE', 'Bis jetzt wart Ihr uns eine große Hilfe, $N. Ihr tätet gut daran, auch die anderen im Lager zu unterstützen.$B$BUnd macht Euch über diese Goblins keine Sorgen, ich kümmere mich um alles.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8867', 'deDE', 'Was starrt Ihr so?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8868', 'deDE', 'Ich bin Adamant Eisenherz aus dem großartigen Eisenschmiede. Wie Euch der Anblick meiner Uniform sicherlich schon verraten hat, war ich einst ein Mitglied der Elitewachen von Eisenschmiede. Jawohl, ic', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8869', 'deDE', '<Cowlen schluchzt.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8870', 'deDE', 'Was habe ich nur getan, dass die Götter mich derart bestrafen? Alles was ich liebte ist verloren - tot oder zerstört. Meine Familie wurde mir von den Ungetümen des Waldes genommen. Mein Heim liegt in ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8871', 'deDE', 'Wenn nicht die Götter, wer war es dann? Wie? Was könnte so etwas derart böses vollbringen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8872', 'deDE', 'Legion? Dämonen? Sie jagen euch? Auch mein Volk wurde von der Brennenden Legion verfolgt: Ihre dunklen Meister hatten unsere Vernichtung beschlossen.$B$B<Cowlen wischt sich eine Träne aus den Augen.>$', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8873', 'deDE', 'Seid gegrüßt, $C. Bevor die Nachtelfen die Draenei hier willkommen hießen, war dieser Teil des Waldes beinahe vergessen.$B$BDie Kaldorei haben eine starke Bindung zu diesem Land und wir Draenei können', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8874', 'deDE', '', 'Es tut gut, hier draußen ein neues Gesicht zu sehen. Unser Stützpunkt ist klein, und die Zahl der Feinde groß. Wir sind umgeben von Kräften, die nach unserer Zerstörung streben: die Satyrn im Westen, ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8875', 'deDE', 'Es tut gut, wieder die Stimmen anderer hier in Waldeslied zu hören. Ich fürchtete schon, ich sei der letzte der Kaldorei, der diese Länder jemals zu Gesicht bekommt, bevor auch sie der Verderbnis anhe', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8876', 'deDE', 'Spürt Ihr das Böse im Süden? Bei der Hand, es muss vernichtet werden!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8880', 'deDE', 'Mit Euch und eurem Volk als Verbündeten haben wir beschlossen, auf dieser Insel zu bleiben und alles wiederaufzubauen. Möge Elune Euch stets segnen, Held.$B$BAuch wenn die Trauer kommt und geht, ich d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8881', 'deDE', 'Ihr seid $Gein:eine:r; $R, nicht wahr? Ich bin erfreut, Euch hier zu sehen! Wir können jede Hilfe gebrauchen, die wir kriegen können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8883', 'deDE', '', 'Es gibt ein paar Dämonen zu töten, $C. Seid Ihr mutig genug um zu tun, was getan werden muss?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8887', 'deDE', '', 'Jetzt, wo der Großteil ihrer Dämonen vernichtet wurde, wird sich die Legion zweimal überlegen, ob sie einen weiteren Angriff auf das Holzfällerlager wagt. Trotz unseres Erfolgs spüre ich noch immer ei', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8893', 'deDE', '', 'Ein Hoch auf $Gden erhabenen Helden:die erhabene Heldin;!', '', 'Über Eure Reise wird noch viele Jahre lang berichtet werden!', '', 'Endlich habe ich eine Geschichte, die ich meinen Enkeln erzählen kann... Unter uns hat ein Verräter gelebt!', '', 'Ehrlich gesagt habe ich diesen Gnom nie gemocht...', '', 'Wir haben Glück, diese Tortur überlebt zu haben. Wenn Ihr nicht gewesen wärt, hätte jeder auf dieser Insel sterben können.', '', 'Ich frage mich, wie viele von denen es noch unter uns gibt. Ich kenne Funks schon jahrelang, ich hätte nie gedacht, dass er ein Verräter ist.', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8939', 'deDE', '', 'Willkommen in Waldeslied, $C. Wenn es etwas gibt, das ich für Euch tun kann, bitte lasst es mich wissen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8952', 'deDE', '<Der Fußsoldat steht entschlossen und angespannt.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8953', 'deDE', 'In unserem Bestreben, die Bewohner dieser Welt für unseren Kampf gegen die Brennende Legion zu gewinnen, dürfen wir nicht vergessen, dass wir uns auch um ein friedliches Zusammenleben mit ihnen bemühe', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8960', 'deDE', 'Lasst ab. Ich habe nichts übrig für Euresgleichen.', 'Lasst ab. Ich habe nichts übrig für Euresgleichen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8961', 'deDE', 'Was ist los, $T Knirps:Schneckchen;? Wollt Ihr zu Eurer Mami? Tja, zu Schade. Versucht\'s mal in der Offiziersmesse von Thrallmar, vielleicht werdet Ihr dort ja fündig!', '', 'Wisst Ihr überhaupt wo Ihr seid? Das ist die Ehrenfeste, Schätzchen! Entweder Euch gefällt\'s oder Ihr verschwindet!', '', 'Das ist hier keine Vergnügungsreise, Made! Ihr seid jetzt in der Scherbenwelt! Reißt Euch zusammen oder spaziert wieder durchs Portal zurück!', '', 'Ich hab weder die Zeit, noch das Verlangen, Euch Drecksäcken auch nur die kleinste Kleinigkeit zu erklären! Also, habt Ihr mir etwas Wichtiges mitzuteilen, oder seid Ihr nur hier, um mich auf ein Bier', '', 'Was in drei Dämonens Namen ist Euer Problem?!', '', 'Ich hab Euch hier herumstolzieren sehen! Warum tut Ihr uns allen nicht einfach einen Gefallen und gebt Eure Ausrüstung wieder in der Rüstkammer ab?', '', 'Wer in Kurdrans Namen hat Euch durch das Dunkle Portal gelassen?', '', 'Aber, aber, aber, was haben wir denn hier? Ein $T Zivilist:Zivilist;? Wie niedlich! Erwartet keine bevorzugte Behandlung von mir!', '');
INSERT INTO `npc_text_localized` VALUES ('8962', 'deDE', 'Senkt Eure Stimme, $C!', '', 'Sprecht, wenn Ihr etwas zu sagen habt!', '', 'Ihr seid ein $T Zivilist : Zivilist;?', '', 'Ich habe zu tun, macht schnell!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8964', 'deDE', 'Wie kommt Ihr mit der Suche nach dem Mal voran?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8965', 'deDE', 'Das ist ein wunderbarer Tag, $C!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8966', 'deDE', 'Ich kann Euch nicht oft genug danken, dass Ihr mir meine Pilgerreise ermöglicht habt!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8970', 'deDE', 'Alles ist vorbereitet. Seid Ihr bereit, für Euer Bühnendebüt?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8971', 'deDE', 'Keine Sorge, Ihr macht das schon! Ihr seid ein Naturtalent!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8972', 'deDE', 'Die meisten Mondweidenhirsche grasen südlich und östlich der Azurwacht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8973', 'deDE', 'Am einfachsten ist es nördlich der Azurwacht, dort grasen eine Menge Mondweidenböcke.$B$BGute Jagd, $N!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8974', 'deDE', 'Anscheinend kommen die meisten infizierten Nachtpirscherjungen aus dem Norden. An Eurer Stelle würde ich dort meine Jagd beginnen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8975', 'deDE', 'Oh, das war aber ein kompletter Fehlschlag. Nehmen wir an, das wäre eine Probe gewesen. Bereit, wieder auf die Bühne zu gehen?', 'Oh, das war aber ein kompletter Fehlschlag. Nehmen wir an, das wäre eine Probe gewesen. Bereit, wieder auf die Bühne zu gehen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8978', 'deDE', 'Seht Ihr nicht, dass ich beschäftigt bin?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8981', 'deDE', 'Ein schwieriges Publikum, aber die werdet Ihr schon in den Griff kriegen. Bereit für einen neuen Versuch?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8983', 'deDE', '[Furbolg] Mil\'ork kix ilfin o oomtor...', '', '[Furbolg] Poorov\'tarkh!', '', '[Furbolg] Yar vartslaf?', '', '[Furbolg] Boro gomsho!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8990', 'deDE', 'Wie kann ich Euch helfen, Fremder?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8991', 'deDE', 'Gurf ist mein Name, Fremder. Gurf der Mutige!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8992', 'deDE', 'Ich bin Moordo, erfahrenster der Jäger der Tannenruhfeste.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8993', 'deDE', 'Willkommen bei meinem Laden!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8994', 'deDE', 'Ah, der Erlöser ist gekommen. Willkommen bei der Tannenruhfeste.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8996', 'deDE', 'Schön Euch zu sehen, $C. Ich bin der Jüngling Tannenruh, Thronerbe der Tannenruh.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8997', 'deDE', 'Ihr werdet meine Leute verteidigen und eine neue Ära des Wohlstands für die Tannenruhfeste einläuten!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9002', 'deDE', 'Es tut mir leid, ich bin gerade mit der Reparatur einiger Hufeisen beschäftigt. Würde es Euch etwas ausmachen, später wiederzukommen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9003', 'deDE', 'Einst diente ich Medivh... zumindest sollte er das glauben. Ich half dem Violetten Auge dabei, den Meister zu beobachten.$B$BDoch alles woran ich jetzt noch denken kann, ist das Schmieden von Hufeisen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9004', 'deDE', 'Ihr wisst über das Violette Auge Bescheid? Möglicherweise kann ich später etwas Zeit für Euch erübrigen.$B$BDoch jetzt muss ich zurück an die Arbeit.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9005', 'deDE', '', '<Eralan scheint auf etwas Todeskraut herumzukauen.>$B$BSolltet Ihr auf der Suche nach einem Liebestrank sein, dann seid Ihr zur falschen Person gekommen. Blenden, Verstümmeln und Töten ist mein Spezia', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9006', 'deDE', 'Ich spüre Eure Gier nach Macht, $C!', 'Ich spüre Eure Gier nach Macht, $C!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9007', 'deDE', 'Das Licht gibt uns die Stärke und Magie für den Sieg.', 'Das Licht gibt uns die Stärke und Magie für den Sieg.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9008', 'deDE', '', 'Willkommen beim Gildenregister. Wie kann Ich Euch behilflich sein?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9009', 'deDE', '', 'Hallo, mein Kind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9010', 'deDE', '', 'Damit ich Euch besser hören kann.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9011', 'deDE', '', 'Damit ich Euch besser sehen kann.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9014', 'deDE', 'Seid gegrüßt, $G junger Schamane:junge Schamanin;. Fühlt Euch in meinem Riff wie zu Hause!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9015', 'deDE', 'Gemeinsam werden wir uns der Verderbnis des Wassers annehmen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9019', 'deDE', '', 'Ihr seid sicher auf der Suche nach Schmiedezubehör, nicht wahr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9020', 'deDE', 'Ich habe frisches Fleisch, Fische und Pilze. Und reichlich zu trinken.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9021', 'deDE', '', 'Seid gegrüßt, $N. Je mehr Euer Ansehen bei den Streitkräften der Ehrenfeste steigt, desto mehr unserer Vorräte kann ich Euch zugänglich machen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9022', 'deDE', 'Ich bin Admetius, der Kommandeur der Blutwacht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9023', 'deDE', 'Willkommen in Thrallmar, $N! Dient auch weiterhin unserer Sache und ich werde Euch den Zugriff auf weitere unserer Vorräte erlauben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9024', 'deDE', 'Möchtet Ihr, dass ich mich um Eure tierischen Begleiter kümmere?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9025', 'deDE', 'Auf Argus war Mac\'Aree die heiligste unserer Städte.$B$BWürdet Ihr mir glauben, wenn ich Euch sage, dass die Pfade mit Edelsteinen ausgelegt waren? Dass die Flüsse selbst in absoluter Dunkelheit noch ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9029', 'deDE', 'Willkommen in der Blutwacht, $C. Während meiner Reisen habe ich eine Vielzahl unterschiedlichster nützlicher Gegenstände, Artefakte und Wissen von den verschiedensten Völkern angesammelt, denen wir au', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9033', 'deDE', 'Auch wenn Ihr nur eine Sache auf der Blutmythosinsel gelernt habt, $C, so ist es hoffentlich Wachsamkeit. Diese Insel fordert das Leben der ersten Unachtsamen ein und verschont danach selbst die wachs', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9034', 'deDE', '', 'Seit dem Absturz ist so viel passiert... wenn ich mich an meine Zeit in der Exodar erinnere, scheint das in einem anderen Leben gewesen zu sein.$B$BMein Leben heute beschränkt sich auf diese Insel mit', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9036', 'deDE', 'Die anderen blicken auf dieses Land und sehen nur Tod, Verderben und Elend. Natürlich gibt es hier reichlich davon, doch birgt diese Sichtweise die Gefahr, dass man auch die Gelegenheiten übersieht, d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9037', 'deDE', 'Hallo, $C.$B$BDer alte Kessel hier ist ein ganz Gerissener. An Eurer Stelle wäre ich bei Euren Geschäften mit ihm sehr vorsichtig.$B$BEr hat mir mal Arbeit angeboten, ich sollte mich um seine Elekk kü', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9038', 'deDE', 'Danke für die Warnung, $N! Wir sollten uns vorbereiten!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9039', 'deDE', 'Habt Dank für Eure Warnung, großer Erlöser. Die Furbolg der Tannenruhfeste werden auf das Kommende vorbereitet sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9040', 'deDE', 'Danke für die Warnung, $N! Wir werden unsere Verteidigung vorbereiten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9041', 'deDE', 'Ich bin Kessel, der Elekklord, Jünger der Hand von Argus. Seid Ihr der neue Rekrut?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9042', 'deDE', 'Ich bin Achelus, Oberster Offizier der Expeditionsstreitkräfte der Blutwacht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9043', 'deDE', 'Ich stelle sämtliche Post von und zur Azurmythosinsel zu, $C. Wenn Ihr eine Botschaft verschicken wollt, ist es sehr wahrscheinlich, dass ich sie zuerst erhalte, bevor sie weiter zugestellt wird.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9044', 'deDE', 'Was es bedeutet, ein Mitglied der Hand von Argus zu sein... Nun, wir sind die Agenten des Lichts. Wir dienen und wir sterben ohne unser Handeln in Frage zu stellen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9045', 'deDE', 'Haben sie Euch hierher geschickt, um mich zu finden? Nun, ich werde nicht ohne meine Männer gehen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9050', 'deDE', 'Tatsächlich bin ich einer der \'Zerschlagenen\'. Ich hoffe, dass macht Euch nichts aus? Zerschlagene oder Draenei, wir alle müssen von Zeit zu Zeit den Spott ertragen, den einige Mitglieder unserer Gese', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9051', 'deDE', 'Bitte, setzt Euch und macht es Euch gemütlich.', 'Bitte, setzt Euch und macht es Euch gemütlich.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9052', 'deDE', 'Guten Tag, $C. Seid Ihr auf der Suche nach einem verlässlichen Hippogryphen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9053', 'deDE', 'Falls Ihr zur Exodar reisen möchtet, habe ich genau den richtigen Hippogryphen für Euch!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9054', 'deDE', 'Psssst! Seid ganz still; Ich beobachte Murlocs.$B$BIn einem übergroßen Schildkrötenpanzer zu leben, ist ein Opfer, das ich für die Wissenschaft gewillt bin zu erbringen. Dennoch, sollten die Murlocs m', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9056', 'deDE', 'Seid Ihr verrückt? Schaut Euch um, $GSterblicher:Sterbliche;. Seht Ihr nicht, dass Ihr auf geteilter Erde steht? Verlasst diesen Ort, oder erleidet das gleiche Schicksal wie mein Volk und ich...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9057', 'deDE', 'Seid Ihr genauso blind, wie Ihr verrückt seid?$B$BIch bin ein Geist - eine grausame Erinnerung an eine Zivilisation, die es seit langem nicht mehr gibt - an ein Land gebunden, das ich nicht schaffte z', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9058', 'deDE', 'Habt Ihr nicht von Ysera gehört? Wächterin der Natur? Aspekt des Traums? Sie ist die Matrone aller grünen Drachen! Ja, es war Ysera selbst, die mein Königreich mit ihrer Brut segnete. Wir standen Seit', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9059', 'deDE', 'Todesschwinges Brut... Yseras Güte erweckte den Zorn von Todesschwinge - dem Anführer des schwarzen Drachenschwarms. Sie überfielen uns im Schlaf! Viele starben während der ersten Angriffswelle, doch ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9060', 'deDE', 'Ich starb als Letzter. Als ich fühlte, wie mein Geist meine sterbliche Hülle verließ, schwor ich einen Blutfluch.$B$B<Prinz Toreth schüttelt seinen Kopf.>$B$BEin Pakt zwischen mir und diesem Land war ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9061', 'deDE', 'Schüler sind ein bisschen wie Schafe. Sie sind nützlich und sogar liebenswert, wenn man sie führt und sich gut um sie kümmert.$B$BLässt man sie eine Minute allein, fallen sie von einer Klippe oder ert', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9062', 'deDE', 'Auf diesem Schild befinden sich verschiedene Ankündigungen sowie Vermerke über Belohnungen für das Erfüllen gefährlich klingender Aufträge. Im Moment fällt Euch nichts Besonderes ins Auge; vielleicht ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9063', 'deDE', 'Seid gegrüßt, Bürger. Bitte geht weiter, wenn ich Euch nicht behilflich sein kann.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9067', 'deDE', 'Bleibt mir fern, $R! Man weiß nicht, wann die Krankheit mich wieder befällt.$B$BLasst mich einfach allein, bevor es noch einmal passiert.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9070', 'deDE', 'Ihr untersucht den Leichnam und kommt zu dem Schluss, dass der Tote Blutritter Morgenstern ist, der Anführer des fehlgeschlagenen Angriffs auf die Todesfestung.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9071', 'deDE', 'Ich kann Euch bei der Gründung einer Gilde behilflich sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9072', 'deDE', 'Die Durchsuchung des Toten, seiner Kleidung und Ausrüstung fördert die von Euch gesuchte Insignie zu Tage. Sie scheint, trotz der Schlacht und der unwirtlichen Umgebung, unbeschadet zu sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9073', 'deDE', '', 'Elunes Anmut sei mit Euch. Womit kann ich dienen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9074', 'deDE', '', 'Grüße, $C. Es ist gut, dass Ihr beschlossen habt, uns zu besuchen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9076', 'deDE', 'Ihr habt einem der Drei ehrvoll gedient, $N. Unsere Zeit ist vorüber. Kuros wird nach Euch rufen lassen, sobald er bereit ist.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9077', 'deDE', 'Seid gegrüßt, $C. Ich bin Kuros, Zweiter des Dreigestirns der Hand.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9079', 'deDE', 'Ich sah zu, wie sie ihn gnadenlos zusammenschlugen. Der unbarmherzige Leutnant benahm sich ganz so, als wäre eine derartige Brutalität etwas Alltägliches. Beim Anblick der gnadenlosen Gewalt, zerbrach', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9080', 'deDE', 'Es erschien mir wie eine Ewigkeit, bis der Verteidiger das Bewusstsein verlor. Die Folter war einfach zuviel, selbst für jemanden wie ihn. Der Brutale - sein Name war Matis, wie ich später herausfand ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9081', 'deDE', 'Ich wollte schreien, doch meinen Lippen entrann nur ein verzweifeltes Seufzen. Unverzüglich bestrafte man mich für diesen Akt der Rebellion. Auf dem Boden liegend sah ich, wie Matis vor Saruans Körper', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9082', 'deDE', 'Das Wesen Sironas hatte Pläne mit Saruan...$B$B<Das Geschriebene endet abrupt in einer langen Linie aus Tinte. Der Draenei starb während er diese Zeilen schrieb.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9084', 'deDE', 'Guten Tag, $C. Wenn Ihr die Kunst des Schmiedens erlernen wollt, seid Ihr bei mir goldrichtig.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9085', 'deDE', '', 'Oh, lasst Euch von Schatzi nicht stören. Er tut Euch nicht weh, er kratzt nur ab und zu.$B$BEr ist einfach nur ein großes, knuddeliges Kätzchen.$B$BStimmt\'s, Schatzi? Ja, das bist du, du süßes kleines', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9086', 'deDE', 'Wir cenarischen Druiden bewahren und verteidigen das Gleichgewicht der Natur. Wenn es notwendig wird, dürfen wir nicht zögern; vielmehr müssen wir sofort handeln. Lasst Euch von Niemandem etwas andere', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9090', 'deDE', 'Wer seid Ihr? Lasst mich einfach in Frieden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9097', 'deDE', 'Beginnt im Norden mit Eurer Suche, $N. Unsere Späher haben die Warpgondel nahe der Küstenlinie ausgemacht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9098', 'deDE', 'Meine Geschichte? Es ist lieb von Euch, danach zu fragen. Nun gut.$B$BVor langer Zeit lebten alle Draenei in Frieden auf dem Planeten Draenor, der heute als Scherbenwelt bekannt ist. Aber dann kam die', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9099', 'deDE', 'Die teuflischen Energien hatten auf die Zerschlagenen und Verirrten auch die Nebenwirkung, unsere Verbindung zum Licht zu trennen. Zu dieser Zeit wusste ich nicht, warum, aber als Verteidiger hatte ic', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9100', 'deDE', 'Ich hatte Glück und fand eine Oase, in der ich bleiben wollte, bis ich eine Antwort gefunden hatte. Ich saß dort, bewegte mich nur wenn unbedingt nötig und ernährte mich von Kreaturen, die dem Gewässe', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9101', 'deDE', 'Es war ein seltsamer Wind. Wochenlang blies er, mal stärker, mal schwächer, aber er hörte nie auf. Die ganze Zeit war die Stimme zu hören, aber ich konnte sie nicht verstehen, und ich wusste nicht, wo', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9102', 'deDE', 'Sie sprach von Geheimnissen, von Macht und von Wahrheit. Das Licht hatte die Zerschlagenen nicht verlassen! Die Verderbnis der Orcs hatte uns Draenei durchdrungen und machte es uns unmöglich, das Lich', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9103', 'deDE', 'Der Wind brachte mir die Wege des Schamanismus bei, die gewissermaßen auch ein Aspekt des Lichts sind. Dem Wind schloss sich das Wasser der Oase an, das Feuer meines Lagers, und die Erde, auf der ich ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9104', 'deDE', 'Ich kehrte zu meinem Volk zurück und wurde mit offenen Armen und lachenden Gesichtern begrüßt, vor allem von den Zerschlagenen. Die Vorurteile vieler Draenei uns gegenüber hatten noch Bestand, aber es', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9105', 'deDE', 'Velen drängte mich, eine Führungsrolle unter den Zerschlagenen zu übernehmen. Aber wichtiger noch, er bat mich, den Schamanismus allen zu lehren, sowohl den Zerschlagenen als auch den Draenei. Ich nah', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9106', 'deDE', 'Wenn unsere Feinde sehen würden, dass wir uns lediglich wünschen, in Eintracht mit ihnen zu leben, vielleicht müssten wir sie dann nicht zu unserem eigenen Schutz angreifen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9107', 'deDE', 'Ihr Tag wird kommen, $N.$B$BWenn es soweit ist, werde ich hier sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9108', 'deDE', 'Mit neuen Feinden vor den Türen, dürfen wir es nicht zulassen, dass unsere alten Widersacher ihren Nutzen aus unserer Lage ziehen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9109', 'deDE', '', 'Ist dieser Ort nicht unglaublich? Aber hier oben ist es etwas stickig, wenn Ihr mich fragt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9110', 'deDE', 'Seht Ihr diesen Bogen im See, Richtung Westen? Ich wundere mich, wohin er führt. Vielleicht sollte ich da tauchen gehen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9111', 'deDE', 'Stinkige Fungusgiganten! Was haben wir ihnen jemals getan?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9112', 'deDE', '', 'Wenn ich mir nicht meinen Knöchel verstaucht hätte, als ich herkam, wäre ich jetzt dort bei meinen Freunden. Hmm, wenn ich darüber nachdenke, vielleicht ist es doch besser, dass ich hier bin?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9113', 'deDE', 'Fhwoor mag kleine Sporlinge. Sie retten Fhwoor. Sie nicht essen.$B$BFhwoor hasst Naga. Fhwoor zertrümmern!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9117', 'deDE', 'Ich bin Aesom, der Dritte des Dreigestirns.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9118', 'deDE', 'Hier liegen die Überreste von Jäter Gründaum, einer von Behüterin Jhangs Druidenfreunden. Wie es aussieht, wurde er schnell und sauber getötet, als er aus den Sklavenunterkünften fliehen wollte.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9119', 'deDE', 'Ich habe es fast geschafft!$B$BIch bin hierher gekommen, um die Existenz von Quagmirran zu beweisen, dem wohl sagenumwobensten Fungusgiganten. Mein Wunsch wurde erfüllt... naja, irgendwie jedenfalls.$', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9121', 'deDE', 'Welch Glück, dass Ihr hier seid! Klaue und ich haben diesen Ort untersucht, als wir von Sumpffürst Musel\'ek angegriffen wurden! Er kann Tiere kontrollieren, sogar Druiden!$B$BUnglücklicherweise war Kl', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9122', 'deDE', 'Als der anscheinend einzige Überlebende der \'Zerschlagenen\' sehe ich es als meine Aufgabe an, die schamanistischen Lehren von Weissager Nobundo fortzuführen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9123', 'deDE', '', 'Verdammt seien diese Blutelfen und ihre ruchlose Magie!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9124', 'deDE', 'Verschwendet nicht meine Zeit. Was wollt Ihr von mir?', 'Verschwendet nicht meine Zeit. Was wollt Ihr von mir?', 'Seht Ihr denn nicht, dass ich beschäftigt bin?', 'Seht Ihr denn nicht, dass ich beschäftigt bin?', 'Ja? Was gibt es?', 'Ja? Was gibt es?', 'Und Ihr seid?', 'Und Ihr seid?', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9125', 'deDE', 'Wie peinlich von einem Jäger als Tier benutzt zu werden!$B$BIch schulde Euch mein Leben, aber bitte, kümmert Euch nicht weiter um mein Wohlergehen. Jetzt, da Ihr mich von Musel\'ek befreit habt, kann i', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9127', 'deDE', '', 'Möge sich das Licht bei all Euren Bemühungen stets Eurem Willen beugen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9128', 'deDE', '', 'Alle Lebewesen sind miteinander verbunden, ein natürliches Gleichgewicht. Wenn wir vorsichtig sind und nur beobachten, können wir viel über die komplexen Beziehungen zwischen den Organismen um uns her', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9130', 'deDE', '<Verteidiger Aesom grüßt Euch.>$B$BHabt Ihr mit Admetius gesprochen? Ich glaube, dass er sich nach Euch erkundigt hat.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9131', 'deDE', 'Die Druiden beschützen mich vor den Naga und den bösartigen Verirrten. Dafür unterrichte ich sie über die Pflanzen und Tiere der Marschen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9134', 'deDE', 'Seid froh, dass ich hier in diesem Käfig festsitze, $R.', 'Seid froh, dass ich hier in diesem Käfig festsitze, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9136', 'deDE', '<Der gefangene Agent der Sonnenfalken beäugt Euch misstrauisch.>$B$BIch habe Euch noch nie zuvor gesehen. Wer seid Ihr?', '<Der gefangene Agent der Sonnenfalken beäugt Euch misstrauisch.>$B$BIch habe Euch noch nie zuvor gesehen. Wer seid Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9137', 'deDE', '<Der gefangene Agent der Sonnenfalken grinst.>$B$BKeine Sorge, Frischling, es existieren noch viel mehr Portalaufseher und noch weit mehr Orte, die sich für die Erschaffung eines Portals eignen. Ich b', '<Der gefangene Agent der Sonnenfalken grinst.>$B$BKeine Sorge, Frischling, es existieren noch viel mehr Portalaufseher und noch weit mehr Orte, die sich für die Erschaffung eines Portals eignen. Ich b', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9138', 'deDE', 'Es war Sironas Idee... Die Monstrosität war einst einer der ihren. Ursprünglich war er ein hochrangiges Mitglied eines militärischen Ordens der Draenei: das Dreigestirn oder so etwas.$B$BWas auch imme', 'Es war Sironas Idee... Die Monstrosität war einst einer der ihren. Ursprünglich war er ein hochrangiges Mitglied eines militärischen Ordens der Draenei: das Dreigestirn oder so etwas.$B$BWas auch imme', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9139', 'deDE', 'Sironas behauptet, das einige Eredar die Fähigkeit besitzen, andere ihres Volkes in Man\'ari zu verwandeln. Vermutlich waren sogar die Draenei einst Eredar und Sirona ist eine der mächtigsten Eredar.$B', 'Sironas behauptet, das einige Eredar die Fähigkeit besitzen, andere ihres Volkes in Man\'ari zu verwandeln. Vermutlich waren sogar die Draenei einst Eredar und Sirona ist eine der mächtigsten Eredar.$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9140', 'deDE', 'Offensichtlich. Wie sonst konnte sie ihre Reihen infiltrieren und als eine Draenei durchgehen? Sie lebt schon seit langer Zeit unter ihnen.', 'Offensichtlich. Wie sonst konnte sie ihre Reihen infiltrieren und als eine Draenei durchgehen? Sie lebt schon seit langer Zeit unter ihnen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9141', 'deDE', 'Zahlreiche? Sie hat genau zwei Schwachpunkte: Das Fundament der Struktur auf der Westseite und das Fundament auf der östlichen Seite. He, Moment mal. Wie könnt Ihr überhaupt etwas über die Vektorspule', 'Zahlreiche? Sie hat genau zwei Schwachpunkte: Das Fundament der Struktur auf der Westseite und das Fundament auf der östlichen Seite. He, Moment mal. Wie könnt Ihr überhaupt etwas über die Vektorspule', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9142', 'deDE', 'Die Zeit ist gekommen, um die Vektorspule zu vernichten!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9143', 'deDE', '', 'Ich warte geduldig auf Befehle, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9144', 'deDE', 'Endlich ist diese unhöfliche Unterbrechung vorbei. Wie ich bereits sagte, Quagmirran ist in der Nähe!$B$BWie versprochen kann ich Euch einen magischen Gefallen tun, der Euch im Kampf gegen ihn unterst', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9163', 'deDE', 'Das alles ist für Euch, $N. Ihr habt das getan. Ihr habt all diese Leute und tausende mehr gerettet.$B$BIhr habt Euch über das Leid und die Tragödien dieser Welt erhoben und uns alle mit Euch gezogen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9167', 'deDE', 'Ysiel hat bei ihrem Streifzug in die Scherbenwelt mit feindlicher Gegenwehr gerechnet. Deshalb sind wir Aufseher mitgekommen.$B$BWir haben nicht erwartet, dass wir auf Gegner in so großer Zahl und Stä', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9168', 'deDE', '', 'Ich habe keine Ahnung wer Ihr seid, aber Eure Anwesenheit ist mehr als willkommen. Archimonde und seine Armee der Geißel können jeden Moment angreifen, $C. Werdet Ihr Euch gemeinsam mit uns dem Bösen ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9170', 'deDE', '', 'Wir müssen so lange durchhalten, wie wir können! Kämpft weiter!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9174', 'deDE', 'Willkommen im Hauptquartier der Blutritter, den Verteidigern von Quel\'Thalas, den treuen Dienern von Silbermond und wahren Meistern des Lichts.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9176', 'deDE', 'Die Natur bevorzugt jene, die bereit sind, ihre Geheimnisse zu erlernen, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9177', 'deDE', 'Die Natur ist eine starke Macht, $R. Jene, die versuchten sie zu bändigen, haben dies nie dauerhaft geschafft.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9179', 'deDE', '', 'Seid gegrüßt, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9180', 'deDE', 'Die Geister sind rastlos, $R. Achtet auf Eure Umgebung, während Ihr in Nagrand seid, oder Ihre Wut wird Euch verzehren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9181', 'deDE', 'Der Irdene Ring hat es sich zur Aufgabe gemacht, die Stärke und Größe der Elemente zu bewahren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9182', 'deDE', '', 'Wie kann mein Volk kämpfen, wenn die Elemente so in Aufruhr sind? Verstehen sie nicht, dass die Welt ohne die Unterstützung der Elemente einfach nicht existieren kann?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9183', 'deDE', 'Ich bin Untrag vom Irdenen Ring. Versucht, die Elemente nicht zu stören, während Ihr Euch am Thron der Elemente aufhaltet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9184', 'deDE', 'Ich spüre Eure Gier nach Macht, $C!', 'Ich spüre Eure Gier nach Macht, $C!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9186', 'deDE', 'Ich habe keine Zeit für Predigten, $C. Sucht Euer Wissen woanders.', 'Ich habe keine Zeit für Predigten, $C. Sucht Euer Wissen woanders.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9187', 'deDE', 'Mir scheint, Ihr habt die falsche Abzweigung genommen, $C.', 'Mir scheint, Ihr habt die falsche Abzweigung genommen, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9188', 'deDE', 'Seid gegrüßt, $N. Ich hoffe doch, dass Euch niemand gefolgt ist.', 'Seid gegrüßt, $N. Ich hoffe doch, dass Euch niemand gefolgt ist.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9189', 'deDE', 'Wir haben nichts zu besprechen, $C. Vielleicht solltet Ihr nach Gleichgesinnten suchen.', 'Wir haben nichts zu besprechen, $C. Vielleicht solltet Ihr nach Gleichgesinnten suchen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9190', 'deDE', 'Es tut gut, hier mal jemanden anzutreffen, der noch bei Verstand ist. Was kann ich für Euch tun?', 'Es tut gut, hier mal jemanden anzutreffen, der noch bei Verstand ist. Was kann ich für Euch tun?', 'Ah, ein Waldläuferkollege. Was benötigt Ihr?', 'Ah, ein Waldläuferkollege. Was benötigt Ihr?', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9191', 'deDE', 'Euch mangelt es offensichtlich an Verstand, um die Feinheiten des Arkanen zu erfassen, $C. Hinfort mit Euch.', 'Euch mangelt es offensichtlich an Verstand, um die Feinheiten des Arkanen zu erfassen, $C. Hinfort mit Euch.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9192', 'deDE', 'Wie kann ich Euch bei Euren Studien behilflich sein?', 'Wie kann ich Euch bei Euren Studien behilflich sein?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9193', 'deDE', 'Ein eiserner Wille und unerschütterliche Entschlossenheit zeichnet die unseren aus. Ihr scheint keine dieser Eigenschaften Euer Eigen zu nennen, $C.', 'Ein eiserner Wille und unerschütterliche Entschlossenheit zeichnet die unseren aus. Ihr scheint keine dieser Eigenschaften Euer Eigen zu nennen, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9194', 'deDE', 'Dieses Land wurde durch den Absturz verwüstet. Auch wenn Ihr unseren Lehren nicht folgen mögt, $C, so bitte ich Euch doch, was immer in Eurer Macht steht zu tun, um dem Heilungsprozess zu helfen.', 'Dieses Land wurde durch den Absturz verwüstet. Auch wenn Ihr unseren Lehren nicht folgen mögt, $C, so bitte ich Euch doch, was immer in Eurer Macht steht zu tun, um dem Heilungsprozess zu helfen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9196', 'deDE', '', 'Mögen die Winde Euch leiten, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9199', 'deDE', '', 'Ich habe es schon immer gemocht, Dinge zu sortieren und zu organisieren. Ich glaube, das kommt von meiner Mutter.', '', 'Neue Pflanzenarten zu untersuchen ist sehr wichtig für unsere Expedition. Und nicht nur die essbaren oder medizinisch nützlichen.$B$BPflanzen sind die Grundlage für viele andere Formen von Leben auf d', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9201', 'deDE', 'Ich weiß noch nicht, was ich von Eurer Art halten soll, $R. Einige von Euch sind gewalttätig und töten alle Sporlinge, die sie sehen.$B$BIhr seht freundlich aus... aber ein bisschen seltsam, denke ich', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9202', 'deDE', 'Ich heiße Euch in Sporeggar willkommen, $R. Normalerweise bekommen wir hier nicht viele von Euer Art zu Gesicht, aber Ihr habt Euren Wert für uns bewiesen. Fühlt Euch wie zu Hause.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9203', 'deDE', 'Die Sumpflords verschlingen die Sporenbeutel! Sie müssen aufgehalten werden!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9204', 'deDE', 'Dank Euch sind unsere Jungen jetzt sicher, $N. Geht nach Sporeggar und sprecht mit meinem Volk. Sie haben große Dinge über Euch gehört.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9208', 'deDE', '<Die Erde poltert als Gordawgs Bewegungen zum Halten kommen.>$B$BSprecht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9218', 'deDE', 'Mein Vater hat sich in exotischere Gefilde im Süden auf die Jagd nach größerer Beute gemacht. Aber keine Sorge, wir jagen hier immer noch!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9219', 'deDE', 'Seid wachsam, $C. Ihr wisst nie, wann ein Wildtierchen Euch jagt anstatt anders herum.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9220', 'deDE', 'Hört zu, $GFreund:Freundin;. Ich habe die Expedition in die fähigen Hände meines Sohnes Junior übergeben. Wenn man die mächtigsten Tiere von Azeroth gejagt hat, sehnt man sich nach etwas anderem.$B$BU', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9221', 'deDE', '$N! Ich erinnere mich gut an Euer Können als $GJäger:Jägerin;. Euer Anfang im Schlingendorntal mag etwas schwierig gewesen sein, aber Ihr habt durchgehalten und seid in den Kreis der Großen eingetrete', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9226', 'deDE', 'Ich heiße Euch in unserem bescheidenen Tempel willkommen, Geist.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9227', 'deDE', 'Mit den Draenei verhandeln, die uns den Rücken gekehrt haben während wir litten? Ich glaube nicht.$B$BEin wahrer Geist spricht nicht so! Zeigt Euer wahres Gesicht! Wer hat Euch geschickt?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9229', 'deDE', 'Wir sollen also denjenigen vertrauen, die uns mit Täuschungen beleidigen? Halten die Draenei so wenig von uns, dass sie auf Betrügereien angewiesen sind, um unsere Aufmerksamkeit zu erlangen?$B$BWir s', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9230', 'deDE', 'Die Brennende Legion setzt uns ganz schön unter Druck, aber unsere Verteidigung hält stand. Jede Sekunde, die wir Archimonde vom Weltenbaum fernhalten können, bringt uns dem Sieg näher.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9231', 'deDE', 'Gut. Stellt sicher, dass sie es verstehen.$B$BNun würde ich es vorziehen, wenn Ihr meinen Tempel in Frieden lasst.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9235', 'deDE', 'Willkommen in der Oreborzuflucht, $GFreund:Freundin;. Ihr habt hier nichts zu fürchten. Wir sind Zerschlagene, aber nicht Eure Feinde.$B$BIch bin Ikuti von den Kurenai, einer Gruppe Zerschlagener, die', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9236', 'deDE', 'Maktu erkennt Euch nicht, $R, aber ist erfreut, Eure Bekanntschaft zu machen. Falls Ihr jemals Rüstungen kaufen oder reparieren wollt, kommt einfach zu Maktu.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9237', 'deDE', 'Erinnerungen an diesen Tempel verschleiern meine Gedanken wie ein dichter Nebel, aber ich kann sie nicht zu einer sinnvollen Geschichte zusammenfügen. Dieser Ort bedeutete mir einmal etwas. Bis ich di', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9238', 'deDE', 'Es gibt Leute, die mich \'Tim\' nennen, aber für meine Freunde bin ich \'Der Spezialist\'.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9239', 'deDE', 'Wenn Ihr das fragen müsst, kennt Ihr mich bei weitem nicht gut genug, um diese Anrede zu benutzen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9240', 'deDE', 'Grüße, $C. Ihr wollt die Grenzen Eurer Jagdkunst erkunden? Dann seid Ihr hier richtig.$B$BHemet Nesingwary hat die großartigste Expedition zusammengestellt, die diese Welt je gesehen hat. Schließt Euc', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9241', 'deDE', 'Lasst Euch von den Ruinen um uns herum nicht täuschen, $C. Dalaran ist so stark wie eh und je.', 'Lasst Euch von den Ruinen um uns herum nicht täuschen, $C. Dalaran ist so stark wie eh und je.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9242', 'deDE', 'Karazhan ist mehr als ein einfaches Gebäude, $N. Wenn Ihr die Ley-Linien betrachtet, die Azeroth durchziehen, treffen sich alle ziemlich genau hier.$B$B<Alturus zeigt auf einen unsichtbaren Punkt unte', '', 'Erzmagier Alvareux schrieb eine Abhandlung über eine sehr interessante Theorie. Er postulierte die Existenz einer exakten Kopie von Karazhan in einer anderen Welt als unserer, und dass beide gewisserm', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9243', 'deDE', 'Die Naaru sind weiser, als wir begreifen können. Ich habe in den letzten fünf Monaten mehr von A\'dal gelernt, als in all den Jahren in den Bibliotheken in Dalaran.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9244', 'deDE', 'Hier werden Steckbriefe von bekannten Verbrechern und bösartigen Tieren aufgehängt. Es wäre viel zu gefährlich für Euch, Euch jetzt schon auf die Jagd nach den Kopfgeldern hier zu machen, aber schaut ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9246', 'deDE', 'Dieses Schild zeigt verschiedene Ankündigungen und Kopfgeldausschreibungen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9248', 'deDE', 'Willkommen in Zabra\'jin, $C. Ich bin Schattenjäger Denjai, Aufseher dieses Außenpostens und Befehlshaber der Streitkräfte der Dunkelspeere hier in den Zangarmarschen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9249', 'deDE', 'Denkt nicht, Ihr könntet mit meinen Murlocbabys abhauen! Ich habe die Augen eines Falken und Ohren wie ein... wie ein... wie irgendetwas mit mächtig guten Ohren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9250', 'deDE', '', 'Tor\'gash glaubt, er kann die Zukunft aus den Innereien von Vögeln lesen, oder durch das Werfen der Knochen seiner Vorfahren. Ehrlich, alles was man tun muss, ist die Geister zu fragen. Aber versucht n', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9256', 'deDE', 'Ihr habt bewiesen, dass Ihr $Gein sehr fähiger Großwildjäger:eine sehr fähige Großwildjägerin; seid, $N. Aber es gibt immer noch ein Wildtier, von dem Ihr nur träumen könnt... Zahna!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9257', 'deDE', 'Ihr habt sie alle eingesackt: Bach\'lor, Banthar, Aaskralle, sogar die mächtige Zahna! Ihr seid wahrlich $Gder beste Großwildjäger, den:die beste Großwildjägerin, die; ich kenne!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9258', 'deDE', 'Da rang ich mit einem Talbukhirsch im Unterholz, Auge in Auge, nur mit meinem Messer bewaffnet... <hust> und meinem Können als Jäger, um mich zu retten.$B$BPlötzlich haben mich zwei Windrocjäger aus d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9259', 'deDE', 'Als ich das erste mal Pilze gesehen haben, die höher als Häuser waren, war ich mir nicht mehr so sicher, diesen Ort meine Heimat nennen zu können. Nach einer Weile fällt das aber gar nicht mehr auf.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9260', 'deDE', '', 'Vergesst nicht, einige Zeit mit Angeln zu verbringen, während Ihr in den Zangarmarschen seid. Ihr wisst nicht wann ihr wieder Wasser sehen werdet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9261', 'deDE', 'Telredor war einst ein abgeschiedener Rückzugsort für Draeneipriester und Pilger. Die ansässigen Priester erhielten selten Besuch.$B$BAls die Orcs begannen, Draeneisiedlungen anzugreifen, kamen Flücht', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9262', 'deDE', '', 'Die Aussicht von der Spitze Telredors aus ist großartig, oder?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9263', 'deDE', 'Willkommen in Telredor, $C. Falls Ihr irgendetwas benötigt, zögert nicht, zu fragen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9264', 'deDE', 'Ich habe noch nie so viele verschiedene Pilze an einem Ort gesehen. Wenn ich genug Zeit hätte, könnte ich sicherlich für jeden von ihnen eine Verwendung finden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9265', 'deDE', 'In den letzten 20 Jahren hat Telredor mehr Veränderungen gesehen als in den vergangenen Jahrhunderten. Ich kann mich nicht beschweren, $N, denn die Welt hat sich in dieser Zeit noch mehr verändert.$B$', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9267', 'deDE', 'Schattenjäger Denjai hat mich mit einem kleinen Trupp Arbeiter und Krieger hergeschickt, um diesen Außenposten zu errichten. Wir sind gerade fertig mit aufbauen, und er verlangt schon Berichte!$B$BWen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9268', 'deDE', 'Falls Ihr jemals Hexendoktor Tor\'gash trefft, sagt ihm, seine Tränke und Glücksbringer funktionieren nicht! Ich habe genau gemacht, was er sagte, und immer noch hat Magasha nur Augen für Zurai.$B$BGla', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9269', 'deDE', '', 'Reavij hat die Feinfühligkeit eines Kodos! Das Lustige ist, er weiß nicht mal, wie offensichtlich das ist. Ich sehe, wie er mich anstarrt, wenn er denkt, niemand schaut zu.$B$BER ist mir unheimlich. I', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9271', 'deDE', 'Garadar liegt in Trümmern, $GFremder:Fremde;. Seit die Großmutter krank wurde, ist Garrosh in tiefe Depressionen verfallen. Er ist offensichtlich nicht seines Vaters Sohn.$B$B<Kurkush seufzt.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9273', 'deDE', 'Hier werden Belohnungen für die Anführer der Naga der Dunkelkämme und Blutschuppen bekannt gegeben. Sie klingen beide extrem gefährlich, vielleicht solltet Ihr später noch einmal vorbei schauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9274', 'deDE', 'Hier sind Bekanntmachungen über Belohnungen für die Anführer der Naga der Dunkelkämme und Blutschuppen aufgeführt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9275', 'deDE', 'Sie starben. Alle von ihnen...$B$BAls die Finsterblut uns überrannten, ergriff etwas Besitz von mir, dass ich nicht verstehen kann. Sagt mir, habt Ihr jemals eine Wut gespürt, so groß, das Euer ganzes', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9276', 'deDE', 'Finstere Zeiten stehen uns bevor, $GFremder:Fremde;...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9277', 'deDE', 'Die Geister sind sehr aufgeregt. Wir müssen den Grund für ihre Verstörung herausfinden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9283', 'deDE', 'Hemet ist der größte Jäger, den ich kenne. Wir trafen uns vor vielen Monden durch Zufall in den Hügeln von Winterquell. Er und ich waren auf dieselbe Beute aus: einen großen Frostsäbler namens Rak\'shi', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9284', 'deDE', '$GDer große Jäger:Die große Jägerin;, $N, ist zurückgekehrt. Erzählt mir von Eurer neuesten Beute.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9286', 'deDE', 'Ja, ja, ich fühle mich besser, aber irgendwie mag ich es hier unten.', 'Ja, ja, ich fühle mich besser, aber irgendwie mag ich es hier unten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9287', 'deDE', 'Das Konsortium plante diese Operation schon seit Monaten! Wir sollten nach Oshu\'gun graben, dem größten Diamanten des Universums.$B$BMinuten bevor wir anfangen wollten, erhielten wir eine Botschaft vo', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9288', 'deDE', 'Gezhe ist hier, um eine Aufgabe zu erledigen. Ich bin hier, um sicherzustellen, dass ihn niemand dabei stört.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9289', 'deDE', 'Wisst Ihr wer wir sind, $GFremder:Fremde;? Wir sind Mag\'har... braune Orcs werden wir von Euresgleichen genannt. Die Mag\'har sind Überlebende: Überlebende einer gefallenen Dynastie, Überlebende der ro', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9290', 'deDE', '', 'Wir wären nicht abgestürzt, wenn Hemet lernen könnte, nicht durch das Querruder zu schießen, wenn er auf Beute am Boden aus ist.$B$BAber keine Sorge, ich werde das schnell reparieren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9296', 'deDE', 'Seid gegrüßt, $N.', 'He! Habe ich Euch nicht schon mal ein Bündel mit Tierhäuten gegeben?$B$BEgal, Ich bin froh, dass Ihr mich aus den Klauen des bösen Diebus Faulfaust gerettet habt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9297', 'deDE', '', 'Die Großmutter hat alle von uns aufgezogen. Sie ist für alle Mag\'hari, die Ihr seht, etwas ganz besonderes. Wir werden sie vermissen, wenn sie von uns geht.', '', 'Großmutter sagt, ich sei nach einer großen orcischen Heldin benannt.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9312', 'deDE', 'Ihr seht vernünftig aus, $R. Vielleicht können wir uns gegenseitig von Nutzen sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9315', 'deDE', 'Seid stark. Kael\'thas hat uns verraten und wir erhalten ständig neue Nachrichten - doch verliert nicht den Glauben. Wir werden durchhalten, so wie wir schon immer durchgehalten haben. Arthas hat nicht', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9316', 'deDE', 'Wohin in unserer schönen Stadt möchtet Ihr gehen?', 'Wohin in unserer schönen Stadt möchtet Ihr gehen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9317', 'deDE', 'Es gibt zwei Auktionshäuser in Silbermond. Welches bevorzugt Ihr?', 'Es gibt zwei Auktionshäuser in Silbermond. Welches bevorzugt Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9318', 'deDE', 'Das westliche Auktionshaus befindet sich in der Mitte des Basars, im Westen der Straße der Urahnen.', 'Das westliche Auktionshaus befindet sich in der Mitte des Basars, im Westen der Straße der Urahnen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9319', 'deDE', 'Das Auktionshaus des königlichen Markts befindet sich im Osten der Stadt, direkt südlich der Trainingsstätte für Blutritter und Waldläufer.', 'Das Auktionshaus des königlichen Markts befindet sich im Osten der Stadt, direkt südlich der Trainingsstätte für Blutritter und Waldläufer.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9320', 'deDE', 'Welchem Pfad möchtet Ihr folgen?', 'Welchem Pfad möchtet Ihr folgen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9321', 'deDE', 'Die Bank von Silbermond kann im Basar gefunden werden. Sucht in der südöstlichen Ecke.', 'Die Bank von Silbermond kann im Basar gefunden werden. Sucht in der südöstlichen Ecke.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9322', 'deDE', 'Wenn Ihr eine Bank im östlichen Teil der Stadt sucht, braucht Ihr nur zum königlichen Markt zu gehen. Die Bank befindet sich in dessen Nordmauer, ', 'Wenn Ihr eine Bank im östlichen Teil der Stadt sucht, braucht Ihr nur zum königlichen Markt zu gehen. Die Bank befindet sich in dessen Nordmauer, ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9323', 'deDE', 'Himmelsmeisterin Abendlicht und ihre Drachenfalken halten sich direkt außerhalb der Stadt auf, westlich des Hirtentors.', 'Himmelsmeisterin Abendlicht und ihre Drachenfalken halten sich direkt außerhalb der Stadt auf, westlich des Hirtentors.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9324', 'deDE', 'Tandrine ist die Gildenmeisterin von Silbermond. Ihr Aufenthaltsort ist an der Straße der Urahnen, nahe des Magistertors.', 'Tandrine ist die Gildenmeisterin von Silbermond. Ihr Aufenthaltsort ist an der Straße der Urahnen, nahe des Magistertors.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9325', 'deDE', 'Das Gasthaus von Silbermond am königlichen Markt wird von einer talentierten Frau namens Velandra geführt. Es wird gesagt, dass sich dort auch manchmal ein berühmter Kellermeister aufhält.', 'Das Gasthaus von Silbermond am königlichen Markt wird von einer talentierten Frau namens Velandra geführt. Es wird gesagt, dass sich dort auch manchmal ein berühmter Kellermeister aufhält.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9326', 'deDE', 'Es gibt eine große Zahl von Briefkästen in unserer schönen Stadt. Ich kann Euch den bei der Bank des königlichen Markts empfehlen.', 'Es gibt eine große Zahl von Briefkästen in unserer schönen Stadt. Ich kann Euch den bei der Bank des königlichen Markts empfehlen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9327', 'deDE', 'Shalenn kümmert sich um die Tiere in der Stadt und verhindert, dass diese Amok laufen, während ihre Herren hier ihre Zeit verschwenden. Sie steht auf dem Platz der Weltenwanderer, vor der Halle der Wa', 'Shalenn kümmert sich um die Tiere in der Stadt und verhindert, dass diese Amok laufen, während ihre Herren hier ihre Zeit verschwenden. Sie steht auf dem Platz der Weltenwanderer, vor der Halle der Wa', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
