INSERT INTO `npc_text_localized` VALUES ('9329', 'deDE', 'Die Kampfmeister halten sich tief in den Hallen der Blutritter am Platz der Weltenwanderer auf. Sie mögen etwas grob sein, aber ihre Talente sind unverzichtbar.', 'Die Kampfmeister halten sich tief in den Hallen der Blutritter am Platz der Weltenwanderer auf. Sie mögen etwas grob sein, aber ihre Talente sind unverzichtbar.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
