INSERT INTO `npc_text_localized` VALUES ('9331', 'deDE', 'Welchem Pfad möchtet Ihr folgen?', 'Welchem Pfad möchtet Ihr folgen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9332', 'deDE', 'Oh, Ihr sucht einen von... denen. Wenn Ihr Eure Zeit wirklich mit diesen Hinterwäldlern verschwenden wollt, findet Ihr die Waldläufer auf dem Platz der Weltenwanderer. Zandine kann sich um Euch kümmer', 'Oh, Ihr sucht einen von... denen. Wenn Ihr Eure Zeit wirklich mit diesen Hinterwäldlern verschwenden wollt, findet Ihr die Waldläufer auf dem Platz der Weltenwanderer. Zandine kann sich um Euch kümmer', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
