INSERT INTO `npc_text_localized` VALUES ('9365', 'deDE', 'Hütet Eure Zunge, $R. Ich habe in meinem Leben Dinge gesehen, von denen Ihr nur in Legenden gehört habt. Ich habe Männer, Frauen und Kinder ohne Gnade getötet. Ich habe unter dem Banner zweier Kriegsh', 'Hütet Eure Zunge, $R. Ich habe in meinem Leben Dinge gesehen, von denen Ihr nur in Legenden gehört habt. Ich habe Männer, Frauen und Kinder ohne Gnade getötet. Ich habe unter dem Banner zweier Kriegsh', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
