INSERT INTO `npc_text_localized` VALUES ('9370', 'deDE', 'Kein übergroßer Käfer wird mich aus den Zangarmarschen jagen! Ich werde ihnen zeigen, aus welchem Holz die Forscherliga geschnitzt ist...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9373', 'deDE', 'Registranten müssen zuerst mit Aufseher Bullrok sprechen, bevor sie Aufgaben bekannt geben können.$B$BUnerlaubte Bekanntmachungen werden bestraft.$B$BKeine Rechnungen veröffentlichen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9374', 'deDE', 'Eingeschriebene müssen zuerst mit Aufseher Moi\'bff Jill sprechen, bevor sie Aufgaben bekannt geben können.$B$BUnerlaubte Bekanntmachungen werden bestraft.$B$BKeine Rechnungen veröffentlichen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9375', 'deDE', 'Schaut auf den Aushang, wenn Ihr Arbeit sucht, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9376', 'deDE', 'Sie haben keinen Respekt für die Toten, $R. Den Ogern muss eine Lektion erteilt werden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9377', 'deDE', 'Wir müssen nach Überlebenden suchen. Wenn noch jemand am Leben ist, müssen wir zumindest versuchen, sie zurückzuholen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9383', 'deDE', 'Wer seid Ihr? Was wollt Ihr hier?', '', 'Warum habt Ihr uns das angetan?', '', 'Es gibt VIER Lichter!', '', 'Meine bösartigen Teromotten werden mich rächen!', '', 'Wohin sind alle gegangen?', '', 'Essensgäste sollten respektvoller sein!', '', 'Oh, welch heiliger Ort wurde hier gestürzt!', '', 'Salbt meinen Kopf mit Heilbalsam. Mögt Ihr meine Dornenkrone?', '');
INSERT INTO `npc_text_localized` VALUES ('9384', 'deDE', 'Ich kann Euch das Handwerk des Bergbaus beibringen. Seid Ihr interessiert?', 'Ich kann Euch das Handwerk des Bergbaus beibringen. Seid Ihr interessiert?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
