INSERT INTO `npc_text_localized` VALUES ('9387', 'deDE', '', 'Fantastisch! Anetherons Niederlage durch Eure Hand, hat Archimondes Pläne weiter verzögert und uns dem Sieg ein Stückchen näher gebracht. Dennoch können meine Truppen hier nicht länger verweilen. Mein', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
