INSERT INTO `npc_text_localized` VALUES ('9391', 'deDE', 'Seid vorsichtig, wenn Ihr in dieses Dickicht geht, $C. Es liegt eine böse Aura über diesem Ort. Dabei muss es sich um Reste von dem handeln, was die anderen Druiden zerstört hat.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9392', 'deDE', 'Dieser Wald ist ein gespenstischer Ort. In ihm steckt mehr schlechtes Juju als die bloße Allianz, passt also gut auf Euch auf.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9393', 'deDE', '', 'Die Horde ist nicht die einzige Gefahr in diesem Wald. Lasst Euch nicht durch die scheinbare Ruhe täuschen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9394', 'deDE', 'Ihr seid zweifellos stark, $R, aber verfügt Ihr auch über das, was man braucht, um Champion der Totschläger zu werden?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9395', 'deDE', 'Aus dem Weg, $R. Könnt Ihr nicht sehen, dass wir mitten in einem Kampf sind!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9398', 'deDE', 'Außerordentlich! Einen Grubenlord wie Azgalor zu töten ist eine beachtliche Leistung, $Gmein Freund:meine Freundin;. Wäre die Zeit nicht so knapp, wäre es mir ein Vergnügen, die Geschichte Eure Sieges', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9401', 'deDE', 'Die Leiche eines der Zerschlagenen liegt hier. Seltsamerweise scheint die Todesursache nichts mit den mutierten Motten zu tun zu haben.$B$BVielleicht wurde sein Tod durch das seltsame Objekt verursach', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9403', 'deDE', 'Ein Hoch auf den Champion! Habt Ihr darüber nachgedacht, ernsthaft in das Gladiatorengeschäft einzusteigen? Zusammen könnten wir einen Berg Gold machen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
