INSERT INTO `npc_text_localized` VALUES ('9410', 'deDE', '', 'Der Moment, vor dem wir uns solange gefürchtet haben, ist gekommen, $C. Archimonde hat es bis zu Nordrassil geschafft. Er hat bereits damit begonnen, die Energien des Weltenbaums anzuzapfen. Malfurion', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9414', 'deDE', 'Herzlich willkommen in der Taverne der Zeit!', '', 'Ruht Euch etwas aus, wir haben alle Zeit der Welt...', '', 'Kann ich Euch etwas zu trinken anbieten? Ich versichere Euch, es ist älter und reifer als alles, was Ihr je getrunken habt!', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9417', 'deDE', 'Die Taverne Weltenend hat meinesgleichen nicht viel zu bieten. Jedoch zieht ein solches Etablissement genau die Art von Gästen an, die ein gesundes Interesse an meinen Waren haben.$B$BMeine Spezialitä', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9419', 'deDE', 'Was seht Ihr, wenn Ihr nach Westen schaut, $GSterblicher:Sterbliche;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9420', 'deDE', 'Wisst Ihr was ich sehe? Vorzeichen einer Invasion.$B$BJa, der Meister war weise, die Portale zu stehlen, aber seit dem hat er... wie soll ich es sagen... seinen Fokus verloren. Ja... Fokus.$B$BDurch d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9421', 'deDE', 'Die Brennende Legion. Dämonen. Ich sehe sie so klar, wie Ihr Eure eigene Hand vor Augen seht. Das heißt, bisher konnte ich das.$B$BMit der Rückkehr der Legion gibt es auch neue Machenschaften. Unter n', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9422', 'deDE', 'Sie weichen mir aus. Ich kann nicht hinter Ihre Verteidigung sehen. Sie haben Schutzzauber platziert, um der Entdeckung zu entgehen.$B$B<Altruis zeigt nach Westen.>$B$BSie fürchten uns, weil sie wisse', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9423', 'deDE', '<Altruis wendet sich Euch zu und lächelt.>$B$BDas tue ich nicht.$B$B<Altruis zeigt auf sein Reittier, Nethrandamus.>$B$BWir müssen sie aufhalten, bevor die in diesen Konstruktionslagern fertigstellen ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9424', 'deDE', 'So werden die Bauwerke genannt, die hier in der Landschaft verstreut sind. Die Legion beschäftigt die Mo\'arg und Gan\'arg, um in diesen mobilen Konstruktionslagern Massenvernichtungswaffen herzustellen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9425', 'deDE', 'Oh nein, nicht Ihr schon wieder! Geht weg!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9427', 'deDE', 'Die Informationen, die Ihr gesammelt habt, führen zu folgender Erkenntnis.$B$BEs gibt zwei Hauptkonstruktionslager: das Konstruktionslager: Hass direkt westlich von uns und das Konstruktionslager: Fur', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9429', 'deDE', 'Ich mache nebenbei meinen eigenen Profit, während die anderen noch darüber streiten, ob es sicher ist, nach Oshu\'gun zu gehen.$B$BZeit ist Gold!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
