INSERT INTO `npc_text_localized` VALUES ('9503', 'deDE', 'Üben! Bald bin ich so gut, dass ich mich den anderen Beschwörern anschließen kann und in das Schattenlabyrinth nach Auchindoun darf.$B$BIch hoffe, dass ich etwas ganz Großes rufen darf! Vielleicht so ', 'Üben! Bald bin ich so gut, dass ich mich den anderen Beschwörern anschließen kann und in das Schattenlabyrinth nach Auchindoun darf.$B$BIch hoffe, dass ich etwas ganz Großes rufen darf! Vielleicht so ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
