INSERT INTO `npc_text_localized` VALUES ('9508', 'deDE', '', 'Hallo, Liebes. Ich bin Geyah, Oberin der Mag\'har.$B$B<Großmutter Geyah hustet.>$B$BWenn Ihr hier seid, kann ich sicher annehmen, dass das Portal geöffnet wurde. Die meisten der Orcs hier waren zu jung', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
