INSERT INTO `npc_text_localized` VALUES ('9515', 'deDE', 'Hier sind noch einige von den Bomben, $N. Seid vorsichtig damit, wenn man sie falsch benutzt, können sie das Zeitgefüge ganz leicht durcheinander bringen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
