INSERT INTO `npc_text_localized` VALUES ('9553', 'deDE', 'Die Paladinlehrer der Exodar befinden sich alle in der hell erleuchteten Kapelle des Lichts beim Sitz der Naaru.', 'Die Paladinlehrer der Exodar befinden sich alle in der hell erleuchteten Kapelle des Lichts beim Sitz der Naaru.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9554', 'deDE', 'Die Räumlichkeiten des Ordens des Lichts am Eingang zum Sitz der Naaru beherbergen unsere Priesterlehrer. Das Licht sei mit Euch.', 'Die Räumlichkeiten des Ordens des Lichts am Eingang zum Sitz der Naaru beherbergen unsere Priesterlehrer. Das Licht sei mit Euch.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9555', 'deDE', 'Einen Lehrer für welchen Beruf sucht Ihr?', 'Einen Lehrer für welchen Beruf sucht Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9556', 'deDE', 'In der Kristallhalle werdet ihr eine Reihe Schamanenlehrer finden. Vielleicht solltet Ihr den Zerschlagenen gegenüber etwas Erbarmen zeigen und Weissager Nobundo aufsuchen. Das Licht sei mit Euch.', 'In der Kristallhalle werdet ihr eine Reihe Schamanenlehrer finden. Vielleicht solltet Ihr den Zerschlagenen gegenüber etwas Erbarmen zeigen und Weissager Nobundo aufsuchen. Das Licht sei mit Euch.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9557', 'deDE', 'Ihr findet die Kürschnerlehrer der Exodar bei den Lederern auf der Händlertreppe. Möge das Licht Euch segnen.', 'Ihr findet die Kürschnerlehrer der Exodar bei den Lederern auf der Händlertreppe. Möge das Licht Euch segnen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9558', 'deDE', 'Ihr findet Arthaid, unseren Stallmeister, außerhalb des Versorgungszentrums. Er befindet sich auf der oberen Ebene des Zentralgewölbes der Exodar in der Nähe des Haupteingangs.', 'Ihr findet Arthaid, unseren Stallmeister, außerhalb des Versorgungszentrums. Er befindet sich auf der oberen Ebene des Zentralgewölbes der Exodar in der Nähe des Haupteingangs.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9559', 'deDE', 'Vor langer Zeit trugen wir nur einteilige silberne Overalls. Unseren einfallsreichen Schneidern sei Dank, können wir heute auf solch eine unpraktische Kleidung verzichten. Ihr findet unsere modischen ', 'Vor langer Zeit trugen wir nur einteilige silberne Overalls. Unseren einfallsreichen Schneidern sei Dank, können wir heute auf solch eine unpraktische Kleidung verzichten. Ihr findet unsere modischen ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9562', 'deDE', 'Ihr möchtet also ein großer Krieger sein? Kriege machen niemanden groß. Wie auch immer, ihr findet unsere Kriegerlehrer auf der Terrasse der Krieger, hoch über der restlichen Händlertreppe.', 'Ihr möchtet also ein großer Krieger sein? Kriege machen niemanden groß. Wie auch immer, ihr findet unsere Kriegerlehrer auf der Terrasse der Krieger, hoch über der restlichen Händlertreppe.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9563', 'deDE', 'Willkommen in der Bank des königlichen Markts, $GSire:holde Dame;. Womit kann ich Euch heute dienen?', 'Willkommen in der Bank des königlichen Markts, $GSire:holde Dame;. Womit kann ich Euch heute dienen?', 'Seid gegrüßt! Womit kann die Bank des königlichen Markts Euch heute dienen?', 'Seid gegrüßt! Womit kann die Bank des königlichen Markts Euch heute dienen?', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9564', 'deDE', 'Vielen Dank, dass Ihr Euch für die Bank von Silbermond entschieden habt. Was kann ich heute für Euch tun?', 'Vielen Dank, dass Ihr Euch für die Bank von Silbermond entschieden habt. Was kann ich heute für Euch tun?', 'Willkommen in der Bank von Silbermond, $G Sire:Mylady;. Wünscht Ihr, einen Blick auf Eure Einlagen zu werfen?', 'Willkommen in der Bank von Silbermond, $G Sire:Mylady;. Wünscht Ihr, einen Blick auf Eure Einlagen zu werfen?', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9565', 'deDE', 'Ihr wollt Euch im Umgang mit neuen Waffen üben? Unser Waffenmeister befindet sich auf der obersten Ebene der Händlertreppe auf der Terrasse der Krieger.', 'Ihr wollt Euch im Umgang mit neuen Waffen üben? Unser Waffenmeister befindet sich auf der obersten Ebene der Händlertreppe auf der Terrasse der Krieger.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9566', 'deDE', 'Ja, $TBürger:Bürgerin;, welche wichtigen Angelegenheiten benötigen meine Aufmerksamkeit?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9567', 'deDE', 'Die Lagerräume der Exodar sind hervorragend geschützt. Eure Besitztümern sind hier sicher.', 'Die Lagerräume der Exodar sind hervorragend geschützt. Eure Besitztümern sind hier sicher.', 'Wir kontrollieren den Zugang zu den Ladeeinrichtungen der Exodar. Gibt es etwas, das Ihr gerne einlagern oder abholen möchtet?', 'Wir kontrollieren den Zugang zu den Ladeeinrichtungen der Exodar. Gibt es etwas, das Ihr gerne einlagern oder abholen möchtet?', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9568', 'deDE', '', 'Hallo! Denkt Ihr, Ihr könntet mir hier heraus helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9574', 'deDE', 'Ja? Was gibt es?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9575', 'deDE', 'Ich nehme an, dass Ihr keine Neuigkeiten über die Minen habt, oder?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9577', 'deDE', 'Hier gibt es nichts zu sehen. Geht weiter.', 'Hier gibt es nichts zu sehen. Geht weiter.', 'Das geht Euch nichts an. Macht, dass Ihr weiter kommt.', 'Das geht Euch nichts an. Macht, dass Ihr weiter kommt.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9578', 'deDE', 'Ich danke Euch, meine Freunde. Dank Euch habe ich meine Freiheit wieder. Wo ist Taretha? Ich hatte gehofft, sie vor meinem Aufbruch in die Berge noch ein letztes Mal sehen und mich bei ihr bedanken zu', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9579', 'deDE', 'Was wollt Ihr damit sagen? Befindet sich Taretha in Gefahr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9580', 'deDE', 'Ich werde nichts dergleichen tun. Ich kann Taretha nicht einfach so zurücklassen und in die Berge flüchten. Ich bin kein Feigling. Also, wo ist sie?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9584', 'deDE', '<Als Ihr Botschafter Frasaboo in der Sprache der Furbolg begrüßt, lächelt er.>$B$BIhr müsst $Gder:die; sein, von $Gdem:der; in meiner Prophezeiung die Rede war. Es ist mir eine große Ehre, Euch kennen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9586', 'deDE', 'In all den Jahren als Mitglied der Forscherliga war ich schon überall in Azeroth, aber nie zuvor habe ich etwas wie die Blutmythosinsel gesehen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9587', 'deDE', 'Bitte, redet nicht mit mir. Ich möchte nicht in noch mehr Schwierigkeiten mit denen geraten.', 'Bitte, redet nicht mit mir. Ich möchte nicht in noch mehr Schwierigkeiten mit denen geraten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9591', 'deDE', '', 'Wir sind gerade erst angekommen, und haben bereits zu viele unserer Freunde und Kameraden begraben müssen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9595', 'deDE', '', 'Wenn Ihr nicht hier seid, um der Sache zu helfen, verschwindet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9596', 'deDE', 'Mit Eurer Hilfe können wir hier erfolgreich sein!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9597', 'deDE', 'Tarrens Mühle liegt hinter diesen Bäumen. Das Durcheinander auf Burg Durnholde sollte uns genug Zeit geben, um Taretha zu finden, bevor es hier überall von Soldaten der Menschen nur so wimmelt. Lasst ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9598', 'deDE', 'Ihr könnt bei mir im Austausch gegen Kampfmarken und Forschermarken von Halaa Gegenstände erwerben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9599', 'deDE', 'Ich kann Euch die Kunst der Kürschnerei beibringen. Interessiert?', 'Ich kann Euch die Kunst der Kürschnerei beibringen. Interessiert?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9602', 'deDE', 'Meint Ihr das Gasthaus von Silbermond oder die Taverne Wanderers Ruh\'?', 'Meint Ihr das Gasthaus von Silbermond oder die Taverne Wanderers Ruh\'?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9603', 'deDE', 'Die Taverne Wanderers Ruh\' liegt zwischen der Straße der Urahnen und dem Basar. Ihr könnt es von beiden Richtungen aus erreichen.', 'Die Taverne Wanderers Ruh\' liegt zwischen der Straße der Urahnen und dem Basar. Ihr könnt es von beiden Richtungen aus erreichen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9610', 'deDE', '', 'Vielen Dank, dass Ihr Thrall bei seiner Flucht geholfen habt, meine Freunde. Nun hoffe ich, dass Ihr auch mir helfen könnt. Ich bin diesen Morgen aufgewacht, nur um mich in diesem magischen Gefängnis ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9613', 'deDE', '', 'Ja, Freunde. Dieser Mann war kein Zauberer aus Dalaran. Seine Kleidung war recht ungewöhnlich und sein Akzent war mir unbekannt. Er redete ständig von Zeit hier und Zeit da. Ihr müsst diesen Mann find', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9614', 'deDE', 'Ich bin froh, dass Taretha noch am Leben ist. Wir müssen nun einen Weg finden, um sie von ihren magischen Fesseln zu befreien.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9615', 'deDE', '', 'Ich kam her als Abgesandte des unteren Viertels, um unserem Bestreben Ausdruck zu verleihen, Auchindoun von der Heimsuchung des Schattenrats zu befreien. Das ist notwendig, wenn wir eine Katastrophe v', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9617', 'deDE', 'Nehmt Platz, $GBruder:Schwester;. Die Verheerungen der Zangarmarschen können $Geinem Mann:einer Frau; viel abverlangen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9618', 'deDE', 'Wenn Ihr Euch ausruh\'n wollt, braucht nur fragen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9619', 'deDE', 'Danke für Eure Hilfe bei der Herstellung der Salbe, $N. Sobald sie wirkt und die Schmerzen gelindert sind, werde ich sehen, wie weit ich mit diesem Bein komme.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9620', 'deDE', '', 'Willkommen in Garadar, $GFremder:Fremde;. Sucht Ihr einen Ort, um Euch auszuruhen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9622', 'deDE', 'Ich bin Skaltesh, spiritueller Führer für jene Schamanen, die hier Erleuchtung suchen... doch auch denen, die anderen Pfaden folgen, kann ich das eine oder andere zeigen. Wünscht Ihr zu sehen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9623', 'deDE', 'Trinkt von dieser Phiole und der Nebel wird sich für kurze Zeit lüften. Geht mit den Geistern, $Gmein Freund:meine Freundin;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9624', 'deDE', 'Sylann, eine der besten Köchinnen der Stadt, hat ein Zimmer im ersten Stock der Taverne Wanderers Ruh\'. Ihr könnt dort auch Kochbedarfswaren von ihrem Assistenten erwerben.', 'Sylann, eine der besten Köchinnen der Stadt, hat ein Zimmer im ersten Stock der Taverne Wanderers Ruh\'. Ihr könnt dort auch Kochbedarfswaren von ihrem Assistenten erwerben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9626', 'deDE', 'Während dem Winterhauchfest versteckt Altvater Winter für alle Spielzeuge unter dem Baum in Eisenschmiede.', 'Während dem Winterhauchfest versteckt Altvater Winter für alle Spielzeuge unter dem Baum in Eisenschmiede.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9630', 'deDE', 'Meine Schwester Silanna ist den Gefahren des Waldes gegenüber blauäugig. Aber ob sie es mag oder nicht, sobald ich diesen Wagen vollgeladen habe, ziehen wir in die Stadt.$B$BEs scheint, als hätte ich ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9631', 'deDE', 'Ich habe nicht viel Zeit für Geschwätz, $R. Oder wollt Ihr mir mit meinem Problemchen hier helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9632', 'deDE', 'Kann Euch nicht genug dafür danken, dass Ihr mir mit den Minen geholfen habt. Nun müssen nur noch die Minenarbeiter überzeugt werden, wieder an die Arbeit zu gehen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9634', 'deDE', '<Isfar seufzt.>$B$BEin paar Baumhäuser sind noch lange kein Zuhause, aber schon viel besser als die Sethekkhallen.$B$BSo bequem es in Shattrath war, kann ich es nicht über mich bringen, ohne meine Sch', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9636', 'deDE', 'Jetzt ist unsere Chance! Lasst mich aus dem Käfig raus!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9637', 'deDE', 'Der Ring der Prüfung war einst ein Ort der Ehre und Würde. Aber dann kam das Dampfdruckkartell mit ihren Wagen voller Gold und Federn, die schärfer waren, als jede Orcaxt...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9638', 'deDE', 'Die Sethekk glauben, dass sie die wahren und treuesten Anhänger des Meisters aller Arakkoa sind. Fragt mich nicht nach dem Meister, $R, denn seine Geheimnisse sind nur für das gefiederte Volk bestimmt', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9640', 'deDE', 'Der Klauenkönig Ikiss ist einer der Anführer der Sethekk, zusammen mit meinem Bruder, Dunkelwirker Syth.$B$BSie haben den Auszug der Sethekk aus Skettis zu den Ruinen von Auchindoun angeführt. Zu Anfa', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9641', 'deDE', 'Er ist der größte Held, den die Arakkoa je kannten.$B$BMan sagt, dass er in alten Zeiten die Stadt Skettis mit seinen eigenen Klauen erbaut hat. Er war unser größter Champion und Verteidiger, aber er ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9642', 'deDE', 'Nur Muskeln, kein Hirn, das sind die Peons! Sie sind großartig, wenn sie motiviert sind, aber sie brauchen einen An-Tritt um loszulegen, wenn Ihr versteht, was ich meine.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9643', 'deDE', '', 'Gefällt Euch etwas, von dem, was Ihr seht?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9644', 'deDE', 'Wenn Ihr Geld habt, habe ich, was Ihr braucht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9645', 'deDE', '', 'Seht Euch ruhig um und lasst es mich wissen, wenn Ihr etwas gefunden habt, das Euch gefällt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9646', 'deDE', '', 'Bitte, nehmt Euch ruhig Zeit und betrachtet die Waffen in aller Ruhe. Ich repariere auch Waffen und Rüstungen, falls Ihr derartige Dienste benötigt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9647', 'deDE', 'Ich habe genau das, was Ihr für Euren Zauber braucht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9648', 'deDE', 'Um in Nagrand Ruhe und Frieden wiederherzustellen, braucht es eine scharfe Klinge und verstärkte Rüstung. Soll ich Eure Ausrüstung reparieren, damit Ihr Gerechtigkeit walten lassen könnt?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9649', 'deDE', 'Sucht Ihr Reagenzien oder Zutaten für Gifte? Dann seid Ihr hier richtig!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9650', 'deDE', 'Ich verkaufe Waren für Handwerker. Nur die Grundlagen, nichts Ausgefallenes.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9651', 'deDE', '', 'Sucht Ihr einen Platz für Euer Tier?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9652', 'deDE', '', 'Habt Ihr Euch verlaufen? Die meisten Händler befinden sich in der Stadtmitte.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9653', 'deDE', '', 'Ich kann Euch Krimskrams verkaufen, oder auch nur Krams.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9654', 'deDE', 'Es treibt mich in den Wahnsinn. Ich kann diese verdammten Eier nicht erreichen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9656', 'deDE', '', 'Jahrelang war die Ehrenfeste die letzte Bastion gegen die Schrecken der Höllenfeuerhalbinsel. Doch jetzt, wo sich das Dunkle Portal erneut geöffnet hat und beständig Entsatz aus Azeroth eintrifft, fin', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9657', 'deDE', '', 'Es ist bedauerlich, dass sich unter all unseren Feinden auf der Höllenfeuerhalbinsel auch die Orcs der Höllenfeuerzitadelle befinden.$B$BDoch wer vermag die Horde besser herauszufordern als... Orcs!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9659', 'deDE', 'Gut, Euch wiederzusehen, $N. Nur ein paar Feineinstellungen mehr, und die Richtung des Sprungs sollte perfekt sein!', 'Gut, Euch wiederzusehen, $N. Nur ein paar Feineinstellungen mehr, und die Richtung des Sprungs sollte perfekt sein!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9686', 'deDE', 'Ihr findet Altvater Winter vor der Bank von Eisenschmiede. Geschenke werden beim Baum in seiner Nähe verteilt.', 'Ihr findet Altvater Winter vor der Bank von Eisenschmiede. Geschenke werden beim Baum in seiner Nähe verteilt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9688', 'deDE', 'Geht in das Mystikerviertel von Eisenschmiede und sprecht dort mit den Druiden der Nachtelfen. Sie können Euch in null Komma nichts zur Mondlichtung transportieren.', 'Geht in das Mystikerviertel von Eisenschmiede und sprecht dort mit den Druiden der Nachtelfen. Sie können Euch in null Komma nichts zur Mondlichtung transportieren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9692', 'deDE', 'Auf der Mondlichtung sind die Festivitäten des Mondfests in vollem Gange. Ihr könnt Euch zu Fuß auf den Weg machen, oder die Druiden in der Enklave des Cenarius aufsuchen. Sie verfügen über die Fähigk', 'Auf der Mondlichtung sind die Festivitäten des Mondfests in vollem Gange. Ihr könnt Euch zu Fuß auf den Weg machen, oder die Druiden in der Enklave des Cenarius aufsuchen. Sie verfügen über die Fähigk', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9696', 'deDE', 'Die Druiden von Azeroth halten ein großes Fest auf der Mondlichtung ab. Sucht in den Städten nach den Feiernden des Mondfests, sie können Euch dorthin transportieren.', 'Die Druiden von Azeroth halten ein großes Fest auf der Mondlichtung ab. Sucht in den Städten nach den Feiernden des Mondfests, sie können Euch dorthin transportieren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9700', 'deDE', 'Falls Ihr die Feiernden des Mondfests in Silbermond findet, können sie Euch zur Mondlichtung transportieren, damit Ihr die Feierlichkeiten dort genießen könnt.', 'Falls Ihr die Feiernden des Mondfests in Silbermond findet, können sie Euch zur Mondlichtung transportieren, damit Ihr die Feierlichkeiten dort genießen könnt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9706', 'deDE', 'Die Feierlichkeiten des Winterhauchfests sind ein Quell der Wärme in dieser ach so kalten Jahreszeit, und Orgrimmar ist der Dreh- und Angelpunkt dieser wundervollen Tradition.', 'Die Feierlichkeiten des Winterhauchfests sind ein Quell der Wärme in dieser ach so kalten Jahreszeit, und Orgrimmar ist der Dreh- und Angelpunkt dieser wundervollen Tradition.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9710', 'deDE', 'Noch immer richten wir uns nach den alten Traditionen, obwohl die Seele sich während des Winterhauchfests leer anfühlt. Doch in Orgrimmar, da finden die wahren Feierlichkeiten statt.', 'Noch immer richten wir uns nach den alten Traditionen, obwohl die Seele sich während des Winterhauchfests leer anfühlt. Doch in Orgrimmar, da finden die wahren Feierlichkeiten statt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9712', 'deDE', 'Dort wo einst die Sonne in unseren Herzen schien, hat nun der Mond ihren Platz eingenommen. Solltet Ihr Euch dennoch dazu entschließen, an den Feierlichkeiten teilzunehmen, begebt Euch zur Mondlichtun', 'Dort wo einst die Sonne in unseren Herzen schien, hat nun der Mond ihren Platz eingenommen. Solltet Ihr Euch dennoch dazu entschließen, an den Feierlichkeiten teilzunehmen, begebt Euch zur Mondlichtun', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9713', 'deDE', 'Ihr da - kommt her. Sind diese Klingen nicht von bester Qualität? Ich wollt sicher von mir kaufen. Nirgends sonst findet Ihr Waffen, die mit solcher Kunstfertigkeit und Hingabe geschmiedet wurden, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9714', 'deDE', 'Was starrt Ihr so, $R?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9729', 'deDE', 'Die wilden Ebenen von Nagrand gehören zu den besten Jagdgründen, die ich kenne.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9730', 'deDE', 'Mögt Ihr Fisch, $R? Ich habe Fisch. Wenn Ihr frischen Fisch mögt, den frischesten, dann kommt und begutachtet meine Waren!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9733', 'deDE', 'Schaut diesen Zerschlagenen niemals direkt in die Augen. Ich glaube, sie können Euch die Seele aussaugen, wenn sie wollen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9734', 'deDE', 'Verschwindet, Knirps! Könnt Ihr nicht sehen, dass ich trainiere? Genau, stimmt, das wird der nächste Champion der Totschläger werden.$B$BJa, ich bin fast bereit für den Ring des Blutes oben im Norden,', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9735', 'deDE', 'Na sieh einer an. Seid Ihr aber groß und zäh... Vielleicht glaubt Ihr Euch ja stark genug, um in den Ring des Blutes zu steigen und den Champion der Totschläger zu bekämpfen?$B$B<Der Grubenkämpfer lac', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9738', 'deDE', 'Der Verhörer will herausfinden, was diese Blutelfen hier in Nagrand machen. Ich habe gehört, sie suchen Rückstände der Kristalle von Oshu\'gun, dem diamantenen Berg im Westen.$B$BOhne Zweifel brauchen ', 'Der Verhörer will herausfinden, was diese Blutelfen hier in Nagrand machen. Ich habe gehört, sie suchen Rückstände der Kristalle von Oshu\'gun, dem diamantenen Berg im Westen.$B$BOhne Zweifel brauchen ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9739', 'deDE', 'Die Draenei sind zurück! Eine Gruppe von Ihnen griff zwei meiner besten Wolfsreiter an, als diese die westlichen Straßen patrouillierten. Natürlich waren wir in diesem Gefecht siegreich.$B$BIch bin ni', 'Die Draenei sind zurück! Eine Gruppe von Ihnen griff zwei meiner besten Wolfsreiter an, als diese die westlichen Straßen patrouillierten. Natürlich waren wir in diesem Gefecht siegreich.$B$BIch bin ni', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9749', 'deDE', 'Wir haben bereits die Kontrolle über die Ruinen der Zwillingsspitze. Geht dort hin und verteidigt sie!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9750', 'deDE', 'Wir haben bereits die Kontrolle über die Ruinen der Zwillingsspitze. Geht dort hin und verteidigt sie!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9751', 'deDE', 'Diese alte Tafel der Draenei wurde instand gesetzt und wird jetzt verwendet, um Nachrichten und Kopfgelder bekanntzugeben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9752', 'deDE', 'Schättenjäger Denjai benutzt diesen Aushang, um wichtige Neuigkeiten und Kopfgelder für die Feinde von Zabra\'jin bekannt zu geben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9755', 'deDE', 'Ahh, Ihr konntet nicht widerstehen, was? Wolltet besser, stärker, schneller sein? Jedes dieser Amulette tut garantiert genau das, was es verspricht. Kommt her und kauft diese wundervollen Spielereien!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9766', 'deDE', '', 'Willkommen. Wollt Ihr Euch ausruhen, oder vielleicht eine Mahlzeit?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9769', 'deDE', '', 'Wir müssen hinter das Geheimnis des Riesenkristalls kommen, welcher in den Himmelssturzsee fiel.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9770', 'deDE', 'Manchmal ist der direkte Weg die beste Lösung für ein Problem.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9771', 'deDE', 'Falls Ihr jemals von der Legion gefangen genommen werdet, sagt ihnen \'Xar il romath da tidesbi.\" Sie werden Euch sofort töten, weil Ihr ihren Gott beleidigt habt, und ersparen Euch damit unerträgliche', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9774', 'deDE', 'Wir sind hier, um Gadgetzans Interessen im Krater von Un\'Goro zu wahren, und das geht Euch nichts an.', 'Wir sind hier, um Gadgetzans Interessen im Krater von Un\'Goro zu wahren, und das geht Euch nichts an.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9775', 'deDE', '', 'Die Höllenfeuerhalbinsel ist ein geschundenes Land, hart und unbarmherzig zu seinen Einwohnern. Ihr solltet auf jeden Fall Eure Ausrüstung reparieren und Eure Vorräte auffüllen, bevor Ihr los zieht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9777', 'deDE', 'Macht keine lauten Geräusche und haltet Eure Augen offen, $N. Wir sind hier von Feinden umgeben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9778', 'deDE', 'Ihr müsst die Brandsätze auf den Fässern in den Internierungsbaracken von Burg Durnholde platzieren. Sobald fünf Bomben gezündet wurden und die Wachen dadurch abgelenkt sind, sollte der Weg in das Kel', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9779', 'deDE', 'Wenn es Euer Wunsch ist, kann ich Euch direkt nach Durnholde bringen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9780', 'deDE', 'Ihr solltet Euch ein Bündel mit Brandbomben von Erozion holen, bevor ich Euch nach Durnholde fliege. Wir dürfen keine Zeit verschwenden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9781', 'deDE', 'Schrecklich... einfach schrecklich!$B$BWir gingen unseren Geschäften nach und waren auf dem Weg zur Area 52 im Nethersturm, als sie uns einfach abschossen! Ich habe es gewusst! Niemals hätten wir dies', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9784', 'deDE', 'Der Expeditionsposten der Allianz liegt der Legionsfront am nächsten. Nördlich von hier gibt es nichts anderes als Dämonen über Dämonen. Reist am besten mit einem Freund.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9787', 'deDE', 'Die Illidaridämonen halten mich hier wegen meiner Kenntnisse über altertümliche Draeneikristalle und -relikte gefangen.$B$BViele meines Volkes wurden von ihnen versklavt, um nach einem mächtigen Krist', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9788', 'deDE', 'Warum sollte ich? Wenn mich Illidans Dämonen nicht wieder einfangen und erneut versklaven, werden es die Naga der Zangarmarschen tun.$B$BSich Illidan und seinen Verbündeten anzuschließen hat den meist', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9789', 'deDE', 'Ich schwöre, dass ich nie wieder eine Abkürzung nehmen werde! Wie hätte ich denn auch wissen können, dass der Himmel über der Höllenfeuerhalbinsel dermaßen gefährlich ist?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9800', 'deDE', '<Ein beruhigendes Licht erfüllt Euch, als Ihr auf den Naaru zugeht. Sanfte musikalische Klänge schwingen in Eurem Geist, und obwohl kein Wort gesprochen wird, fühlt Ihr Euch sicher. Seid herzlich will', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9801', 'deDE', 'Verschwindet, wenn Ihr nichts mit der Luftflotte zu besprechen habt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9805', 'deDE', 'Ne wahre Schönheit, was? Hori\'zee... der treue Greif unseres Kommandanten. Kurdran selbst hält seine Audienzen in der Festung. Ich kümmer\' mich so lange um unsere Hori\'zee hier. Ist \'n Prachtexemplar,', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9826', 'deDE', 'Der Nethersturm war nicht immer so wie jetzt.$B$BEinst war dieses zerbröckelnde Land als die Weiten von Farahlon bekannt. Nur wenig von damals hat das Böse, das sich hier niedergelassen hat, überlebt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9827', 'deDE', '', '$C, stoppt Euren Angriff! Das ist nur eine Verkleidung. Ich bin ein Spion, der außerhalb von Shattrath für das Bündnis des unteren Viertels arbeitet!$B$BIch hoffe, Ihr kommt, um zu helfen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9828', 'deDE', 'Erzmagier Vargoth hat mir seinen Stab anvertraut, als er mir befahl, vor dem Angriff der Blutelfen auf den Violetten Turm zu flüchten. Ich habe getan, was er mir befohlen hat, und gehofft, dass ich in', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9832', 'deDE', 'Nun sieh mal einer an, was uns die Netherwinde hier hereingeweht haben. Kommt \'rein, setzt Euch und nehmt Euch \'nen Zauber. Unsere Preise sollten Euren Geldbeutel nicht allzu sehr strapazieren.', 'Nun sieh mal einer an, was uns die Netherwinde hier hereingeweht haben. Kommt \'rein, setzt Euch und nehmt Euch \'nen Zauber. Unsere Preise sollten Euren Geldbeutel nicht allzu sehr strapazieren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9833', 'deDE', 'Geben sie denn niemals Ruhe?!$B$BWenn wir die Zukunft des unteren Viertels sichern wollen, müssen wir uns um den Schattenrat kümmern, bevor sie noch eine größere Katastrophe verursachen als die Knoche', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9834', 'deDE', 'Vor langer Zeit, in einer für den sterblichen Verstand unvorstellbaren Dimension des Kosmos, wurde ein Wesen von so unvorstellbarer Macht geboren (wenn man diesen Begriff überhaupt verwenden kann), de', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9835', 'deDE', 'Es existierte nur für das Chaos, und das leiseste Flüstern von ihm bedeutete die Zerstörung von Welten!$B$BTrotzdem gab es solche, die dieses intelligenzlose Wesen verehrten. Es gab sogar Verrückte, d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9836', 'deDE', 'Die modrigen Seiten des Kodex scheinen sich auf magische Weise von alleine umzublättern und lenken Eure Aufmerksamkeit auf ein bestimmtes Kapitel, vorbei an Schrecken, die nie ein sterbliches Auge erb', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9837', 'deDE', 'Oh, wie die Mächtigen zitternd zu seinen Füßen lagen und fürchteten, es würde zu ihnen sprechen.$B$BUnd dennoch wagten diese Scharlatane, diese verblendeten Anbeter einer nicht existierenden Religion,', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9838', 'deDE', 'Als Sterblicher mit unübertrefflichem arkanen und dunklen Wissen entwarf er eine Methode, mit der er seine Beschwörungen durch die Gefangennahme und den unsäglichen Missbrauch von Seelen verbessern ko', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9839', 'deDE', 'Und sie haben Murmur gerufen.$B$BMächtige Magier der Beherrschung und Unterdrückung wurden dafür angestellt und durch den ständigen Nachschub an Seelen genährt. Und doch konnten sie das Biest nicht ko', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9840', 'deDE', 'Nur dieses Buch überlebte die Katastrophe, die ihre Welt zerstörte. Diese stolzen und dummen Menschen, die dachten, ein kosmisches Wesen von unglaublicher Macht zu kontrollieren.$B$BWürdet Ihr als Bes', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9841', 'deDE', 'He da! Schön ein neues Gesicht zu sehen. Ich bekomme nur wenig Besuch und die Techniker sind nicht gerade gesprächig.$B$BIch wette, Ihr seid auf dem Weg nach Area 52. Habe ich Recht?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9844', 'deDE', 'Raketenchef Doppeldecker, Oberster von B.U.M.M - der Beratergruppe für ungeheure Mondmissionen - stehts zu Diensten.$B$BSind alle Systeme bereit?! Sieht so aus, als gäbe es an der X.52 Netherrakete no', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9845', 'deDE', '', 'Willkommen in Allerias Feste, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9846', 'deDE', '', 'Das ist Feinarbeit hier, versteht Ihr? Das kann man nicht überstürzen, sonst geht etwas kaputt.$B$BDoppeldeckers Kopf, zum Beispiel.$B$BIhr wundert euch vielleicht über meine Bots? Ich arbeite regelmä', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9847', 'deDE', 'Seid vorsichtig in den Ruinen, $C. Die Geister der Kirin Tor, auf die Ihr dort treffen werdet, betrachten Euch nicht als Freund.$B$BDer Hüter und ich sind die Einzigen, deren Geister stark genug an di', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9848', 'deDE', 'Kirin\'Var war 20 Jahre lang unzählbaren Angriffen der Orcs ausgesetzt, aber Kael\'thas\' Streitkräften konnte es nicht standhalten. Ein Mann, der sich einst ein Mitglied der Kirin Tor nannte, hat uns al', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9853', 'deDE', 'Ihr lesen Grek Steckbrief vor?$B$BGrek will helfen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9854', 'deDE', '', 'Hallo. Dies ist ein Waisenhaus für die Kinder der Scherbenwelt, welche Ihre Eltern verloren haben oder niemanden haben, der für sie da ist.$B$BVielleicht seid Ihr ja gekommen, um eines von ihnen zu ad', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9863', 'deDE', 'Was möchtet Ihr? Schnappt Euch einen Stuhl und kippt einen oder zwei.$B$BIch bin Stiefels, leitender Mixologe von B.U.M.M. und außergewöhnlicher Schankkellner!$B$BWenn es kickt, kann der alte Stiefels', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9868', 'deDE', '', 'Seid Ihr nicht der Korrespondent von der Gadgetzan Gazette? Wenn Ihr mir einfach folgen würdet, ich werde Euch meinen experimentellen Apparat vorführen....$B$B<Ihr erklärt der Magierin, dass Ihr nicht', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9869', 'deDE', 'Strahlung?!$B$BNein, darüber müsst Ihr Euch keine Sorgen machen! Pfff, was die Leute heutzutage alles glauben.$B$BWas haltet Ihr von unserer Operation hier unten? Topmodern, Schätzchen! Das könnt Ihr ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9871', 'deDE', 'Jahrelang war ich davon überzeugt, dass wir in Silbermond das Richtige tun. Wir haben hart gearbeitet, damit wir in die Scherbenwelt gelangen und mit Kael\'thas vereint sein können.$B$BHätte ich gewuss', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9872', 'deDE', 'Zwei Zettel auf dem Brett fallen Euch ins Auge. Einer stammt von Papa Mauschel, der andere von Raketenchef Doppeldecker.$B$BWie es scheint, ist Papa auf der Suche nach einem Maschinenteil. Der Raketen', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9875', 'deDE', 'Schredder?! Die schneiden einfach nicht mehr. Sie sind so was von gestern!$B$BWenn wir Raketen bauen und die X-52 zum Abheben bringen wollen, brauchen wir bessere Technologie!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9876', 'deDE', 'Ich bin Khadgar, ehemaliger Lehrling des gefallenen Wächters, Medivh. Ich bin sicher, Ihr habt seinen Namen bereits gehört.$B$BIch war es, der entdeckte, dass Medivh von Sargeras besessen wurde, und d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9877', 'deDE', 'Ich kämpfte an der Seite von Lord Anduin Lothar, als wir die Horde aus unseren Ländern auf Azeroth vertrieben. Als Erbe des Vermächtnisses Medivhs sah ich das als meine Pflicht an.$B$BAls dazu aufgeru', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9878', 'deDE', 'Meine Kameraden und ich nannten uns die Söhne Lothars, zu Ehren des größten, selbstlosesten Helden aller Zeiten. Wir gaben alles, um Ner\'zhul zu besiegen und seinem Wahnsinn ein Ende zu bereiten.$B$BA', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9879', 'deDE', 'Gefangen in dieser zersplitterten Einöde, taten wir unser Bestes, um zu überleben. Mit der Zeit spürten wir eine neue böse Macht, die sich uns vom wirbelnden Nether aus näherte. Schlimmer als die dunk', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9880', 'deDE', 'Während meine Kameraden die Stellung gegen die sich nähernde Dunkelheit hielten, benutzte ich Magie, um in das Große Dunkle Jenseits zu langen. Dort spürte ich Wesen aus unglaublichem Licht, von große', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9881', 'deDE', '<Khadgar lächelt.>$B$BIch überzeugte A\'dal und seine Geschwister, mit mir hier her zurückzukehren. Ich glaube, nur mit ihrer Macht können wir die Legion aus diesem Universum zurückdrängen. Aber wie Ih', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9886', 'deDE', 'Was wollt Ihr, $R?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9894', 'deDE', 'Was ist ein Ologe? Oder besser, wer ist kein Ologe? Es bedeutet, dass ich alles lerne! In der Tat bin ich in allen Dingen so bewandert, dass ich mit Sicherheit der intelligenteste Astrale bin, den Ihr', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9895', 'deDE', 'Was meint Ihr damit, wo sind die anderen drei? Habt Ihr ein Problem?$B$BVerschwindet aus meinen Augen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9896', 'deDE', 'Ah, wie ich sehe, seid Ihr ein Freund des Konsortiums. Vielleicht seid Ihr an einem kleinen Kauf interessiert?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9897', 'deDE', 'Dieser Shaffar ist ein kluger Bursche. Er hat den Eingang mit irgendeiner Art von Schutzzauber versehen, um andere Astralgruppen am Eindringen zu hindern. Wir arbeiten daran, seine Verteidigungsmaßnah', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9899', 'deDE', 'Auf der Bedienungskonsole blinken einige Lichter auf und verschiedene Mechanismen setzen sich in Bewegung.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9901', 'deDE', '<Der Kopf des Bilds wird unscharf, als er sich dreht, um die Umgebung zu erkunden.>$B$BWenn Ihr die Weiten von Farahlon gesehen hättet, würdet Ihr nicht glauben, dass es sich um denselben Ort handelt.', '', 'Das Land befindet sich ein einem schlimmeren Zustand, als ich befürchtet habe. Wenn der Schaden so weitergeht, wird Farahlon -äh, der Nethersturm sehr bald nichts weiter als kosmischer Kies sein.$B$BI', '', '<Das Abbild des Erzmagiers scheint eher durch Euch hindurch, als Euch anzuschauen.>$B$BWogen und Ströme arkaner Energie steigen und fallen, als wäre das Gebiet einem magischen Gezeitenstrom ausgesetzt', '', 'Ich werde mich an Kael\'thas für seine Vergehen an den Kirin Tor rächen! Einst wandelte er als Bruder unter uns, doch wir sind keine Brüder mehr!$B$BDer Tag der Abrechnung wird kommen.$B$B<Der Erzmagie', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9902', 'deDE', 'Mit der Kugel hinter mir konnte ich einst einen Blick darauf werfen, was in Azeroth vor sich geht.$B$BKael\'thas Fluch hat das alles natürlich verändert, aber in den Jahren meiner Isolation haben viele', '', '<Erzmagier Vargoth schenkt Euch ein schiefes Lächeln.>$B$BIch habe viel Platz hier im verfluchten Violetten Turm.$B$BIhr könnt mich das ganze Jahr über hier finden.', '', 'In der Nacht, als Kael\'thas das Dorf dem Erdboden gleichgemacht hat, konnte ich nichts tun, um zu helfen. Seine Magie hatte den Turm schon verschlossen und mein Leid noch vergrößert, indem ich zusehen', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9903', 'deDE', 'Wenn Ihr hier seid, um mich zu töten, nehmt Euch eine Nummer und stellt Euch hinten an.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9904', 'deDE', 'Still! Ich möchte nicht, dass mich hier jemand sieht.$B$BOh, das regt mich wirklich auf. Für wen halten sich diese Astralen eigentlich?$B$BDas ist MEIN Gebiet!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9905', 'deDE', '', 'Bitte lasst mich mit meiner Traurigkeit alleine.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9914', 'deDE', 'Das Kind macht mir Gänsehaut. Er steht den ganzen Tag da und redet mit seinem Großvater. Dann dreht er sich um und erzählt seiner Mutter, was der Großvater ihm gesagt hat. Und sie lächelt und nickt na', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9916', 'deDE', 'Als Raketenchef Doppeldecker nach Freiwilligen für den Bau der X-52 Netherrakete gesucht hat, habe ich mich als Wachmann beworben.$B$BUnglücklicherweise war nun noch die Stelle als Chefingenieur übrig', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9917', 'deDE', 'Willkommen, Freund des freien Volkes aus dem unteren Viertel, aber verweilt nicht lange.$B$BVom Inneren erklingen Kampfgeräusche. Meine Gefährten könnten Eure Hilfe brauchen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9918', 'deDE', 'Wenn Ihr ein Freund des unteren Viertels werdet, vielleicht können wir dann ein paar Geschäfte machen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9919', 'deDE', '', 'Könnt Ihr diese astrale Technologie fassen?!$B$BIch bin so froh, dass sie mich mit ihr hier draußen gelassen haben. Ich werde viel lernen... und vielleicht ein wenig davon für mich abzweigen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9920', 'deDE', 'Hau ab, Kind. Ich habe wichtige Geschäfte...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9921', 'deDE', 'Man sagt, dass diejenigen, die von den Geistern Auchindouns gesegnet sind, die Fähigkeit besitzen, die mächtigen Seelen in Form von Geistsplittern zu sammeln. Ich habe auf meinen Reisen ein paar diese', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9922', 'deDE', 'Das Gebilde aus Kristall summt vor Energie. Merkwürdige Glyphen werden vor Euch projiziert und laufen schnell vor Euren Augen ab.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9923', 'deDE', 'Beschäftigt, beschäftigt! So viel zu tun und so wenig Zeit!$B$BWo habe ich nur diesen Pyroschlüssel hingelegt? Ich weiß, dass er hier irgendwo sein muss... Ich habe mir noch vor einer Minute die Finge', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9925', 'deDE', 'Ein wenig Magie zu beherrschen ist bei dieser Arbeit ganz nützlich. Illusionen können nicht jeden täuschen, aber meistens reichen sie aus, um die Aufgabe zu erledigen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9926', 'deDE', 'Was macht Ihr hier? Solltet Ihr nicht an der Energieleitung arbeiten?$B$BWir haben die Energielieferung an die Festung der Stürme fast verdoppelt, und wir können hier keine Faulenzer gebrauchen. Schli', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9927', 'deDE', 'Manaschmiede Ultris, natürlich! Wo seid Ihr gewesen?$B$BDie Manaschmiede wurde überladen und es gab eine mächtige Explosion. Sie hinterließ einen Riss in der Realität, durch den nun Leerkreaturen eind', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9929', 'deDE', 'Ihr solltet Euch hier sicher fühlen. Nach den Angriffen auf die Manaschmiede Duro haben wir die Sicherheitsvorkehrungen hier verschärft.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9930', 'deDE', 'Die Manaschmiede Duro liegt am nahesten an der Festung der Stürme, daher muss sie auch die meiste Arbeit verrichten.$B$BWie Ihr sicher wisst, verursacht eine Überlastung dieser Manaschmieden Lecks in ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9931', 'deDE', '', 'Mit dieser Technologie zu arbeiten ist nervenaufreibend. Alles ist auf so eine fremdartige Weise konstruiert.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9932', 'deDE', 'Vor vielen Jahren folgten meine Männer und ich dem Magier Khadgar und dem Paladin Turalyon auf diese Welt. Unser einziges Ziel war die Auslöschung der Orcs und ihrer Verbündeten. Doch in den letzten S', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9933', 'deDE', 'Wir, die Söhne Lothars, harren seitdem hier aus - kämpfend... sterbend... betend, dass ein Tag wie heute kommen möge, an dem sich das Dunkle Portal erneut öffnet und unsere Brüder kommen, um uns endli', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9934', 'deDE', '', 'Bringen sie euch neuen Rekruten denn gar nichts bei? Die Manaschmieden wurden aus Teilen der Festung der Stürme gebaut, um Energie aus dem Nether abzusaugen.$B$BDie Naaru benutzten diese Technologie, ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9935', 'deDE', 'Oh, $Gein weiterer Soldat:eine weitere Soldatin; auf dem Weg zur Ehrenfeste... bei Kurdrans Bart, sie können dort jede Hilfe brauchen!', 'Oh, $Gein weiterer Soldat:eine weitere Soldatin; auf dem Weg zur Ehrenfeste... bei Kurdrans Bart, sie können dort jede Hilfe brauchen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9936', 'deDE', 'Es ist meine Leidenschaft, versteht Ihr? Rüstungsschmieden ist meine liebste Beschäftigung. Bitte, behandelt mich, als wäre ich ein lebendiger Draenei, Fremder. Lasst mich wieder wissen, wie es ist zu', 'Es ist meine Leidenschaft, versteht Ihr? Rüstungsschmieden ist meine liebste Beschäftigung. Bitte, behandelt mich, als wäre ich ein lebendiger Draenei, Fremder. Lasst mich wieder wissen, wie es ist zu', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9937', 'deDE', 'Das Konsortium wird sich an den Verrätern der Zaxxis rächen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9938', 'deDE', 'Ah, $Gein neuer Held:eine neue Heldin;, $Gder:die; für unsere Sache kämpfen möchte? Was sagt Ihr, $N?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9939', 'deDE', '$N, zwei Jahrzehnte lang haben die Söhne Lothars auf der Höllenfeuerhalbinsel gekämpft. Von allen Seiten wurden wir durch die einheimischen Orcs und finsteren Dämonen des Brennenden Kreuzzuges belager', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9941', 'deDE', 'Orcs. Mir scheint, ich hätte mein ganzes Leben nichts anderes getan, als gegen sie zu kämpfen.$B$BVor zwanzig Jahren zerschlugen wir die Horde und rissen die Mauern ihrer Höllenfeuerzitadelle ein. Wir', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9942', 'deDE', 'Vor ungefähr zwanzig Jahren schickte die Allianz eine erste Expedition auf diese Welt, in der Hoffnung, die Orcs für immer auszulöschen. Es war ein... schrecklicher Konflikt. Zahlreiche mutige Seelen ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9943', 'deDE', 'Vor ungefähr zwanzig Jahren schickte die Allianz eine erste Expedition auf diese Welt, in der Hoffnung, die Orcs für immer auszulöschen. Es war ein... schrecklicher Konflikt. Zahlreiche mutige Seelen ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9944', 'deDE', 'Die Männer und Frauen der Ehrenfeste haben so viel Blut vergossen und ihre Jugend in diesem monströsen Land verloren... und dennoch geben sie nicht auf!$B$BJetzt, da Ihr und die Heerscharen von Azerot', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9947', 'deDE', 'Als Vertreter des Konsortiums gebe ich immer auf die Auswirkungen, die mein Handeln auf unsere Geschäfte hat, Acht.$B$BUnglücklicherweise behandeln die Blutelfen ihre Geschäftspartner nicht mit solche', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9948', 'deDE', 'Mein jüngerer Bruder hier ist der aufsteigende Stern des Konsortiums. Natürlich ist er zu bescheiden, um das zuzugeben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9949', 'deDE', 'Wenn er \'jüngerer Bruder\' sagt, dann vergisst er zu erwähnen, dass wir Zwillinge sind. Er ist tatsächlich älter, aber nur ein paar Minuten.$B$BLasst Euch nicht von diesem \'Aufsteigender Stern\'-Gerede ', 'Wenn er \'jüngerer Bruder\' sagt, dann vergisst er zu erwähnen, dass wir Zwillinge sind. Er ist tatsächlich älter, aber nur ein paar Minuten.$B$BLasst Euch nicht von diesem \'Aufsteigender Stern\'-Gerede ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9951', 'deDE', 'Seid gegrüßt, $R. Was ich hier mache, ist eine andere Geschichte, die in eine andere Zeit gehört. Oh, der war schlecht.$B$BMein Bruder und ich sind hier zwischen unserer Zeit und der Gegenwart gefange', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9953', 'deDE', 'Ihr hört schon richtig. Ich habe tatsächlich die Stimme einer jungen Orcfrau. Aber hört zu, $GJunge:Mädel;, Zeitreisen können merkwürdige Dinge mit einem Körper anstellen.$B$BEs sind übrigens 250 Jahr', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9954', 'deDE', 'Ja, ich sitze hier schon seit ich denken kann fest.$B$BIn meinem früheren Leben war ich Rüstungsschmied. Vielleicht kann ich etwas für Euch reparieren?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9955', 'deDE', 'Endlich kommt jemand herunter, um mit mir zu reden! Möchtet Ihr ein paar meiner Reime hören?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9956', 'deDE', 'Ich geh die Straße runter! Schau auf die Füße munter! Was ist das für ein Wetter? Es regnet bunte Blätter!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9958', 'deDE', 'Diese Astralteleportplattform ist mit leuchtend bunten Knöpfen und Schaltern versehen, aber sie scheinen nicht benutzt zu werden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9959', 'deDE', 'Alles steht zum Verkauf, mein Freund! Groß und klein, was immer Ihr braucht, Hazzin hat es.$B$BWenn Euch Euer Leben lieb ist, geht nicht ohne Ausrüstung zur Manaschmiede Ultris. Hazzin ist die letzte ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9960', 'deDE', 'Ich bin felsenfest davon überzeugt, dass wir das, was Nexusprinz Haramad begehrt, beschaffen können.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9962', 'deDE', 'Kokelwälder bietet wieder erlesene Waren in Orgrimmar und Eisenschmiede an. Ich frage mich, welch Wunder sie sich dieses Mal ausgedacht haben?', 'Kokelwälder bietet wieder erlesene Waren in Orgrimmar und Eisenschmiede an. Ich frage mich, welch Wunder sie sich dieses Mal ausgedacht haben?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9964', 'deDE', 'Die Druiden halten auf der Mondlichtung große Feierlichkeiten zu Ehren des Mondfests ab. Wenn Euch der weg bis dorthin zu weit ist, besucht eine der großen Städte. Dort findet Ihr immer jemanden, der ', 'Die Druiden halten auf der Mondlichtung große Feierlichkeiten zu Ehren des Mondfests ab. Wenn Euch der weg bis dorthin zu weit ist, besucht eine der großen Städte. Dort findet Ihr immer jemanden, der ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9968', 'deDE', '', 'Ich habe noch nie zuvor etwas wie diese Netherdrachen gesehen. Sie scheinen das Temperament ihres Vorfahren zu haben, sind aber doch völlig anders.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9969', 'deDE', 'Der Boden hier ist noch warm von frisch vergossenem Blut. Ah, ich hätte mein eigenes Auge gegeben, um hier zu sein, als die Dämonen durch das Portal kamen und auf Azeroths Helden trafen! Was für eine ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9970', 'deDE', 'Wenn Ihr den ganzen Weg hierher gekommen seid, dann wollt Ihr Euch sicher in die Gegend der Manaschmiede Ara wagen.$B$BWollt Ihr vorher noch etwas kaufen oder verkaufen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9971', 'deDE', 'Es gibt nur 3 Arten von Leuten auf dieser Welt: Konkurrenten, Kunden und Angestellte. Konkurrenten müssen rücksichtslos vernichtet, Kunden verhätschelt und Angestellte streng überwacht werden.$B$BUnab', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9977', 'deDE', 'Vorsichtig, $GSterblicher:Sterbliche;, hinter dieser Halle lauern weitaus schlimmere Gefahren, als man sie von Eurer Welt kennt. Seid wachsam...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9978', 'deDE', 'Schön, Euch wiederzusehen, $N. Möchtet Ihr, dass ich Euch zum Hort des Meisters bringe?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9979', 'deDE', 'Das Arathibecken wurde von den Entweihten überrannt! Werdet Ihr uns helfen, $N?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9980', 'deDE', 'Es ist schön, einen Freund der Aldor in diesen abgelegenen Teilen der Welt zu sehen. Ich fürchte, ich kann dieses anmaßende Geplapper der Elfen nicht länger ertragen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9981', 'deDE', '', 'Geht, $N, in das Arathibecken! Tötet, TÖTET wie ihr es niemals zuvor getan habt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9982', 'deDE', 'Der Eingang in die Kriegshymnenschlucht liegt vor Euch. Werdet Ihr dort gegen die elendige Allianz kämpfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9983', 'deDE', '', 'Grüße, $C. Gerade jetzt greift die Horde die Kriegshymnenschlucht an. Seid Ihr bereit, auf der Seite der Allianz zu kämpfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9985', 'deDE', '', 'Ebermist! Warum muss ich hier rumstehen und anderen den Weg ins Alteractal zeigen, wenn ich dort selbst kämpfen könnte? Ihr! Seid Ihr gekommen, um gegen die Allianz zu kämpfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9986', 'deDE', 'Der Wald singt von der keimenden Veränderung. Unsere neuen Freunde, die Draenei, sind hier. Wir werden ihnen helfen, ihr eigenes Zuhause zu errichten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9988', 'deDE', 'Was wollt Ihr? Ich habe keine Zeit zum Quatschen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9989', 'deDE', '', 'Ich bin gerade damit beschäftigt, ein Bestechungsgeld zu verhandeln. Kommt später wieder.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9990', 'deDE', '', 'Ich habe Euch einen Flug zur Manaschmiede Coruu besorgt. Seid Ihr bereit, $N? Ihr werdet hinter den feindlichen Linien abgesetzt. Nehmt also besser alles mit, was Ihr braucht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9991', 'deDE', 'Oh, $Gein weiterer Soldat:eine weitere Soldatin; auf dem Weg zur Ehrenfeste... bei Kurdrans Bart, sie können dort jede Hilfe brauchen!', 'Oh, $Gein weiterer Soldat:eine weitere Soldatin; auf dem Weg zur Ehrenfeste... bei Kurdrans Bart, sie können dort jede Hilfe brauchen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9993', 'deDE', 'Willkommen auf der Sturmsäule, $C.$B$BBitte, lasst Euch von meiner Erscheinung durch dieses Bildgerät nicht abschrecken. Ich habe viele Feinde, die mich gerne tot sehen würden. Daher diese Sicherheits', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9994', 'deDE', 'Möchtet Ihr etwas kaufen? Möchtet Ihr etwas verkaufen?$B$BDafür sind wir hier, mein Freund.$B$BEure Vorlieben, nennt mir Eure Vorlieben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9995', 'deDE', 'So, Ihr seid also erst kürzlich aus Azeroth hier eingetroffen, mh? Macht Euch auf einiges gefasst, $C - In diesem Land lauern weit schlimmere Gefahren, als alles, dem sich die Horde je stellen musste.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9996', 'deDE', 'Throm\'ka, $GBruder:Schwester;! Willkommen in der Scherbenwelt. Die Horde kann auf dieser zerrissenen Welt jede Hilfe gebrauchen. Ich verspreche Euch, wenn Ihr auf der Suche nach glorreichen Schlachten', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9997', 'deDE', 'Fasst Euch kurz, $C! Wir versuchen hier einen Krieg zu gewinnen, falls Ihr es noch nicht mitbekommen habt!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10000', 'deDE', 'Seid gegrüßt, Abenteurer. Erhört meine Worte, bevor Ihr Euch in den schwarzen Morast begebt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10001', 'deDE', 'Ah, willkommen zurück, Held. Ihr seid also zurückgekehrt, um den Wächter erneut zu schützen? Vergesst nicht, ein Chronosignal mitzunehmen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10002', 'deDE', 'Seid gegrüßt, $N. Fahrt mit der gebotenen Vorsicht fort...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10003', 'deDE', 'Kaum hatte unser Kriegshäuptling von der erneuten Öffnung des Portals erfahren, ließ er seine Berater zusammenkommen, um diese wagemutige Expedition vorzubereiten. Den Zweifeln einiger Berater zum Tro', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10004', 'deDE', 'Die Horde hat viel riskiert, um die Scherbenwelt zu erforschen: Ressourcen, Krieger und Arbeitskräfte. Sogar die Verteidigung unserer Heimat wurde zu Gunsten dieser Unternehmung vernachlässigt. Thrall', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10005', 'deDE', 'Throm\'ka, $C. Ihr seid neu hier. Ihr seht noch frisch und unberührt von den Schrecken dieser Welt aus.$B$B<Unteroffizier Schädelbrecher spuckt auf den staubigen Grund.>$B$BDas wird sich bald ändern.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10006', 'deDE', 'Unsere Expedition hat es sicher durch das Portal geschafft und einen neuen Stützpunkt namens Thrallmar errichtet. Wie Ihr sicherlich schon gesehen habt, ist die Brennende Legion aufmarschiert und hat ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10008', 'deDE', 'Ich glaube, dass Taerix mit etwas Hilfe schon bald der Durchbruch gelingen dürfte.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10009', 'deDE', 'Meine Brüder in Sturmwind haben mir gesagt, ich sollte nicht in die Armee eintreten... ich bin froh, dass ich nicht auf sie gehört habe!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10010', 'deDE', 'Der Trümmerposten ist ein großartiger Ort für einen Wildhammerzwerg. Ich hatte noch nie so viel Spaß in der Luft!', 'Der Trümmerposten ist ein großartiger Ort für einen Wildhammerzwerg. Ich hatte noch nie so viel Spaß in der Luft!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10011', 'deDE', '', 'Falkenschreiter sind edle Geschöpfe. Behandelt sie immer gut, denn es gibt nichts Gefährlicheres als einen wütenden Falkenschreiter.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10012', 'deDE', '', 'Ich bin mir sicher, dass Botanikerin Taerix mit Eurer Hilfe eine Lösung für unsere Probleme finden wird.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10019', 'deDE', 'Habt Ihr Lust, mir dabei zu helfen, das Warptor auszuschalten? Es befindet sich im Lager der Brennenden Legion dort im Süden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10022', 'deDE', '', 'Diese Leute sind vor ein paar Nächten aus Shattrath gekommen und haben seitdem nicht aufgehört zu streiten.$B$BIch bin froh, dass das Euch nicht verscheucht hat. Ich hatte schon Angst, dass sie mir mö', '', 'Ich hoffe, die Leute aus Shattrath hören bald auf zu kämpfen. Sie gehen mir langsam auf die Nerven.$B$BWie kann ich Euch helfen?', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10024', 'deDE', 'Ihr seid nicht stark genug, um im Auge des Sturms zu kämpfen. Kehrt zurück, wenn Ihr mehr Erfahrung gesammelt habt.', 'Ihr seid nicht stark genug, um im Auge des Sturms zu kämpfen. Kehrt zurück, wenn Ihr mehr Erfahrung gesammelt habt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10025', 'deDE', 'Draeneistreitkräfte und ihre Allianzverbündeten kämpfen im Auge des Sturms auf der Scherbenwelt gegen die Horde. Wollt Ihr Euch ihnen anschließen, um das Schlachtenglück zu unseren Gunsten zu wenden?', 'Draeneistreitkräfte und ihre Allianzverbündeten kämpfen im Auge des Sturms auf der Scherbenwelt gegen die Horde. Wollt Ihr Euch ihnen anschließen, um das Schlachtenglück zu unseren Gunsten zu wenden?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10026', 'deDE', 'Die Horde kann den Draenei und der sich einmischenden Allianz nicht erlauben, alle Manazellen der Scherbenwelt zu kontrollieren. Werdet Ihr Euch den Blutelfen in ihrem Angriff auf das Auge des Sturms ', 'Die Horde kann den Draenei und der sich einmischenden Allianz nicht erlauben, alle Manazellen der Scherbenwelt zu kontrollieren. Werdet Ihr Euch den Blutelfen in ihrem Angriff auf das Auge des Sturms ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10027', 'deDE', 'Hallo, $C! Seid Ihr hier, um Euch für einen Flugeinsatz zu melden?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10031', 'deDE', 'Wenn Ihr in den Zangarmarschen wart, habt Ihr vielleicht einen Kerl namens Daniels getroffen. Ihr wisst schon - trägt so einen komischen Helm und hört nur auf \"der Spezialist\".$B$BNatürlich habe ich i', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10032', 'deDE', 'Tag für Tag arbeiten wir in dieser Mine. Blutelfenmänner sind für solche körperliche Arbeit nicht geschaffen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10039', 'deDE', '$N, denkt bitte daran, Gahruj von unserem Erfolg zu berichten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10040', 'deDE', '', 'Abgesandter Taluun hat mich mitgenommen, um unseren neuen Verbündeten über die Elemente dieser Welt zu berichten... aber niemand will zuhören.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10041', 'deDE', '', 'Lasst mich allein, $C. Ich muss meinen Aufgaben für Botschafter Sonnenleid nachgehen, so langweilig sie auch sein mögen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10042', 'deDE', '', 'Auch wenn die Sha\'tar sich auf das Schattenmondtal und Illidan konzentrieren, dürfen wir die Bedrohung, die von Kael\'thas Sonnenwanderer ausgeht, nicht außer Acht lassen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10043', 'deDE', '', 'Abgesandter Taluun hat mich mitgenommen, um unseren neuen Verbündeten über die Elemente dieser Welt zu berichten. Bisher wollte keiner zuhören, aber vielleicht habt Ihr Interesse?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10044', 'deDE', '', 'Mich langweilt es, Botschafter Sonnenleid zu bewachen. Vielleicht können wir ein wenig trainieren? Das könnte eine lehrreiche Erfahrung sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10045', 'deDE', 'Als Ihr Euch der Konsole nähert, beginnt der Zugangskristall, den Ihr bei Euch tragt, sanft zu summen und pulsierend zu leuchten.$B$BEine Flut von Informationen überkommt Euren Geist. Ihr seid Euch de', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10047', 'deDE', 'Finger weg von den Umhängen! Ja, ja, sicherlich möchtet Ihr auch einen haben. Jeder möchte einen meiner Umhänge sein Eigen nennen. Glaubt mir, Menschen von niederer Moral und Stand, eines Tages werde ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10051', 'deDE', 'Die Aufgabe des Lichtwächters besteht aus zwei Teilen. Die erste und vorrangigste ist, die Priesterschaft mit allen Mitteln zu schützen. Und die zweite ist, die Feinde des Lichts zu zerstören, wo auch', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10052', 'deDE', 'Beim Barte Muradins, $C. Dieses Land ist beinahe vollständig zerstört. Ich kann mir kaum vorstellen, dass hier jemand seit dem Ende des Zweiten Krieges überlebt hat.', 'Beim Barte Muradins, $C. Dieses Land ist beinahe vollständig zerstört. Ich kann mir kaum vorstellen, dass hier jemand seit dem Ende des Zweiten Krieges überlebt hat.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10053', 'deDE', 'Throm\'ka, $C. Ist diese zerschlagene Welt das, was Ihr erwartet habt, als Ihr hierher kamt? Dieses Land hat nichts mit den Geschichten aus meiner Jugend gemein. Es ist nur schwer vorzustellen, wie mei', 'Throm\'ka, $C. Ist diese zerschlagene Welt das, was Ihr erwartet habt, als Ihr hierher kamt? Dieses Land hat nichts mit den Geschichten aus meiner Jugend gemein. Es ist nur schwer vorzustellen, wie mei', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10054', 'deDE', 'Ich bin auf der Suche nach jemandem, der mir bei einer Aufgabe hilft, die schon lange überfällig ist.$B$BIhr seht aus, als wärt Ihr dazu in der Lage.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10055', 'deDE', 'Die Arbeit, die Ihr für mich in den Ruinen von Farahlon geleistet habt, hat meinen Zeitplan wieder ins Reine gebracht. Ich werde mein Team zusammenstellen und sofort nach dort aufbrechen.$B$BDanke, $N', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10056', 'deDE', 'Die Legion ist überall... wir müssen handeln!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10057', 'deDE', 'Dank Euch können wir mit unseren Plänen in den Ruinen von Farahlon fortfahren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10058', 'deDE', 'Was wisst Ihr über die Astralen, Fleischling?$B$BIhr seht uns sicher alle im selben Licht und bemerkt den Streit und die Kämpfe zwischen den verschiedenen Fraktionen nicht.$B$BVielleicht ist es Zeit f', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10059', 'deDE', 'Das Astraleum war einst die führenden Schicht meiner Heimatwelt K\'aresh. Tragischerweise mussten wir K\'aresh verlassen, kurz bevor der Fürst der Leere, Dimensius, seine Leerarmeen auf die Welt los lie', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10060', 'deDE', 'Das Astraleum ist finster geworden... wahnsinnig. Rache ist ihr einziges Ziel. Alles, was ihnen im Weg steht oder sich ihnen nicht anschließen will, wird als Feind betrachtet. Viele sahen, dass das As', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10061', 'deDE', 'Wir spüren, dass vom Astraleum eine gleichgroße Gefahr für uns ausgeht, wie von Dimensius. Beide müssen aufgehalten werden...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10062', 'deDE', 'Hi. Ich bin Vixton Quetschpfeife, offizieller Verkäufer der Dampfdruckarena. Wenn Ihr den Ruf habt, habe ich die Waren, die Euch das Wasser im Mund zusammenlaufen lassen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10063', 'deDE', 'Wenn Ihr Euch unwohl fühlt, sprecht mit Pestle. Er könnte etwas gegen die Flugkrankheit haben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10064', 'deDE', '<Der Lehrling wirft gelegentlich einen Blick über die Schulter. Anscheinend reagiert er auf Geräusche, die Ihr nicht hören könnt.>$B$BGuten Tag, $N. Verzeiht, wenn ich etwas abgelenkt erscheine, aber ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10065', 'deDE', 'Agent Sechs-Uhr und mir wurde vom Protektorat die äußerst wichtige Aufgabe übertragen, alle Anzeichen von Leerkreaturen in Ultris auszurotten. Wir sollten dabei taktisch vorgehen. Unser letzter Anschl', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10066', 'deDE', 'Wenn das Licht Euch nicht führen kann, $Gmein Freund:meine Freundin;, vielleicht kann ich Euch behilflich sein. Sucht Ihr etwas?', 'Wenn das Licht Euch nicht führen kann, $Gmein Freund:meine Freundin;, vielleicht kann ich Euch behilflich sein. Sucht Ihr etwas?', 'Diese Insel ist manchmal seltsam, findet Ihr nicht auch? Kann ich Euch mit etwas behilflich sein?', 'Diese Insel ist manchmal seltsam, findet Ihr nicht auch? Kann ich Euch mit etwas behilflich sein?', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10067', 'deDE', 'Ihr wollt etwas lagern? Es gibt hier keinen sichereren Ort als die Exodar... die Bankiers dort werden Euch gern weiterhelfen.', 'Ihr wollt etwas lagern? Es gibt hier keinen sichereren Ort als die Exodar... die Bankiers dort werden Euch gern weiterhelfen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10069', 'deDE', '', 'Besucht Vixton Quetschpfeife in Area 52 im Nethersturm. Als offizieller Arenahändler des Kampfverbandes des Dampfdruckkartells bietet er viele unglaubliche Gegenstände zum Verkauf an, wenn Ihr genug A', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10071', 'deDE', 'Die Hippogryphen werden nahe der Exodar gut versorgt. Reist dort hin, wenn ihr zur Blutwacht fliegen wollt.', 'Die Hippogryphen werden nahe der Exodar gut versorgt. Reist dort hin, wenn ihr zur Blutwacht fliegen wollt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10072', 'deDE', 'Der einst unerschöpfliche Vorrat an Textilien der Exodar ist dramatisch gesunken. Euer Ruf bei den Draenei hier würde sich gewaltig verbessern, wenn Ihr uns etwas Stoff spenden würdet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10073', 'deDE', 'Funaam ist unser Gildenmeister. Er hat sein Büro innerhalb der Exodar eingerichtet. Folgt einfach der Straße, dann werdet Ihr bald dort sein.', 'Funaam ist unser Gildenmeister. Er hat sein Büro innerhalb der Exodar eingerichtet. Folgt einfach der Straße, dann werdet Ihr bald dort sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10074', 'deDE', 'Unser bescheidenes Gasthaus befindet sich im Norden der Azurwacht. Chellan wird sich dort gut um Euch kümmern.', 'Unser bescheidenes Gasthaus befindet sich im Norden der Azurwacht. Chellan wird sich dort gut um Euch kümmern.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10075', 'deDE', 'Esbina wird sich gern um Euer Tier kümmern, $Gmein Freund:meine Freundin;. Sie wartet außerhalb des Gasthauses innerhalb der Azurwacht.', 'Esbina wird sich gern um Euer Tier kümmern, $Gmein Freund:meine Freundin;. Sie wartet außerhalb des Gasthauses innerhalb der Azurwacht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10076', 'deDE', 'Ah, Ihr sucht Ausbildung? In der Azurwacht haben mehrere Lehrer ihr Lager aufgeschlagen... wonach sucht Ihr genau?', 'Ah, Ihr sucht Ausbildung? In der Azurwacht haben mehrere Lehrer ihr Lager aufgeschlagen... wonach sucht Ihr genau?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10078', 'deDE', 'Acteon ist der Fährtenleser vor Ort, und ein guter noch dazu... das Leder, welches er trägt, zeugt von seinen Fertigkeiten. Sein Lager befindet sich südöstlich der Azurwacht.', 'Acteon ist der Fährtenleser vor Ort, und ein guter noch dazu... das Leder, welches er trägt, zeugt von seinen Fertigkeiten. Sein Lager befindet sich südöstlich der Azurwacht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10079', 'deDE', 'Was? Ihr möchtet Silbermond Stoff spenden?$B$BIhr seid nur auf einen Falkenschreiter aus, oder? Gut, gut... tut der Stadt diesen Gefallen, und ich werde dafür sorgen, dass Euer Ruf hier sich verbesser', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10083', 'deDE', 'Die gute Tullas ist eine ehrenhafte Frau, und wackere Verteidigerin des Lichts. Ihren Unterricht gibt sie im Gasthaus.', 'Die gute Tullas ist eine ehrenhafte Frau, und wackere Verteidigerin des Lichts. Ihren Unterricht gibt sie im Gasthaus.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10087', 'deDE', 'Es gibt eine ganze Menge von gut ausgebildeten Handwerkern auf der Azurmythosinsel. Nach welchem sucht Ihr denn?', 'Es gibt eine ganze Menge von gut ausgebildeten Handwerkern auf der Azurmythosinsel. Nach welchem sucht Ihr denn?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10088', 'deDE', 'Daedal kennt sich beim Brauen von Tränken sehr gut aus. Er arbeitet eng mit Fateema im Westen der Azurwacht zusammen.', 'Daedal kennt sich beim Brauen von Tränken sehr gut aus. Er arbeitet eng mit Fateema im Westen der Azurwacht zusammen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10089', 'deDE', 'Wir haben hier in der Azurwacht keine Schmiede. Ich habe jedoch ein paar junge Schmiede aus diesem merkwürdigen Lager an der südlichen Küste kommen sehen. Vielleicht solltet Ihr dort nachschauen.', 'Wir haben hier in der Azurwacht keine Schmiede. Ich habe jedoch ein paar junge Schmiede aus diesem merkwürdigen Lager an der südlichen Küste kommen sehen. Vielleicht solltet Ihr dort nachschauen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10090', 'deDE', 'Wir können keine Köche entbehren, aber eines ist merkwürdig... vom Süden her riecht es sehr appetitlich. Folgt einfach Eurer Nase, ja?', 'Wir können keine Köche entbehren, aber eines ist merkwürdig... vom Süden her riecht es sehr appetitlich. Folgt einfach Eurer Nase, ja?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10092', 'deDE', 'Der hiesige Konstrukteur, Daelo, kann Euch in den Grundlagen der Ingenieurskunst unterweisen. Vielleicht könnt Ihr ihm ja helfen, diese defekten Kommunikationsgeräte zu reparieren!', 'Der hiesige Konstrukteur, Daelo, kann Euch in den Grundlagen der Ingenieurskunst unterweisen. Vielleicht könnt Ihr ihm ja helfen, diese defekten Kommunikationsgeräte zu reparieren!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10093', 'deDE', 'Fateema ist unser Anachoret. Bei der Versorgung der Absturzopfer war sie uns eine unschätzbare Hilfe. Sie übt ihr Handwerk auf der westlichen Seite der Wacht aus.', 'Fateema ist unser Anachoret. Bei der Versorgung der Absturzopfer war sie uns eine unschätzbare Hilfe. Sie übt ihr Handwerk auf der westlichen Seite der Wacht aus.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10094', 'deDE', 'Ein aufstrebender Angler... Diktynna am Ufer des Am\'menflusses versorgt uns alle hier mit ihrem Netz. Sie könnte sicher etwas Hilfe brauchen.', 'Ein aufstrebender Angler... Diktynna am Ufer des Am\'menflusses versorgt uns alle hier mit ihrem Netz. Sie könnte sicher etwas Hilfe brauchen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10095', 'deDE', 'Heur hat seit unserer Ankunft die Pflanzenwelt dieser Gegend erkundet. Er wird Euch etwas beibringen können. Ihr findet ihn bei der Arbeit zusammen mit Daedal am westlichen Ende der Azurwacht.', 'Heur hat seit unserer Ankunft die Pflanzenwelt dieser Gegend erkundet. Er wird Euch etwas beibringen können. Ihr findet ihn bei der Arbeit zusammen mit Daedal am westlichen Ende der Azurwacht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10096', 'deDE', 'Unsere neuen Verbündeten, die Furbolgs der Tannenruhfeste, haben ihr Interesse daran bekundet, uns alle Häute abzunehmen. Sie leben im Norden. Sucht bei ihnen nach Wissen.', 'Unsere neuen Verbündeten, die Furbolgs der Tannenruhfeste, haben ihr Interesse daran bekundet, uns alle Häute abzunehmen. Sie leben im Norden. Sucht bei ihnen nach Wissen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10097', 'deDE', 'Dulvi ist die Frau, nach der Ihr sucht. Sie bildet neue Talente in der Mitte der Wacht aus.', 'Dulvi ist die Frau, nach der Ihr sucht. Sie bildet neue Talente in der Mitte der Wacht aus.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10098', 'deDE', 'Reist zur Tannenruhfeste, mein Freund. Die Furbolgs dort waren sehr hilfreich. Vielleicht haben sie genau das Wissen, das Ihr braucht.', 'Reist zur Tannenruhfeste, mein Freund. Die Furbolgs dort waren sehr hilfreich. Vielleicht haben sie genau das Wissen, das Ihr braucht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10099', 'deDE', 'Wir haben hier in der Azurwacht keine Schneider. Ihr könnt es jedoch bei unserem Lager am Strand versuchen. Dort soll es eine Reihe von Handwerkern von jenseits des Meeres geben.', 'Wir haben hier in der Azurwacht keine Schneider. Ihr könnt es jedoch bei unserem Lager am Strand versuchen. Dort soll es eine Reihe von Handwerkern von jenseits des Meeres geben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10100', 'deDE', 'Sucht Ihr nach einem Edelsteinschneider? Unsere Juwelenschleifer befinden sich in der Exodar und bewirken wahre Wunder mit den Materialien, die sie gefunden haben. Sprecht mit Farii, wenn Ihr dort sei', 'Sucht Ihr nach einem Edelsteinschneider? Unsere Juwelenschleifer befinden sich in der Exodar und bewirken wahre Wunder mit den Materialien, die sie gefunden haben. Sprecht mit Farii, wenn Ihr dort sei', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10101', 'deDE', 'Was wollt Ihr?! Ich habe etwas von diesen merkwürdigen Draeneikreaturen im Tal der Helden gehört. Vielleicht weiß sie etwas darüber. Ich nicht.', 'Was wollt Ihr?! Ich habe etwas von diesen merkwürdigen Draeneikreaturen im Tal der Helden gehört. Vielleicht weiß sie etwas darüber. Ich nicht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10103', 'deDE', 'Der Geist des Wassers hat uns verlassen. Unser Stamm ist verloren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10104', 'deDE', 'Die Winde sprechen nicht mehr zu meinem Stamm, $N. Meine Leute sind ohne die Ältesten verloren. Nur sie können die Geister zu meinem Stamm zurückbringen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10105', 'deDE', 'Die Fesseln, welche mich gefangen halten, hindern mich daran, meinem Volk den Geist des Feuers zu bringen, $N. Seelen ohne Feuer sind zu schwach, um gegen Ihre Herren aufzubegehren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10106', 'deDE', 'Ich weiß nicht, was in Euch gefahren ist, einen Schamanen zu suchen... aber ich habe jemanden gesehen, der Euch weiterhelfen könnte. Am Ufer im Tal der Helden gibt es eine Frau, die sich Weissagerin n', 'Ich weiß nicht, was in Euch gefahren ist, einen Schamanen zu suchen... aber ich habe jemanden gesehen, der Euch weiterhelfen könnte. Am Ufer im Tal der Helden gibt es eine Frau, die sich Weissagerin n', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10107', 'deDE', 'Wir nicht mögen Paladine. Elfendame kommen in Stadt, bleiben in Königsviertel. Ihr reden, lernen Paladinsachen.', 'Wir nicht mögen Paladine. Elfendame kommen in Stadt, bleiben in Königsviertel. Ihr reden, lernen Paladinsachen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10109', 'deDE', '', 'Eure Hilfe war unbezahlbar, $Gmein Freund:meine Freundin;.$B$BIch kann Euch versichern, dass die Expedition des Cenarius alles in ihrer Macht stehende unternehmen wird, um zu verhindern, dass weitere ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10113', 'deDE', 'Dies ist der Turm von Karazhan, Residenz meines Meisters, Medivh. Als bescheidener Pförtner kann ich Euch nicht viel mehr sagen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10114', 'deDE', 'Alle Fragen bezüglich Medivh sollten an Moroes gerichtet werden, den Turmwärter. Ich selbst weiß nicht viel über die Geschäfte meines Meisters. Ich glaube, einige Adlige haben im Moment eine Audienz b', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10115', 'deDE', 'Von hier im Torhaus stehen Euch drei Wege offen. Die Treppe hinter mir führt zum großen Ballsaal, wo im Moment Gäste unterhalten werden. Ich bin sicher, die nahen Ställe könnt Ihr bis hier riechen, un', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10117', 'deDE', 'Mittnacht ist das persönliche Reittier von Attumen dem Jäger. Ich habe niemals ein schnelleres, stärkeres oder niederträchtigeres Pferd gesehen. Und der Reiter ist sogar noch temperamentvoller! Jeder ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10118', 'deDE', 'Diese verdammten Spinnen, endlos kreischenden Fledermäuse und diese verfluchten Dämonenbestien haben sich in den Bedienstetenunterkünften breit gemacht. Es ist dort nicht mehr sicher, vor allem, wenn ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10119', 'deDE', 'Oh ja, ich habe sie nie selbst gesehen, aber manchmal höre ich sie von weitem. Haltet Eure Augen offen, wenn Ihr euch dort hinein begebt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10122', 'deDE', 'Grüße! Vor Euch befindet sich eines der außerordentlichsten Musikinstrumente ganz Lordaerons. Es ist eine wahre Ehre, für den Meister und all seine Gäste zu spielen. Nehmt Platz im Publikum - wie ich ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10124', 'deDE', 'Es ist hier... irgendwo... Ich weiß es. Irgendwo in dieser Bücherei steckt das Wissen, das ich suche.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10125', 'deDE', '', 'Dieser Turm ist durch und durch verrückt. Das ist keine Bücherei, das ist ein großer Heuhaufen von Büchern, und ich glaube, es gibt gar keine Nadel! Nun, wo war ich stehen geblieben?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10126', 'deDE', 'Eure Anwesenheit hier behindert sehr wichtige Arbeiten. Bitte verlasst diesen Ort sofort. Nun, wo war ich stehen geblieben?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10127', 'deDE', 'Ah, welch seltsamer Ort, um mögliche Kunden zu finden!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10152', 'deDE', 'Grüße, Fleischling. Ich bin Navuud, Chefforscher hier im Wachposten des Protektorats.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10153', 'deDE', '', 'Kael\'thas zu verlassen war die beste Entscheidung unseres Lebens. Seine Gefolgsleute sind zu blind oder zu ängstlich, zu erkennen, wohin er sie führt.$B$BWir hingegen sind das nicht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10178', 'deDE', 'Es gibt solche, die nach Lügen und Unwahrheiten suchen. Seher hingegen bringen die Wahrheit ans Licht.$B$BDer Sinn dieser Bibliothek ist es, aus jedem einen Seher zu machen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10179', 'deDE', 'Unter uns Astralen gibt es bestimmte Leute, die sich besonders um Fleisch und Fleischwesen kümmern. Diese Individuen werden Fleischtreiber genannt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10180', 'deDE', 'Sucht Ihr etwas in unserem schönen Land?', 'Sucht Ihr etwas in unserem schönen Land?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10181', 'deDE', 'Himmelsmeisterin Abendlicht steht vor der Stadt Silbermond, in der Nähe des Magistertors und kümmert sich um die Drachenfalken.', 'Himmelsmeisterin Abendlicht steht vor der Stadt Silbermond, in der Nähe des Magistertors und kümmert sich um die Drachenfalken.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10182', 'deDE', 'Wenn Ihr eine Gilde registrieren wollt, solltet Ihr Euch innerhalb von Silbermond erkundigen.', 'Wenn Ihr eine Gilde registrieren wollt, solltet Ihr Euch innerhalb von Silbermond erkundigen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10183', 'deDE', 'Delaniels Gasthaus befindet sich in der südöstlichen Ecke des Falkenplatzes. Gutes Essen, edler Wein, weiche Betten... was kann man sich mehr wünschen?', 'Delaniels Gasthaus befindet sich in der südöstlichen Ecke des Falkenplatzes. Gutes Essen, edler Wein, weiche Betten... was kann man sich mehr wünschen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10184', 'deDE', 'Einige Waldläufer scheinen nicht in der Lage zu sein, auf ihre Biester selbst aufzupassen. Wenn ihr solch ein Vieh in der Stadt verstauen müsst, wird sich Anathos darum kümmern. Sie arbeitet am Falken', 'Einige Waldläufer scheinen nicht in der Lage zu sein, auf ihre Biester selbst aufzupassen. Wenn ihr solch ein Vieh in der Stadt verstauen müsst, wird sich Anathos darum kümmern. Sie arbeitet am Falken', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10185', 'deDE', 'Eine Kuhfrau die behauptet, Druide zu sein, hat Ihr Lager in Silbermond aufgeschlagen. Ohne Zweifel grast sie gerade auf unseren Beeten, während wir sprechen.', 'Eine Kuhfrau, die behauptet, Druide zu sein, hat Ihr Lager in Silbermond aufgeschlagen. Ohne Zweifel grast sie gerade auf unseren Beeten, während wir sprechen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10186', 'deDE', 'Die Waldläufer nehmen Unterricht bei Hannovia, einer Weltenwanderin, die sich im nordöstlichen Gebäude des Falkenplatzes aufhält.', 'Die Waldläufer nehmen Unterricht bei Hannovia, einer Weltenwanderin, die sich im nordöstlichen Gebäude des Falkenplatzes aufhält.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10187', 'deDE', 'Garridel lehrt die arkanen Künste am Falkenplatz, Ihr findet sie im ersten Stock des Gasthauses.', 'Garridel lehrt die arkanen Künste am Falkenplatz, Ihr findet sie im ersten Stock des Gasthauses.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10189', 'deDE', 'Blutritterin Noellene trainiert den Nachwuchs im nordöstlichen Gebäude des Falkenplatzes.', 'Blutritterin Noellene trainiert den Nachwuchs im nordöstlichen Gebäude des Falkenplatzes.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10190', 'deDE', 'Wenn Ihr das Wissen der Priester sucht, ist Ponaris Euer Mann. Er arbeitet mit den anderen im zweiten Stock von Delaniels Gasthaus.', 'Wenn Ihr das Wissen der Priester sucht, ist Ponaris Euer Mann. Er arbeitet mit den anderen im zweiten Stock von Delaniels Gasthaus.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10191', 'deDE', 'Es dort gibt ein Gebäude am Falkenplatz... mit etwas Glück findet ihr darin Tannaria, eine Frau, die sehr gut darin ist, nicht gesehen zu werden.', 'Es dort gibt ein Gebäude am Falkenplatz... mit etwas Glück findet ihr darin Tannaria, eine Frau, die sehr gut darin ist, nicht gesehen zu werden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10192', 'deDE', 'Celoenus ist der hiesige Experte für das Beschwören von Dämonen. Er befindet sich im Gasthaus am Falkenplatz, sicherlich im ersten Stock.', 'Celoenus ist der hiesige Experte für das Beschwören von Dämonen. Er befindet sich im Gasthaus am Falkenplatz, sicherlich im ersten Stock.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10193', 'deDE', 'Ich habe von einem Alchemisten im Ort gehört, Sheynathren ist sein Name, glaube ich. Als ich ihn das letzte Mal sah, war er unterwegs nach Saltherils Hafen, wahrscheinlich, um an einem dieser lächerli', 'Ich habe von einem Alchemisten im Ort gehört, Sheynathren ist sein Name, glaube ich. Als ich ihn das letzte Mal sah, war er unterwegs nach Saltherils Hafen, wahrscheinlich, um an einem dieser lächerli', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10194', 'deDE', 'Die Weltenwanderer haben einen Schmied in ihrer Zuflucht im östlichen Bereich des Immersangwalds. Arathel kann Euch weiterhelfen.', 'Die Weltenwanderer haben einen Schmied in ihrer Zuflucht im östlichen Bereich des Immersangwalds. Arathel kann Euch weiterhelfen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10195', 'deDE', 'Zu Delaniels Angestellten gehört eine wunderbare Köchin namens Quarelestra. Ihr würdet gut daran tun, von ihr zu lernen. Ihr findet sie im Gasthaus am Falkenplatz.', 'Zu Delaniels Angestellten gehört eine wunderbare Köchin namens Quarelestra. Ihr würdet gut daran tun, von ihr zu lernen. Ihr findet sie im Gasthaus am Falkenplatz.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10196', 'deDE', 'Wenn Ihr nach der Verzauberin Eredania sucht, braucht Ihr nur zu Saltherils kleiner Feier zu gehen. Sie erhielt vor ein paar Tagen eine Einladung.', 'Wenn Ihr nach der Verzauberin Eredania sucht, braucht Ihr nur zu Saltherils kleiner Feier zu gehen. Sie erhielt vor ein paar Tagen eine Einladung.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
