INSERT INTO `npc_text_localized` VALUES ('10198', 'deDE', 'Diejenigen, die gewöhnliche Methoden bevorzugen, um Wunden zu heilen, sollten sich bei Kanaria im Gasthaus am Falkenplatz erkundigen.', 'Diejenigen, die gewöhnliche Methoden bevorzugen, um Wunden zu heilen, sollten sich bei Kanaria im Gasthaus am Falkenplatz erkundigen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10199', 'deDE', 'Es gibt einen Angler in unserer Stadt... falls Ihr Spaß daran findet, Eure Zeit mit glitschigen, zappelnden und stinkenden Abscheulichkeiten zu verbringen.', 'Es gibt einen Angler in unserer Stadt... falls Ihr Spaß daran findet, Eure Zeit mit glitschigen, zappelnden und stinkenden Abscheulichkeiten zu verbringen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10200', 'deDE', 'Wer könnte sich besser um Blumengestecke für Feiern kümmern als Botaniker Tyniarrel? Ich bin sicher, Saltheril bezahlt ihn mit Wein.', 'Wer könnte sich besser um Blumengestecke für Feiern kümmern als Botaniker Tyniarrel? Ich bin sicher, Saltheril bezahlt ihn mit Wein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10202', 'deDE', 'Ameer mag sich vielleicht Sorgen darüber machen, was das Astraleum vorhat, aber ich konzentriere mich auf die echte Gefahr hier: Dimensius.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10203', 'deDE', 'Unsere ansässige Juwelenschleiferin, Aleinia, kann in Delaniels Etablissement am Falkenplatz gefunden werden. Es ist möglich, dass sie nach einem Lehrling sucht.', 'Unsere ansässige Juwelenschleiferin, Aleinia, kann in Delaniels Etablissement am Falkenplatz gefunden werden. Es ist möglich, dass sie nach einem Lehrling sucht.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10204', 'deDE', 'Sathein mag Luchspelze aus dem Immersangwald für seine Kreationen. Ihr solltet mit ihm sprechen; er steht zusammen mit Mathreyn südwestlich des Magistertores.', 'Sathein mag Luchspelze aus dem Immersangwald für seine Kreationen. Ihr solltet mit ihm sprechen; er steht zusammen mit Mathreyn südwestlich des Magistertores.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10205', 'deDE', 'Bergleute könnt Ihr innerhalb von Silbermond finden, sicherlich in der Nähe dieser stinkenden Schmieden, wo sie ihre Erträge verhütten.', 'Bergleute könnt Ihr innerhalb von Silbermond finden, sicherlich in der Nähe dieser stinkenden Schmieden, wo sie ihre Erträge verhütten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10206', 'deDE', 'Mathreyn ist bewandert im barbarischen Brauch des Häutens, wenn das wirklich Euer Begehr sein sollte. Er versorgt Sathein mit Leder, ihr könnt beide südwestlich des Magistertors finden.', 'Mathreyn ist bewandert im barbarischen Brauch des Häutens, wenn das wirklich Euer Begehr sein sollte. Er versorgt Sathein mit Leder, ihr könnt beide südwestlich des Magistertors finden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10207', 'deDE', 'Die gute Näherin ist in letzter Zeit in Saltherils Hafen beschäftigt, ihre Begabung mit Nadel und Faden wird dort sicherlich sehr geschätzt.', 'Die gute Näherin ist in letzter Zeit in Saltherils Hafen beschäftigt, ihre Begabung mit Nadel und Faden wird dort sicherlich sehr geschätzt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10208', 'deDE', 'Was wollt Ihr? Sprecht!', 'Was wollt Ihr? Sprecht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10209', 'deDE', 'Zu einer anderen Zeit an einem anderen Ort würde ich als Jüngling betrachtet. Hier bin ich ein erfahrener Kämpfer, der bereit ist, für die Ehre der Naaru zu leben und zu sterben.$B$BZehn von uns sind ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10211', 'deDE', 'Haben die Seher Euch zu mir geschickt? Ihr seht gar nicht wie ein Seher aus.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10212', 'deDE', 'Sprecht schnell. Ich habe keine Zeit für Plaudereien. Die Verlassenen sind ein ernstzunehmender Faktor, aber es gibt immer noch viel zu tun. Diese Stadt läuft nicht von selbst.$B$BWas wollt Ihr?', 'Sprecht schnell. Ich habe keine Zeit für Plaudereien. Die Verlassenen sind ein ernstzunehmender Faktor, aber es gibt immer noch viel zu tun. Diese Stadt läuft nicht von selbst.$B$BWas wollt Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10214', 'deDE', 'Ihr habt nicht das Zeug dazu, in der Dampfdruckarena zu bestehen, $C. Verschwendet nicht meine Zeit.', 'Ihr habt nicht das Zeug dazu, in der Dampfdruckarena zu bestehen, $C. Verschwendet nicht meine Zeit.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10215', 'deDE', 'In Ordnung, $C. Ihr denkt also, dass Ihr das Zeug dazu habt, in der Kampfarena des Dampfdruckstadions zu bestehen?', 'In Ordnung, $C. Ihr denkt also, dass Ihr das Zeug dazu habt, in der Kampfarena des Dampfdruckstadions zu bestehen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10216', 'deDE', 'Ihr findet die Kampfmeister bei den Schlachtplänen in der Halle der Waffen.', 'Ihr findet die Kampfmeister bei den Schlachtplänen in der Halle der Waffen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10217', 'deDE', '', 'Ich findet die Kampfmeister auf der Terrasse der Krieger.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10218', 'deDE', 'Ihr findet die Kampfmeister im Kriegsraum in der Burg Sturmwind.', 'Ihr findet die Kampfmeister im Kriegsraum in der Burg Sturmwind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10220', 'deDE', 'Geht zur Anhöhe der Jäger. Dort findet Ihr die Kampfmeister.', 'Geht zur Anhöhe der Jäger. Dort findet Ihr die Kampfmeister.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10221', 'deDE', 'Ihr findet die Kampfmeister in der Halle der Kriegerhelden im Tal der Ehre.', 'Ihr findet die Kampfmeister in der Halle der Kriegerhelden im Tal der Ehre.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10222', 'deDE', 'Geht ins Königsviertel. Sie stehen dort alle im Kreis.', 'Geht ins Königsviertel. Sie stehen dort alle im Kreis.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10223', 'deDE', 'Ihr findet ihn im Versorgungszentrum im Sitz der Naaru. Das Licht sei mit Euch.', 'Ihr findet ihn im Versorgungszentrum im Sitz der Naaru. Das Licht sei mit Euch.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10224', 'deDE', 'Oh ja, ich habe Krapfen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10229', 'deDE', 'Fleischling, ich bin Hauptmann Saeed des Protektorats, und die Soldaten, die hier bei mir stehen, sind meine Rächer. Wir erwarten Befehle von dem Fleischling, der die Leerversorgung zerstört hat. Auf ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10230', 'deDE', 'Der Netherdrache schaut Euch prüfend an.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10232', 'deDE', 'Da sind wir, $N. Wir greifen auf Euren Befehl hin an. Sagt Bescheid, und ich und meine Männer werden uns in den Kampf stürzen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10233', 'deDE', 'Willkommen in der Biokuppel Mittelreich, Reisender. Ich möchte Euch davor warnen, Geschäfte mit diesem Goblin Shauly zu treiben. Ich habe wohl nie zuvor jemanden gehört, der das Wort \"Kumpel\" als Satz', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10234', 'deDE', 'Da Allerias Feste auf allen Seiten von Feinden umgeben ist, ist es an der Zeit, sie zu bekämpfen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10236', 'deDE', '', 'Wenn Ihr hierher kommt, macht Euch nützlich, denn der Schattenpirscher da drüben macht nicht sehr viel.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10237', 'deDE', '', 'Die Schlacht um den Berg Hyjal ist eines der bestgehüteten Ereignisse dieser Zeitlinie. Sollte ein Eindringling ihren Ausgang ändern, hätte dies Auswirkungen auf alle folgenden Momente in der Geschich', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10239', 'deDE', 'Was wollt Ihr? Sprecht. Ich vertraue meine Elekk niemandem an, der nicht zuvor seine Hingabe und Freundschaft zu unserem Volk bewiesen hat.', 'Was wollt Ihr? Sprecht. Ich vertraue meine Elekk niemandem an, der nicht zuvor seine Hingabe und Freundschaft zu unserem Volk bewiesen hat.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10245', 'deDE', '', 'Unsere Arbeit im lebenden Hain darf nicht gefährdet werden. Unser Leben hängt davon ab, dass er gesund bleibt und wächst.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10246', 'deDE', 'Falls Ihr noch nicht bei ihr wart, bietet bitte Rina Mondquell eure Hilfe an.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10247', 'deDE', 'Wenn ich recht gehört habe, leistet Ihr bereits gute Arbeit für Sylvanaar.$B$BMacht weiter so, und vielleicht werden wir in der Lage sein, unser Heim hier zu behalten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10249', 'deDE', 'Geht und ruht Euch aus, Wanderer. Unsere Feuer sind Eure Feuer.', 'Geht und ruht Euch aus, Wanderer. Unsere Feuer sind Eure Feuer.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10250', 'deDE', 'Hier durchtrennte Gul\'dan die Verbindung zwischen den Orcs und den Elementargeistern. Sein unstillbarer Durst nach Macht konnte durch die Auslöschung der Draenei nicht gesättigt werden. Er musste auße', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10251', 'deDE', 'Zu Ehren meiner Vorfahren übe ich die alte Tradition der Schamanen aus. Wir dürfen niemals die Opfer vergessen, die sie gebracht haben, damit wir heute hier sein können.$B$BUnd im Gegenzug müssen wir ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10252', 'deDE', 'Vor nicht allzu langer Zeit haben wir den nahen Ogern die Kontrolle über die Donnerfeste abgerungen.$B$BSeid Ihr hier, um uns bei der Verteidigung zu helfen? Ich weiß, dass Gor\'drek nach Unterstützung', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10253', 'deDE', 'Wie findet Ihr den Krieg, $N? Denkt immer daran, warum wir hier sind, das macht das Kämpfen einfacher... und das Sterben auch.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10254', 'deDE', 'Es gibt am Sitz der Naaru zwei Briefkästen: einen bei der Bank, den anderen am Auktionshaus.', 'Es gibt am Sitz der Naaru zwei Briefkästen: einen bei der Bank, den anderen am Auktionshaus.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10255', 'deDE', 'Beim Expeditionsposten stationiert zu sein, ist ein hartes Los, aber so wichtig zur Verteidigung der Ehrenfeste. Die Informationen, die dort gesammelt werden, und der Einsatz unserer Soldaten gegen di', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10256', 'deDE', 'Ich höre, dass Ihr Euch hier bereits nützlich macht.$B$BSehr gut. Ich hatte mir schon Sorgen gemacht, wir müssten Euch \'überzeugen\', hier auszuhelfen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10257', 'deDE', 'Auf der Haut, die zwischen den Knochen straff gespannt ist, steht ein Aufruf gegen die wichtigsten Verteidiger von Sylvanaar und dem lebenden Hain im Westen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10258', 'deDE', '', 'Seid gegrüßt, ich bin Azimi! Ich gehe oft hier entlang und verkaufe den Männern frische Früchte und Wasser. Seid Ihr hungrig, oder vielleicht durstig?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10259', 'deDE', 'Bah! Sehe ich aus, als hätte ich Zeit für faules Geschwätz?$B$BSeht zu, dass Ihr fertig werdet, es warten Oger darauf, getötet zu werden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10264', 'deDE', 'Es tut gut zu sehen, dass $Gein:eine:r; $R wie Ihr zum Wohlergehen von Sylvanaar beiträgt. Wir haben hier in kurzer Zeit viel geschafft, aber wir sind von Streitkräften umgeben, die unsere Art zu lebe', 'Es tut gut zu sehen, dass $Gein:eine:r; $R wie Ihr zum Wohlergehen von Sylvanaar beiträgt. Wir haben hier in kurzer Zeit viel geschafft, aber wir sind von Streitkräften umgeben, die unsere Art zu lebe', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10266', 'deDE', '', 'Willkommen in der Zuflucht des Cenarius, $N. Ich wurde angewiesen, Euch mit Grundlegendem zu versorgen, solltet Ihr etwas benötigen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10269', 'deDE', 'Seid Ihr bereit, im Alteractal gegen die Sturmlanzengarde zu kämpfen, $N?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10270', 'deDE', 'Bronwyn und ich sind Abgesandte der Forscherliga. Wir sind hier auf Geheiß der Allianz, um diese Berge zu studieren.$B$BAber unter uns, nach einem langen Aufenthalt in Silithus brauchen wir einfach et', 'Bronwyn und ich sind Abgesandte der Forscherliga. Wir sind hier auf Geheiß der Allianz, um diese Berge zu studieren.$B$BAber unter uns, nach einem langen Aufenthalt in Silithus brauchen wir einfach et', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10271', 'deDE', '', 'Borgrim könnte Recht haben. Vielleicht brauchen wir eine Pause.$B$BAber jetzt, wo wir hier sind, reizt es mich, anzupacken und dieses Land zu studieren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10275', 'deDE', 'Die Söhne Lothars kämpften einen Krieg ohne Ende. Ich bin erstaunt, dass unsere Ausrüstung noch in einem so guten Zustand ist.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10276', 'deDE', '', 'Gerade in diesem Moment kämpfen Untote wie Ihr gegen den Bund von Arathor im Arathibecken. Werdet Ihr den Entweihten dabei helfen, die Menschen und ihre Verbündeten der Allianz zu besiegen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10277', 'deDE', 'Throm\'ka, $N. Die Kriegshymnenschlucht braucht Helden wie Euch.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10278', 'deDE', 'Unsere Verbündeten aus der Allianz helfen dem Bund von Arathor bei der Verteidigung des Arathibeckens. Sie könnten Eure Hilfe gegen die untoten Entweihten brauchen, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10280', 'deDE', 'Grüße, Reisender. Ich bin Oronok. Bitte, setzt Euch an mein Feuer und wärmt Euch auf.$B$BGebt auf die gefräßigen Biester in der Untiefe vor uns Acht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10282', 'deDE', '', 'Vergesst nicht, $C, die Schlachten in Azeroth toben immer noch. Werdet Ihr zurückkehren und der Horde im Alteractal helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10283', 'deDE', 'Throm\'ka, $C. Wusstet Ihr, dass unser Kampf gegen die Schildwachen der Silberschwingen in der Kriegshymnenschlucht immer noch andauert? Eure Anwesenheit wäre dort sehr willkommen...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10284', 'deDE', 'Ich bin weit gereist um einen Weg zu finden, wie ich den Bund von Arathor im Arathibecken endgültig besiegen kann, aber Adam Eternum, dieser Trottel im Lendenschurz, verfolgt mich auf Schritt und Trit', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10285', 'deDE', '', 'Wie geht\'s? Schon genug von der Scherbenwelt? Bereit zur Rückkehr ins Alteractal und der Horde eine zu verpassen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10286', 'deDE', 'Die Schildwachen der Silberschwingen kämpfen immer noch in der Kriegshymnenschlucht gegen die Horde, $C. Wollt Ihr dorthin zurückkehren, um ihnen zu helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10287', 'deDE', 'Ich kam in die Scherbenwelt in der Hoffnung, ein mächtiges Artefakt zu finden, um den Bund von Arathor im Kampf gegen die Entweihten im Arathibecken zu unterstützen. Alles, was ich bisher gefunden hab', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10288', 'deDE', '<Man bekommt den Eindruck, dass Oric hier nur als Pausenclown herumsteht und nicht beachtet werden sollte. Das würde ihn nur ermutigen.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10290', 'deDE', '<Es ist eine riesige, gepanzerte Kampfkatze mit Sattel. Ist das nicht schon lächerlich genug? Soll sie auch noch reden können?>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10291', 'deDE', '', 'Nur ein Magier kann die Portalmagie erlernen, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10292', 'deDE', '', 'Ein Magier... Ihr wollt also die Magie der Portale erlernen, ja?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10293', 'deDE', '', 'Die königliche Apothekervereinigung aus Unterstadt ist begierig darauf, unseren Orcverbündeten hier in der Scherbenwelt zu helfen. Es ist eine Möglichkeit, unsere Verbundenheit zu stärken, und neue Ti', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10299', 'deDE', 'Der Weißgipfelposten ist ein ungeplanter Halt für mich, um es milde auszudrücken. Meine Kunden zählen darauf, dass ich meine Waren ausliefere, und ich beginne daran zu zweifeln, ob ich es bei all dies', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10300', 'deDE', '', 'Heh, nicht den Käfig öffnen! Verstanden?$B$BDie Oger haben mich genau da, wo ich will.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10301', 'deDE', 'Ich habe gehört, der Staub und die Hitze der Höllenfeuerhalbinsel sollen sehr lästig sein... Ich kann das nicht beurteilen, aber ich vertraue meinen geehrten, noch lebenden Verbündeten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10305', 'deDE', '', 'Ihr kauft hier keinen Falkenschreiter, bevor Ihr Euch unserem Volk gegenüber nicht als würdig und ehrerbietig bewiesen habt.$B$B$BKehrt erst zurück, wenn die Blutelfen Euch mit Ehrfurcht begegnen. For', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10306', 'deDE', 'Diese Orcs scheinen es sehr eilig zu haben, die Scherbenwelt für sich zu beanspruchen! Wenn ich Euch sagen würde, wie viel sie mir für meine Dienste bezahlen, würdet Ihr mich vor Neid töten!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10308', 'deDE', 'Wenn Ihr das nötige Gold habt, kann ich Euch beibringen, wie man auf einem Falkenschreiter reitet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10310', 'deDE', 'Die Blutelfen müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10312', 'deDE', 'Seid Ihr bereit, um die Geschichte des Worts der Verdammnis zu hören, $N?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10313', 'deDE', 'Gut. Die Worte, die Ihr sucht, haben einen Namen, $N. Sie sind als Litanei der Verdammnis bekannt. Es sind die Worte, mit denen Gul\'dan selbst dieses Land zerschlagen hat; der Spruch, der das letzte B', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10314', 'deDE', 'Die Litanei der Verdammnis wurde zwanzig Jahre lang in Gul\'dans Privatgemächern in Karabor aufbewahrt. Ah, richtig, Ihr habt keine Ahnung, was Karabor ist, richtig?$B$BDer Schwarze Tempel...$B$BNun is', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10315', 'deDE', 'Wenn Ihr vor dem Altar der Verdammnis steht und die Geister der Verdammten seht, die im Gebet versunken sind, dann seht Ihr nicht, wie ich neben Gul\'dan stehe.$B$B<Oronok nickt.>$B$BIch habe doch gesa', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10316', 'deDE', 'Diesen Tag werde ich nie vergessen, $R. Nie. Auch wenn ich vor langer Zeit dem Schamanismus zugunsten der Schattenmagie den Rücken gekehrt hatte, fühlte ich den Schnitt. So ein tiefes Gefühl von Verlu', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10317', 'deDE', 'Eine Welle der Übelkeit überkam mich. Nachdem das Ritual vorbei war, rannte ich nach Hause zu meinen Kindern. Ich habe alle Habseligkeiten, die ich mit bloßen Händen tragen konnte, geschnappt und bin ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10318', 'deDE', 'Wie ich bereits gesagt habe, wurde die Litanei von Illidan gefunden, als er den Schwarzen Tempel eingenommen hatte. Er hat den Folianten in drei Teile geteilt und sie seinen Dienern anvertraut.$B$BMei', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10321', 'deDE', 'Willkommen in Shattrath, dem Sitz der Sha\'tar in der Scherbenwelt. Soll ich Euch den Weg weisen?', 'Willkommen in Shattrath, dem Sitz der Sha\'tar in der Scherbenwelt. Soll ich Euch den Weg weisen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10322', 'deDE', '', 'Ich kam hier her, um die primitive Kultur des früheren Klans der Donnerfürsten zu studieren.$B$BSie waren schwach und erlagen der Verlockung der Macht, die sie durch die Verwandlung in Höllenorcs durc', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10323', 'deDE', 'Ar\'tors lebloser Körper wird weiterhin von dämonischer Magie in der Luft gehalten. Selbst im Tode wird er noch gemartert.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10324', 'deDE', '', 'Ich freue mich, dass Ihr Euch entschieden habt, Euch unserer Sache anzuschließen. Als Belohnung werde ich einen Ring mit den Resten aus dem Brunnen der Ewigkeit, die Ihr mir gebracht habt, verstärken.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
