INSERT INTO `npc_text_localized` VALUES ('10329', 'deDE', 'Eure Ausbildung als Schwertschmied zu verlernen ist keine Entscheidung, die man leichtfertig treffen sollte. Wenn Ihr Euch entschließt, sie aufzugeben, werdet Ihr alle Rezepte, die die Schwertschmiede', 'Eure Ausbildung als Schwertschmied zu verlernen ist keine Entscheidung, die man leichtfertig treffen sollte. Wenn Ihr Euch entschließt, sie aufzugeben, werdet Ihr alle Rezepte, die die Schwertschmiede', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10339', 'deDE', 'Ich habe meine Zeit in den Schützengräben verbracht und viele Jahre in diversen Gasthäusern trainiert. Es ist schön, wieder zuhause zu sein. Egal, was daraus geworden ist, es ist mein Zuhause.', 'Ich habe meine Zeit in den Schützengräben verbracht und viele Jahre in diversen Gasthäusern trainiert. Es ist schön, wieder zuhause zu sein. Egal, was daraus geworden ist, es ist mein Zuhause.', 'Es ist eine Ehre, sich um die furchtlosen Tiere zu kümmern, die sich zwischen meine Klansgenossen und den Tod stellen.', 'Es ist eine Ehre, sich um die furchtlosen Tiere zu kümmern, die sich zwischen meine Klansgenossen und den Tod stellen.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10341', 'deDE', 'Die Oger! Sie haben mich hier bis zum Hals eingegraben und vergessen, zurückzukommen!$B$BJa, Mann, lustig! Lach nur. Körperloser Geisterkopf hier.$B$BWenn du mir natürlich helfen willst, werde ich dic', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10349', 'deDE', '<Nuainn Sturmbart schüttelt den Kopf.>$B$BWir müssen alles aufbringen, was wir nur können, um die Feste gegen die Legion auf der einen und Illidans Dienern auf der anderen Seite zu verteidigen. aber w', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10350', 'deDE', 'Haltung annehmen, Grunzer! Ihr steht nun unter dem Kommando der Wache der Kor\'kron!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10352', 'deDE', 'Dieses Land trägt immer noch die Narben der Vergangenheit. Ich frage mich, ob sie je heilen werden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10356', 'deDE', 'Seid vorsichtig, $R. Hinter dieser Brücke liegt die Hölle.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10359', 'deDE', 'Toshleys Station ist die beste! Die Einsamkeit und der Nachschub an merkwürdigen Energiequellen der Scherbenwelt sind wie geschaffen für extreme Experimente!', 'Toshleys Station ist die beste! Die Einsamkeit und der Nachschub an merkwürdigen Energiequellen der Scherbenwelt sind wie geschaffen für extreme Experimente!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10360', 'deDE', 'Habt Ihr mit meinem Klon - ich meine... meinem Zwilling gesprochen?', 'Habt Ihr mit meinem Klon - ich meine... meinem Zwilling gesprochen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10363', 'deDE', 'Ich kann nicht glauben, dass sie mich gezwungen haben, meinen Transporter aus der Stadt zu bringen! Die meisten Gnome, die vom Unfall betroffen waren, haben nach ein paar Wochen wieder eine mehr oder ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10365', 'deDE', 'Mit der Hilfe meines Bruders Zap in der Ewigen Warte habe ich genau hier in Area 52 eine dimensionale Implosionsmatrix konstruiert. Wer braucht schon eine Rakete, wenn man sofortigen Transport mit Hil', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10373', 'deDE', 'Vielen Dank, dass Ihr mich hier rausgeholt habt. Ich schulde Euch etwas!', '', 'Ich sollte jetzt alleine hier herauskommen. Danke!', '', 'Dieser Ort ist verrückt! Ich könnte Jahre damit verbringen, all die Technologie zu erforschen!', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10378', 'deDE', 'Nur ein kleines Liederheft, von dem ich dachte, dass es Euch gefallen könnte. Es enthält den Text des Wehklagens der Hochgeborenen, das Fürstin Sylvanas aus der Luft sang.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10379', 'deDE', 'Es gibt zwei Banken in Shattrath, die sich beide am Rand des zentralen Stadtrings befinden. Eine wird von den Draenei der Aldor betrieben, die andere gehört den Blutelfen der Seher.', 'Es gibt zwei Banken in Shattrath, die sich beide am Rand des zentralen Stadtrings befinden. Eine wird von den Draenei der Aldor betrieben, die andere gehört den Blutelfen der Seher.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10380', 'deDE', 'Die Bank der Aldor befindet sich nordwestlich des Stadtzentrums.', 'Die Bank der Aldor befindet sich nordwestlich des Stadtzentrums.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10381', 'deDE', 'Die Bank der Seher befindet sich südöstlich des Stadtzentrums.', 'Die Bank der Seher befindet sich südöstlich des Stadtzentrums.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10382', 'deDE', 'Die Gasthäuser Shattraths sind überfüllt mit verletzten Flüchtlingen. Die Draenei der Aldor und die Blutelfen der Seher haben jedoch noch ein wenig Platz auf der Aldorhöhe und der Sehertreppe.', 'Die Gasthäuser Shattraths sind überfüllt mit verletzten Flüchtlingen. Die Draenei der Aldor und die Blutelfen der Seher haben jedoch noch ein wenig Platz auf der Aldorhöhe und der Sehertreppe.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10383', 'deDE', 'Das Gasthaus der Aldor befindet sich hoch über Shattrath auf der Aldorhöhe. Der Aufzug, der dorthin führt, befindet sich nordwestlich des Stadtzentrums.', 'Das Gasthaus der Aldor befindet sich hoch über Shattrath auf der Aldorhöhe. Der Aufzug, der dorthin führt, befindet sich nordwestlich des Stadtzentrums.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10384', 'deDE', 'Das Gasthaus der Seher befindet sich über Shattrath auf der Sehertreppe. Der Aufzug, der dorthin führt, befindet sich südwestlich des Stadtzentrums.', 'Das Gasthaus der Seher befindet sich über Shattrath auf der Sehertreppe. Der Aufzug, der dorthin führt, befindet sich südwestlich des Stadtzentrums.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10385', 'deDE', 'Nutral ist der Flugmeister von Shattrath. Ihr findet ihn östlich des Stadtzentrums.', 'Nutral ist der Flugmeister von Shattrath. Ihr findet ihn östlich des Stadtzentrums.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10386', 'deDE', 'Briefkästen könnt Ihr in oder direkt außerhalb der Banken und Gasthäuser der Aldor und der Seher finden.', 'Briefkästen könnt Ihr in oder direkt außerhalb der Banken und Gasthäuser der Aldor und der Seher finden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10387', 'deDE', 'Direkt außerhalb der Gasthäuser der Blutelfen und der Draenei auf der Aldorhöhe und der Sehertreppe könnt Ihr Stallmeister finden.', 'Direkt außerhalb der Gasthäuser der Blutelfen und der Draenei auf der Aldorhöhe und der Sehertreppe könnt Ihr Stallmeister finden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10388', 'deDE', 'Die Kampfmeister Shattraths kommen oft auf den Treppen zwischen dem oberen und dem unteren Viertel zusammen. Die Kampfmeister der Allianz sammeln sich auf der einen Seite, die der Horde auf der andere', 'Die Kampfmeister Shattraths kommen oft auf den Treppen zwischen dem oberen und dem unteren Viertel zusammen. Die Kampfmeister der Allianz sammeln sich auf der einen Seite, die der Horde auf der andere', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10389', 'deDE', 'Die Kampfmeister der Allianz befinden sich in der Regel auf der Treppe nordöstlich des Stadtzentrums.', 'Die Kampfmeister der Allianz befinden sich in der Regel auf der Treppe nordöstlich des Stadtzentrums.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10390', 'deDE', 'Die Kampfmeister der Horde befinden sich in der Regel auf der Treppe südöstlich des Stadtzentrums.', 'Die Kampfmeister der Horde befinden sich in der Regel auf der Treppe südöstlich des Stadtzentrums.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10391', 'deDE', 'Nach welcher Art von Ausbilder sucht Ihr?', 'Nach welcher Art von Ausbilder sucht Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10392', 'deDE', 'Lorokeem, der Alchemiegroßmeister und Ausgestoßene von Skettis, lebt bei den anderen Arakkoa im Nordwesten des unteren Viertels.', 'Lorokeem, der Alchemiegroßmeister und Ausgestoßene von Skettis, lebt bei den anderen Arakkoa im Nordwesten des unteren Viertels.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
