INSERT INTO `npc_text_localized` VALUES ('10394', 'deDE', 'Die Taverne Weltenend befindet sich im östlichsten Teil des unteren Viertels.', 'Die Taverne Weltenend befindet sich im östlichsten Teil des unteren Viertels.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10395', 'deDE', 'Ihr findet Hochverzauberer Bardolan in der Bibliothek der Seher auf der Sehertreppe. Als Großmeister seiner Kunst ist er von seinen Schülern umgeben: den Meistern, den Fachmännern, den Experten und Ge', 'Ihr findet Hochverzauberer Bardolan in der Bibliothek der Seher auf der Sehertreppe. Als Großmeister seiner Kunst ist er von seinen Schülern umgeben: den Meistern, den Fachmännern, den Experten und Ge', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10396', 'deDE', 'Mildred Fletcher hält sich in der Krankenstube im Nordosten des unteren Viertels auf.', 'Mildred Fletcher hält sich in der Krankenstube im Nordosten des unteren Viertels auf.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10397', 'deDE', 'Der Juwelenschleifergroßmeister der Draenei, Hamanar, befindet sich bei seinem Volk auf der Aldoranhöhe.', 'Der Juwelenschleifergroßmeister der Draenei, Hamanar, befindet sich bei seinem Volk auf der Aldoranhöhe.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10398', 'deDE', 'Hansi, der blinde Kürschner Shattraths und Großmeister seiner Kunst hält sich auf dem offenen Marktplatz im unteren Viertel auf.', 'Hansi, der blinde Kürschner Shattraths und Großmeister seiner Kunst hält sich auf dem offenen Marktplatz im unteren Viertel auf.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
