INSERT INTO `npc_text_localized` VALUES ('10400', 'deDE', 'Die Rüstungs- und Waffenschmiede Kradu Grimmklinge und Zula Schmelzzorn befinden sich bei der großen Schmiede im unteren Viertel.', 'Die Rüstungs- und Waffenschmiede Kradu Grimmklinge und Zula Schmelzzorn befinden sich bei der großen Schmiede im unteren Viertel.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10401', 'deDE', 'Wer wagt es, den Großkommandanten zu stören?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
