INSERT INTO `npc_text_localized` VALUES ('10406', 'deDE', 'Aha? Ihr habt natürlich Papiere, mit denen Ihr das beweisen könnt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10407', 'deDE', 'Hört zu, Sklave. Wir haben einen Prozess für die Weitergabe und den Schutz der Litanei. Der Bote überbringt den Schriftwechsel, der von Fürst Illidan versiegelt ist. Kein Brief, keine Weitergabe.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10408', 'deDE', '<Ruusk schluckt schwer.>$B$BIch... Was meint Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10409', 'deDE', 'Wartet. Wo wollt Ihr hin? Ihr wolltet, dass ich das Wort weitergebe, richtig? Dann lasst es uns einfach hinter uns bringen, ja? Zuluhed in der Festung des Drachenmals? So sei es. Bitte berichtet Fürst', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10410', 'deDE', 'Mit der Hilfe von Ingenieur Lapforge in Gadgetzan habe ich es geschafft, seinen Erfolg zu wiederholen und einen Transporter hier bei Toshleys Station gebaut\" Ich wäre sehr glücklich, wenn ich einem an', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10413', 'deDE', '<Der Wasserelementar ist bereit, aufzuwallen.>', '<Der Wasserelementar ist bereit, aufzuwallen.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10414', 'deDE', '<Das Streitross steht stolz und kampfbegierig.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10416', 'deDE', '<Der Kleriker starrt Euch aus dem Augenwinkel an.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10417', 'deDE', '<Der Beschwörer steht angespannt, den Blick in die Ferne gerichtet.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10418', 'deDE', '<Llanes wahre Weisheit und Stärke strahlen hell, selbst in diesem Abbild des Königs.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10421', 'deDE', 'Wir werden kämpfen, wenn Ihr bereit seid, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
