INSERT INTO `npc_text_localized` VALUES ('10423', 'deDE', 'Ja, ja... Willkommen in meiner Station.$B$BWie findet Ihr sie? Schick, was wir hier gebaut haben, oder?$B$BIch bin froh, diese eingebildeten Elfen von Sylvanaar los zu sein.$B$BMacht Euch ruhig etwas ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
