INSERT INTO `npc_text_localized` VALUES ('10444', 'deDE', 'Ich bin ein Wissenschaftler, der sowohl die Zeit als auch den Schall studiert. Ich weiß nicht, wie ich bei all diesem Lärm nachdenken oder arbeiten soll?$B$BWarum können diese Narren sich nicht wo and', 'Ich bin ein Wissenschaftler, der sowohl die Zeit als auch den Schall studiert. Ich weiß nicht, wie ich bei all diesem Lärm nachdenken oder arbeiten soll?$B$BWarum können diese Narren sich nicht wo and', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
