INSERT INTO `npc_text_localized` VALUES ('10452', 'deDE', 'Beim Sondieren handelt es sich um das wichtigste Verfahren zum Finden von Edelsteinen! Ihr wendet einfach Eure Fähigkeit \'Sondieren\' (welche Ihr im Bereich \'Allgemein\' Eures Zauberbuchs findet) auf ei', 'Beim Sondieren handelt es sich um das wichtigste Verfahren zum Finden von Edelsteinen! Ihr wendet einfach Eure Fähigkeit \'Sondieren\' (welche Ihr im Bereich \'Allgemein\' Eures Zauberbuchs findet) auf ei', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10454', 'deDE', 'Was möchtet Ihr über das Juwelenschleifen wissen?', 'Was möchtet Ihr über das Juwelenschleifen wissen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10455', 'deDE', '', 'Solange Ihr die Lehrlingsausbildung des Juwelenschleifens nicht abgeschlossen habt, werden wir uns nicht wieder sprechen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10456', 'deDE', '', 'Mein Wissen über das Juwelenschleifen will ich mit Euch teilen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10457', 'deDE', 'Das Erschaffen von Juwelen und Edelsteinen ist mein Lebensinhalt. Darf ich meine Leidenschaft mit Euch teilen?', 'Das Erschaffen von Juwelen und Edelsteinen ist mein Lebensinhalt. Darf ich meine Leidenschaft mit Euch teilen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10458', 'deDE', '', 'Ich wünschte, ich hätte genug Zeit, um alle zu lehren, die zu mir kommen. Leider ist das nicht der Fall. Solange Ihr nicht alles gelernt habt, was Padaar Euch beibringt, kann ich Euch nicht helfen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10459', 'deDE', '', 'Es ist mir eine Freude, jemandem mit Euren Fertigkeiten mehr über das Juwelenschleifen beizubringen. Was möchtet Ihr wissen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10460', 'deDE', '', 'Ich bin sehr beschäftigt, $R.$B$BWir haben ein schreckliches Problem, um das wir uns sofort kümmern müssen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10465', 'deDE', 'Illidans Griff um dieses Land lockert sich. Könnt Ihr es fühlen, $N?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10466', 'deDE', '', 'Wenn Ihr bei Razaks Raunacken mitmacht, lernt Ihr ein, zwei Dinge über das Befolgen von Befehlen.$B$BErstens: Ihr kämpft bis zum Tod. Zweitens: Siehe erstens.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10467', 'deDE', 'Muss Eure Klinge geschärft werden, oder braucht Ihr vielleicht eine neue Waffe? Vorsicht ist besser als Nachsicht...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
