INSERT INTO `npc_text_localized` VALUES ('10473', 'deDE', '', 'Als ich jung war, habe ich hart trainiert, um Anachoret zu werden. Weibliche Exarchen waren praktisch unvorstellbar.$B$BHeute bedeutet das Geschlecht wenig, Entschlossenheit und Überzeugung sind, was ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10474', 'deDE', 'Wenn Ihr bereit seid und schnell zur normalen Zeitlinie zurückkehren wollt, kann ich Euch zu Andormu in den Höhlen der Zeit transportieren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10475', 'deDE', 'Den Fluss der Zeit zu formen ist eine schwierige Aufgabe, die viel Feingefühl erfordert. Ihr habt heute Großartiges geleistet.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10491', 'deDE', 'Seid Ihr hier, um uns mit den Störungen zu helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
