INSERT INTO `npc_text_localized` VALUES ('10493', 'deDE', 'Sein Name war Varedis. Zu Illidans Überraschung überstand er nicht nur die qualvollen Initiationsriten erfolgreich, die Teil der Ausbildung sind; Varedis verfügte bereits über eine bessere Beherrschun', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
