INSERT INTO `npc_text_localized` VALUES ('10495', 'deDE', 'Gerüchte besagen, dass man ein gewisses Maß an Macht über Dämonen erhält, deren wahren Namen man kennt.$B$BDas Buch der teuflischen Namen soll angeblich die wahren Namen von jedem Dämonen enthalten, d', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
