INSERT INTO `npc_text_localized` VALUES ('10501', 'deDE', 'Mumman hat ihren bescheidenen Laden nahe des Haupteingangs der Stadt eröffnet, direkt neben dem Gasthaus. Eine sehr gute Köchin.', 'Mumman hat ihren bescheidenen Laden nahe des Haupteingangs der Stadt eröffnet, direkt neben dem Gasthaus. Eine sehr gute Köchin.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
