INSERT INTO `npc_text_localized` VALUES ('10565', 'deDE', 'Obwohl die Erforschung der neuen Regionen zweifellos wichtig ist, dürft Ihr Azeroth nicht vergessen! Wir brauchen Euch mehr denn je im Kampf gegen die Entweihten. Die Ressourcen müssen gesichert werde', 'Obwohl die Erforschung der neuen Regionen zweifellos wichtig ist, dürft Ihr Azeroth nicht vergessen! Wir brauchen Euch mehr denn je im Kampf gegen die Entweihten. Die Ressourcen müssen gesichert werde', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10566', 'deDE', 'Ihr da, $C! Wir brauchen Eure Hilfe! Die hässlichen Orcs des Kriegshymnenklans zerstören unser schönes Eschental, und wir brauchen Eure Kampfkraft, um sich ihnen auf dem Schlachtfeld zu stellen. Kehrt', 'Ihr da, $C! Wir brauchen Eure Hilfe! Die hässlichen Orcs des Kriegshymnenklans zerstören unser schönes Eschental, und wir brauchen Eure Kampfkraft, um sich ihnen auf dem Schlachtfeld zu stellen. Kehrt', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10568', 'deDE', 'Es gibt ein altes Sprichwort: \"Gebt einem Mann einen Fisch, und er ist satt für einen Tag. Lehrt einen Mann zu fischen, und er ist sein Leben lang satt.\" Am besten schmecken die Fische natürlich gekoc', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10569', 'deDE', 'Hallo, $N! Was machen Eure Abenteuer? Ich komme mit meinen Experimenten gut voran...$B$BBitte kommt später wieder vorbei! Ich könnte mehr Testflüge für Euch haben!', 'Hallo, $N! Was machen Eure Abenteuer? Ich komme mit meinen Experimenten gut voran...$B$BBitte kommt später wieder vorbei! Ich könnte mehr Testflüge für Euch haben!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10570', 'deDE', 'Kraft und Ehre, $C. Wir brauchen mutige Kämpfer wie Euch im Alteractal. Das Gebiet der Frostwölfe muss verteidigt werden! Werdet Ihr nach Azeroth zurückkehren und uns Hilfe leisten?', 'Kraft und Ehre, $C. Wir brauchen mutige Kämpfer wie Euch im Alteractal. Das Gebiet der Frostwölfe muss verteidigt werden! Werdet Ihr nach Azeroth zurückkehren und uns Hilfe leisten?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
