INSERT INTO `npc_text_localized` VALUES ('10572', 'deDE', '$C, daheim auf Azeroth hindern die Schildwachen der Silberschwingen unser Vorankommen in den Wäldern. Wir brauchen Euch an der Front, um sie zurückzudrängen! Ihr werdet große Ehre und hohen Ruf erlang', '$C, daheim auf Azeroth hindern die Schildwachen der Silberschwingen unser Vorankommen in den Wäldern. Wir brauchen Euch an der Front, um sie zurückzudrängen! Ihr werdet große Ehre und hohen Ruf erlang', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10573', 'deDE', 'Rettet Euch selbst, $R. Flüchtet von diesem Ort! Das Purpursiegel wird Euch hinrichten, wenn sie Euch entdeckten.', 'Rettet Euch selbst, $R. Flüchtet von diesem Ort! Das Purpursiegel wird Euch hinrichten, wenn sie Euch entdeckten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10576', 'deDE', 'Der Wettbewerb in der Dampfdruckarena beginnt bald. Bereits jetzt finden Übungswettkämpfe statt, aber Kämpfe um die Rangliste beginnen erst in $4262d.', 'Der Wettbewerb in der Dampfdruckarena beginnt bald. Bereits jetzt finden Übungswettkämpfe statt, aber Kämpfe um die Rangliste beginnen erst in $4262d.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
