INSERT INTO `npc_text_localized` VALUES ('10601', 'deDE', 'Heh! Habt Ihr vom Kampfwettbewerb des Dampfdruckkartells gehört?$B$Bhr könnt Teamsatzungen bei den Arenaveranstaltern in Gadgetzan und außerhalb der Arenen in Nagrand und im Schergrat erwerben.', 'Heh! Habt Ihr vom Kampfwettbewerb des Dampfdruckkartells gehört?$B$Bhr könnt Teamsatzungen bei den Arenaveranstaltern in Gadgetzan und außerhalb der Arenen in Nagrand und im Schergrat erwerben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10603', 'deDE', 'Meine Verabscheuung für Unterbrechungen wird nur noch übertroffen von meinem Hass auf Gruul den Drachenschlächter und seine sieben Söhne!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10604', 'deDE', 'Die Rangwertung für Arenakämpfe beginnt in $4262d. Die angehenden Champions sind bereits damit beschäftigt, Ihre Fertigkeiten zu trainieren und die beste Ausrüstung zusammenzutragen. Werdet Ihr bereit', 'Die Rangwertung für Arenakämpfe beginnt in $4262d. Die angehenden Champions sind bereits damit beschäftigt, Ihre Fertigkeiten zu trainieren und die beste Ausrüstung zusammenzutragen. Werdet Ihr bereit', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10605', 'deDE', '', 'Ihr müsst... meine Kinder... befreien...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10606', 'deDE', 'Sie können das nicht ewig durchhalten! Irgendwann geben Ihre Flügel nach...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10608', 'deDE', 'Ah, $Gein weiterer:eine weitere:c; $C auf dem Weg zu Gladiatorenruhm? Gut!$B$BIch verkaufe Teamsatzungen für die Arena. Die benötigt Ihr, um an offiziellen Wettkämpfen in der Dampfdruckarena teilzuneh', 'Ah, $Gein weiterer:eine weitere:c; $C auf dem Weg zu Gladiatorenruhm? Gut!$B$BIch verkaufe Teamsatzungen für die Arena. Die benötigt Ihr, um an offiziellen Wettkämpfen in der Dampfdruckarena teilzuneh', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10610', 'deDE', '', 'Beim Rückenbrecherposten stationiert zu sein ist großartig. Die Abnormitäten des Blutenden Auges sind so nahe, dass man ihr fauliges Blut riechen kann! Es gibt keinen besseren Ort, um Rache an einem K', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10612', 'deDE', 'Schönwetter und ich sind hier, um Sylvanaars Interessen zu vertreten.$B$BDie Druiden sind sehr zuvorkommend, bei dem Gedanken, dass die Horde hier durchreist, bekomme ich eine Gänsehaut.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10613', 'deDE', 'Grüße, $GSterblicher:Sterbliche;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10614', 'deDE', 'In den östlichen Ausläufern der Netherschwingenfelder liegt die Festung des Drachenmals. Dort foltern die grausamen Orcs des Drachenmalklans gefangene Drachen, um sie ihrem Willen zu unterwerfen. Viel', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10615', 'deDE', 'Ich... Wir sind machtlos. Illidan fand und brachte den Drachenmalorcs ihren Anführer: Zuluhed. Dieser Orc war einst für die Gefangennahme und Einkerkerung von Alexstrasza verantwortlich. Wie können wi', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10616', 'deDE', '<Neltharaku nickt.>$B$BKarynaku... Sie wird auf der Feste des Drachenmals gefangen gehalten. Ich habe viele Versuche unternommen, sie zu befreien. Alle sind fehlgeschlagen... Ihr habt bereits gegen di', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10625', 'deDE', '', 'Der Kampf gegen Kael\'thas und Illidan wird an allen Fronten fortgesetzt. Eine Niederlage können wir uns nicht leisten.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10635', 'deDE', 'Was? Wo bin ich? Das ist doch alles falsch! Seht Euch an! Seht Euch diesen Ort an! Er ist nicht auf meiner Liste! Das gibt \'ne Menge Papierkram...$B$B<Der Goblin schreibt wie wahnsinnig in ein kleines', 'Was? Wo bin ich? Das ist doch alles falsch! Seht Euch an! Seht Euch diesen Ort an! Er ist nicht auf meiner Liste! Das gibt \'ne Menge Papierkram...$B$B<Der Goblin schreibt wie wahnsinnig in ein kleines', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10636', 'deDE', 'Es gibt Geheimnisse an diesem Ort, die wir Orcs verzweifelt aufdecken wollen. Geheimnisse unserer Vergangenheit, Geheimnisse unserer Zukunft, und Geheimnisse, die Einsicht in unser Innerstes gewähren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10637', 'deDE', 'Es gibt Ärger im Wald. Wir müssen den Ursprung finden, bevor es zu spät ist!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10640', 'deDE', 'Bald werde ich Wurzeln schlagen und schlafen. Bevor ich ruhen kann, gibt es aber noch Dinge, die erledigt werden müssen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10641', 'deDE', 'Ihr habt für kurze Zeit Grün in mein Land gebracht. Das ist genug. Ich habe Frieden gefunden. Ich bin bereit für meinen langen Schlaf.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10643', 'deDE', 'Erfolg! Es ist mir gelungen, mich mit der Höllenfeuerzitadelle und der Magie in ihrem Inneren abzustimmen. Oh, welche Macht!$B$BEs sind zahllose Höllenorcs an diesem Ort... aber das ist nicht alles! I', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10645', 'deDE', 'Unsere Feinde haben uns umzingelt. Ich wünschte mir ein besseres Leben für die Mok\'Nathal, aber die Mächte, mit denen wir im Krieg sind, erlauben das nicht.$B$BWenn Ihr Euch den Mok\'Nathal gegenüber w', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10647', 'deDE', 'Seid Ihr verwirrt von dem, was Ihr seht, $GFremder:Fremde;?$B$BWir beten zu den Geistern unserer Ahnen, indem wir die Flamme ihrer Erinnerung am Leben halten und Ihr Tribut zollen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10649', 'deDE', 'Ich kann Euch in Alchemie unterweisen. Interessiert?', 'Ich kann Euch in Alchemie unterweisen. Interessiert?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10650', 'deDE', 'Ich kann Euch in der Ingenieurskunst unterweisen. Interessiert?', 'Ich kann Euch in der Ingenieurskunst unterweisen. Interessiert?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
