INSERT INTO `npc_text_localized` VALUES ('10652', 'deDE', 'Ich kann Euch in der Schneiderei unterweisen. Interessiert?', 'Ich kann Euch in der Schneiderei unterweisen. Interessiert?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10653', 'deDE', 'Ich kann Euch in der Lederverarbeitung unterweisen. Interessiert?', 'Ich kann Euch in der Lederverarbeitung unterweisen. Interessiert?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
