INSERT INTO `npc_text_localized` VALUES ('10655', 'deDE', 'Es tut gut, Euch wiederzusehen, Freund.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10657', 'deDE', 'Es macht Spaß, herumzuspazieren und mal die Beine auszustrecken. Gibt es etwas, womit ich Euch helfen kann, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10659', 'deDE', 'Seid gegrüßt, $C. Wenn Ihr erlaubt, möchte ich Euch daran erinnern dass mir wahrscheinlich Wächter des Höllenfeuers auf den Fersen sein werden, während ich mich auf die Magien der Höllenfeuerzitadelle', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10661', 'deDE', 'Die Suche nach meinem Volk. Unser Kampf gegen die Allianz. Der Krieg gegen die Oger und die Gronn.$B$BAlle diese Dinge liegen schwer auf meinen Schultern, $C.$B$BSelbst ich kann von Zeit zu Zeit Hilfe', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10662', 'deDE', 'Auchindoun ist die Heimat der Geister meiner Ahnen. Es mag nicht so aussehen, aber alle Zerschlagenen waren einst Draenei... unsere Geister sind die gleichen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10664', 'deDE', 'Die Todesschmiede wird vom Schattenrat betrieben, unter dem wachsamen Auge der Legion.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10666', 'deDE', 'Dort unten befindet sich die Todesschmiede, ein passender Name. Würde man sie gewähren lassen, würde der Schattenrat die Legion mit einem endlosen Strom von Höllenbestien versorgen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10667', 'deDE', 'Wir wissen, dass sich die Wyrmkultisten am anderen Ende des Tunnels sammeln. Wir halten hier die Stellung.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10669', 'deDE', '', 'Willkommen, $N. Mein Fachgebiet ist die Kunst der Verarbeitung von Urmondstoff. Schneider, die sich auf Mondstoff spezialisieren wollen, bitten mich oft darum, sie auszubilden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10670', 'deDE', '', 'Ich fürchte, es gibt nicht viel, dass ich Euch lehren kann, $C. Ihr könnt Euch natürlich meine Waren anschauen, aber Ihr werdet nichts finden, das Euch von Nutzen ist.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10671', 'deDE', '', 'Schön, Euch kennenzulernen! Ich bin Gidge, die örtliche Expertin für Zauberfeuerschneiderei. Nicht dass es hier Konkurrenz gibt, aber wenn Ihr daran interessiert seid, Zauberfeuerschneider zu werden, ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10672', 'deDE', '', 'Wenn Ihr kein Schneider seid, glaube ich nicht, dass meine Muster Euch interessieren, aber Ihr könnt Euch gern umschauen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10673', 'deDE', 'Wenn Ihr Muster oder Wissen der Schattenzwirnschneiderei sucht, werde ich Euch gern behilflich sein.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10674', 'deDE', 'Es tut mir leid, $C, aber meine Waren sind für Leute gedacht, die wissen, welches Ende der Nadel man in die Hand nimmt.$B$BIch kann Euch versichern, bei der Schneiderei geht es um mehr, als Taschentüc', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10675', 'deDE', '', 'Diese Tränen der Göttin wurden von Elune gesegnet, ihre Kraft wird Euch helfen, gegen Archimondes böse Magie zu kämpfen. Nutzt ihre Macht weise.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10676', 'deDE', 'Sie fürchten meine Worte. Sie wollen ein Exempel an mir statuieren. Sie verhöhnen den einen wahren Gott, Neptulon.$B$B<Nar\'biss spuckt auf den Boden.>$B$BVergeltung ist nah, $GSterblicher:Sterbliche;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10677', 'deDE', 'Diese Wüste ist durch schreckliche Magie entstellt. Wir müssen tun, was wir können, um diese große Wunde zu heilen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10679', 'deDE', 'Die Schwelle des Todes ist ein Portal, das zu der Welt führt, in der die Brennende Legion Teufelshunde und ähnliche Schrecken züchtet. Immer, wenn Ihr solch eine Kreatur seht, stammt sie von dort und ', 'Die Schwelle des Todes ist ein Portal, das zu der Welt führt, in der die Brennende Legion Teufelshunde und ähnliche Schrecken züchtet. Immer, wenn Ihr solch eine Kreatur seht, stammt sie von dort und ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10680', 'deDE', 'Ich kam in dieses Land, um die ruhelosen Geister zu beruhigen, aber nach diesem Angriff fürchte ich, dass meine Mission gescheitert ist. So viele wurden von der Wüste verschlungen... Ihr müsst ihnen h', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10682', 'deDE', 'Möge das Licht Euch leiten, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10683', 'deDE', 'Ich verrichte meinen Dienst in der Ehrenfeste mit aufrichtigem Stolz.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10686', 'deDE', '', 'Als ich die Schurkenakademie in Shattrath gründete, war hier bei weitem nicht so viel los. Dann kamen die Flüchtlinge, und die Naaru brauchten jedes verfügbare Haus, um sie unterzubringen.$B$BDie Akad', '', 'Der Trick bei der Verwendung von Entrinnen ist, sich vor Augen zu halten, dass die besten 15 Sekunden Eures Lebens oft auch die letzten sind.', '', 'Schwerter, Klauen, Dolche natürlich... Ich habe Schurken in der Handhabung aller Waffen unterrichtet. Aber Streitkolben, die würde ich niemals verwenden, und ich werde auch keinen Schurken ausbilden, ', '', 'Meine Anfängerklassen sind immer voll von Jünglingen mit leuchtenden Augen, aber es gibt nur wenige, die den ersten Test bestehen.$B$BIch habe es aufgegeben, zu rufen: \'Wenn ich sage, Ihr sollt versch', '', 'Verlasst niemals Euer Heim ohne ausreichende Vorräte an Blitzstrahlpulver, insbesondere, wenn Ihr mit anderen zusammenarbeitet. Bei irgendwem ist es immer alle.$B$BWenn ich die Wahl habe, Materialien ', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10687', 'deDE', 'Ich bin Zarevhi vom Konsortium. Meine Fertigkeiten als Transmutierer sind auf tausenden von Welten bekannt.', 'Ich bin Zarevhi vom Konsortium. Meine Fertigkeiten als Transmutierer sind auf tausenden von Welten bekannt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10689', 'deDE', '', 'Oh, nein! Mein Fei-Fei würde niemals Sachen von jemand anderem stehlen! Er ist ein guter Hund, er braucht nur manchmal etwas Aufmerksamkeit. Wenn Ihr ihm ein Leckerli gebt, werdet Ihr sehen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10691', 'deDE', 'Früher hat es mir Spaß gemacht, von jedem, den ich traf, Herausforderungen anzunehmen, aber solche einfachen Siege langweilen mich heute.$B$BIm Moment bin ich dabei, ein Reagenziengeschäft aufzubauen,', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10696', 'deDE', 'Unser örtlicher Alchemist, Lorokeem, hat seine eigene Ausrüstung mitgebracht. Sie mag sich leicht von der unterscheiden, welche Ihr gewohnt seid, schließlich ist er ein Arakkoa, aber er sollte Euch he', 'Unser örtlicher Alchemist, Lorokeem, hat seine eigene Ausrüstung mitgebracht. Sie mag sich leicht von der unterscheiden, welche Ihr gewohnt seid, schließlich ist er ein Arakkoa, aber er sollte Euch he', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10697', 'deDE', 'Es gibt zwei Juwelenhändler in der Stadt. Einer arbeitet eng mit den Sehern zusammen, der andere ist loyal zu den Aldor. Welchen sucht Ihr?', 'Es gibt zwei Juwelenhändler in der Stadt. Einer arbeitet eng mit den Sehern zusammen, der andere ist loyal zu den Aldor. Welchen sucht Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10698', 'deDE', 'Inessera ist eine kluge Frau, die verschiedene Edelsteine und Juwelenschleiferausrüstung verkauft. Ihr findet Ihren Laden im nördlichen Teil der Aldorhöhe.', 'Inessera ist eine kluge Frau, die verschiedene Edelsteine und Juwelenschleiferausrüstung verkauft. Ihr findet Ihren Laden im nördlichen Teil der Aldorhöhe.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10699', 'deDE', 'Lisrythe kann etwas... einschüchternd sein, aber sie hat, was Ihr sucht. Sie verkauft ihre Waren im östlichen Teil der Sehertreppe.', 'Lisrythe kann etwas... einschüchternd sein, aber sie hat, was Ihr sucht. Sie verkauft ihre Waren im östlichen Teil der Sehertreppe.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10702', 'deDE', '<Das Konstrukt deutet auf den östlichen Teil der Sehertreppe.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10705', 'deDE', 'Ich werde Euch nicht ausbilden, solange Ihr bei den Draenei keinen ehrfürchtigen Ruf erlangt habt.', 'Ich werde Euch nicht ausbilden, solange Ihr bei den Draenei keinen ehrfürchtigen Ruf erlangt habt.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10706', 'deDE', 'Habt vielen Dank! Der Schleier der Finsternis, welcher auf meiner Seele lag, ist verschwunden! Ich kann wieder klar sehen... und erblicke das Antlitz meines Retters!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10707', 'deDE', 'Verschwindet, $C! Lasst uns gewähren, oder Ihr seid als nächstes dran und dann werdet Ihr wahre Verzweiflung erfahren!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10717', 'deDE', 'Macht Euch keine Sorgen. Der Trümmerposten ist robust und wird schon halten! Aber nur für den Fall dass er doch mal auseinanderfallen sollte, haltet Euch immer bereit, auf einen Greifen zu springen...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10718', 'deDE', 'Nun, da Ihr mich besiegt habt, erprobt Eure Fertigkeiten gegeneinander! Übernehmt die Kontrolle über einen der Könige, um mit dem Spiel zu beginnen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10720', 'deDE', 'Ihr wünscht, mich zu sprechen, $R?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10721', 'deDE', 'Ich habe in diesem Ödland viele Gefahren gesehen. Wer weiß schon, welche Kreaturen im Staub lauern, um die Ahnungslosen anzufallen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10723', 'deDE', 'Ihr habt den Sehern noch nicht die Treue geschworen, oder Euer Ruf ist noch nicht hoch genug.', 'Ihr habt den Sehern noch nicht die Treue geschworen, oder Euer Ruf ist noch nicht hoch genug.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10729', 'deDE', '', 'Die Auseinandersetzungen in der Scherbenwelt verlaufen tödlicher als alles, was ich bisher gesehen habe. Die hilflosesten Opfer sind die Kinder, die dabei zurückgelassen werden.$B$BMit der Bedrohung d', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10730', 'deDE', 'Ich fühle mich einsam. Ich wünschte, es gäbe hier mehr Kinder in meinem Alter. Manchmal vermisse ich das Waisenhaus in Shattrath.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10732', 'deDE', '', 'Sind wir schon da?', '', 'Wenn die Dämonen gefallen sind, wovon sind sie denn runtergestürzt?', '', 'Ich glaube, ich brauche ein Bad. *Schnupper* Und Ihr auch!', '', 'Wäre ein Taurenpaladin eine heilige Kuh?', '', 'Sehe ich in diesem Kleid dick aus?', '', 'Ich wette, dass Illidan im Innersten ein netter Kerl ist. Er braucht einfach eine Mutter!', '', 'Ich wette, dass Eiscreme besser schmecken würde, wenn sie gesockelt wäre!', '', 'Jungs sind verlaust und Mädchen sind schön!');
INSERT INTO `npc_text_localized` VALUES ('10734', 'deDE', '', 'Warum heißt die Scherbenwelt denn nicht mehr Draenor?', '', 'Der wirbelnde Nether hat die Form einer Bretzel? Was ist der Nether überhaupt? Die Waisenmatrone hat gesagt, dass ich Leute nicht nach ihren Nethern fragen sollte.', '', 'Sind wir bald da?', '', 'Wenn ich die Luft anhalte, welche Farbe bekommt dann mein Gesicht?', '', 'Tragen Draenei eigentlich Schuhe oder Hufeisen?', '', 'Wenn die Brennende Legion so viele Probleme macht, warum löschen wir sie dann nicht einfach? Dann wär es nur eine Legion.', '', 'Wie könnt Ihr so viel trinken und essen und nicht fett werden?', '', 'Macht ein Naaru das Licht aus, wenn er schlafen will?');
INSERT INTO `npc_text_localized` VALUES ('10735', 'deDE', '<Morthis blickt zum Himmel.>$B$BAuf eine gewisse Weise beneide ich die Freiheit, die Krähe und Corvax genießen. Seine irdischen Verpflichtungen abzustreifen ist in der Tat ein großer Segen.$B$BDie Zuk', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10736', 'deDE', 'Es war wirklich sehr nett, dass Gezhe mich hierher kommen ließ, um zu studieren und all diese interessanten Sachen zu lernen, $G werter Herr : werte Dame;.$B$B<Jheel lässt seinen Kopf hängen.>$B$BIch ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10741', 'deDE', 'Aran wurde bezwungen? Ja... ich kann spüren, dass sich sein Griff gelöst hat - zumindest ein klein bisschen. Es ist gerade genug, damit ich meine Aufgabe als Pförtner erfüllen kann! Möchtet Ihr, dass ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10744', 'deDE', '', 'Die Auseinandersetzungen in der Scherbenwelt verlaufen tödlicher als alles, was ich bisher gesehen habe. Die hilflosesten Opfer sind die Kinder, die dabei zurückgelassen werden.$B$BMit der Bedrohung d', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10745', 'deDE', '<Der Druide scheint in einen tiefen Schlaf gesunken zu sein und seine Umgebung nicht mehr wahrzunehmen.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10746', 'deDE', 'Ich bitte Euch, $R, seid schnell! Sie werden nicht aufhören, mich zu verfolgen! Ich kann ihnen nicht entkommen, nicht einmal hier!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10748', 'deDE', 'Ilyenia Mondfeuer, die Waffenmeisterin der Nachtelfen, hält sich auf der Terrasse der Krieger in Darnassus auf. Sie kann Euch in der Benutzung von Bögen unterweisen.', 'Ilyenia Mondfeuer, die Waffenmeisterin der Nachtelfen, hält sich auf der Terrasse der Krieger in Darnassus auf. Sie kann Euch in der Benutzung von Bögen unterweisen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10750', 'deDE', 'Ilyenia Mondfeuer, die Waffenmeisterin der Nachtelfen auf der Terrasse der Krieger in Darnassus oder Buliwyf, der zwergische Waffenmeister bei \'Schlachtholzwaffen\' in Eisenschmiede, können Euch in der', 'Ilyenia Mondfeuer, die Waffenmeisterin der Nachtelfen auf der Terrasse der Krieger in Darnassus oder Buliwyf, der zwergische Waffenmeister bei \'Schlachtholzwaffen\' in Eisenschmiede, können Euch in der', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10751', 'deDE', 'Buliwyf ist genau der richtige Ansprechpartner für Schusswaffen. Ihr findet ihn in bei \'Schlachtholzwaffen\' in Eisenschmiede.', 'Buliwyf ist genau der richtige Ansprechpartner für Schusswaffen. Ihr findet ihn in bei \'Schlachtholzwaffen\' in Eisenschmiede.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10752', 'deDE', 'Ich bin hier im Namen von Nexusprinz Haramad. In der Sturmsäule sind merkwürdige Dinge im Gange.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10754', 'deDE', 'Der zwergische Waffenmeister Buliwyf kann Euch sowohl in Einhand- als auch in Zweihandäxten unterweisen. Ihr findet ihn bei \'Schlachtholzwaffen\' in Eisenschmiede.', 'Der zwergische Waffenmeister Buliwyf kann Euch sowohl in Einhand- als auch in Zweihandäxten unterweisen. Ihr findet ihn bei \'Schlachtholzwaffen\' in Eisenschmiede.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10755', 'deDE', 'Sowohl Handiir als auch Buliwyf können Euch zeigen, wie man mit Streitkolben aller Größen ordentlich zuschlägt. Ihr findet Buliwyf in Eisenschmiede bei \'Schlachtholzwaffen\'. Handiir verbringt seine Ze', 'Sowohl Handiir als auch Buliwyf können Euch zeigen, wie man mit Streitkolben aller Größen ordentlich zuschlägt. Ihr findet Buliwyf in Eisenschmiede bei \'Schlachtholzwaffen\'. Handiir verbringt seine Ze', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10756', 'deDE', 'Woo Ping ist ein Meister im Umgang mit Stangenwaffen. Er unterrichtet seine Schüler in Wellers Arsenal in Sturmwind.', 'Woo Ping ist ein Meister im Umgang mit Stangenwaffen. Er unterrichtet seine Schüler in Wellers Arsenal in Sturmwind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10757', 'deDE', 'Stabkampf wird von Woo Ping, dem Meister in Wellers Arsenal in Sturmwind und Ilyenia Mondfeuer gelehrt. Letztere unterrichtet auf der Terrasse der Krieger in Darnassus die Nachelfen im Kriegshandwerk.', 'Stabkampf wird von Woo Ping, dem Meister in Wellers Arsenal in Sturmwind und Ilyenia Mondfeuer gelehrt. Letztere unterrichtet auf der Terrasse der Krieger in Darnassus die Nachelfen im Kriegshandwerk.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10758', 'deDE', 'Es gibt zwei Meister des Schwertes: Handiir und Woo Ping. Ersteren findet Ihr auf der Händlertreppe der Exodar und letzterer residiert momentan in Wellers Arsenal in Sturmwind.', 'Es gibt zwei Meister des Schwertes: Handiir und Woo Ping. Ersteren findet Ihr auf der Händlertreppe der Exodar und letzterer residiert momentan in Wellers Arsenal in Sturmwind.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10759', 'deDE', 'Sowohl Ilyenia Mondfeuer auf der Terrasse der Krieger in Darnassus als auch Bixi Wobbelbonk bei \'Schlachtholzwaffen\' in Eisenschmiede können Euch im Umgang mit Wurfwaffen unterweisen.', 'Sowohl Ilyenia Mondfeuer auf der Terrasse der Krieger in Darnassus als auch Bixi Wobbelbonk bei \'Schlachtholzwaffen\' in Eisenschmiede können Euch im Umgang mit Wurfwaffen unterweisen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10764', 'deDE', 'Mein Kumpel Buliwyf hier kann Euch beibringen, wie man Faustwaffen verwendet... oder, wenn Euch das lieber ist, sucht Ilyenia Mondfeuer auf. Sie ist die Waffenmeisterin der Nachtelfen und befindet sic', 'Mein Kumpel Buliwyf hier kann Euch beibringen, wie man Faustwaffen verwendet... oder, wenn Euch das lieber ist, sucht Ilyenia Mondfeuer auf. Sie ist die Waffenmeisterin der Nachtelfen und befindet sic', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10765', 'deDE', 'Buliwyf hier wird Euch gerne alles über Schusswaffen beibringen!', 'Buliwyf hier wird Euch gerne alles über Schusswaffen beibringen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10767', 'deDE', 'Wenn Ihr im Umgang mit Waffen unterwiesen werden möchtet, seid Ihr bei mir genau richtig.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10768', 'deDE', 'Ah, Ihr sucht also nach Ausbildung. Ich werde Euch den Weg weisen. Sagt mir, welche Waffe Euer Interesse geweckt hat.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10770', 'deDE', 'Sowohl Hanashi als auch Sayoc können Euch im Umgang mit Einhand- und Zweihandäxten unterweisen. Ihr findet sie in Orgrimmar im Tal der Ehre.', 'Sowohl Hanashi als auch Sayoc können Euch im Umgang mit Einhand- und Zweihandäxten unterweisen. Ihr findet sie in Orgrimmar im Tal der Ehre.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10771', 'deDE', 'Die Waffenmeister im Tal der Ehre in Orgrimmar, Sanyoc und Hanashi, können Euch lehren, wie man einen Bogen richtig abfeuert. Ileda, die Waffenmeisterin von Silbermond, auf dem Platz der Weltenwandere', 'Die Waffenmeister im Tal der Ehre in Orgrimmar, Sanyoc und Hanashi, können Euch lehren, wie man einen Bogen richtig abfeuert. Ileda, die Waffenmeisterin von Silbermond, auf dem Platz der Weltenwandere', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10775', 'deDE', 'Unser Ausbilder für Schusswaffen ist Ansekhwa, der taurische Waffenmeister auf der unteren zentralen Anhöhe von Donnerfels.', 'Unser Ausbilder für Schusswaffen ist Ansekhwa, der taurische Waffenmeister auf der unteren zentralen Anhöhe von Donnerfels.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
