INSERT INTO `npc_text_localized` VALUES ('10835', 'deDE', 'Seid vorsichtig, Freunde. Der Verräter verweilt im Hof direkt vor uns.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10837', 'deDE', '', 'Shh! Seid leise. Die Kinder schlafen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10841', 'deDE', '', 'Macht besser schnell die Augen auf, Grünschnabel. Dies ist nicht unser kuscheliger Hinterhof vor Skettis, das ist der Schergrat! Ein falscher Schritt, und es ist das Ende für Euch!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10842', 'deDE', 'Nun, Weissager Javad befindet sich in der Nähe der Großen Schmiede, ein wenig im Norden auf der östlichen Seite.', 'Nun, Weissager Javad befindet sich in der Nähe der Großen Schmiede, ein wenig im Norden auf der östlichen Seite.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10843', 'deDE', 'Ihr findet Meister Pyreanor bei Thralls Beratern in seiner Festung, der Feste Grommash im Tal der Weisheit.', 'Ihr findet Meister Pyreanor bei Thralls Beratern in seiner Festung, der Feste Grommash im Tal der Weisheit.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10846', 'deDE', 'Ich kann Euch im Juwelenschleifen unterweisen. Seid Ihr interessiert?', 'Ich kann Euch im Juwelenschleifen unterweisen. Seid Ihr interessiert?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10847', 'deDE', 'Was wolle? Riechen komisch für Höllenorc...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10848', 'deDE', 'Ihr diesen Ort nicht zu Eurem Zuhause machen. Vielleicht Ihr wollt was essen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
