INSERT INTO `npc_text_localized` VALUES ('10851', 'deDE', '<Das Licht, das Xi\'ri umgibt, pulsiert blau. Der Naaru heißt Euch als Verbündeten auf dem Schlachtfeld willkommen.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10854', 'deDE', 'Bei Eurem Blute seid Ihr von nun ab an die Netherschwingen gebunden, $G Bruder:Schwester;. Alle Netherschwingen stehen hinter Euch! Die Zeit ist gekommen, um unsere Feinde direkt ins Herz zu treffen, ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10855', 'deDE', 'Ihr habt viel für unsere Sache getan, $N, aber Ihr könnt nicht weitermachen, bis Ihr die Kunst des Fliegens zur Meisterschaft gebracht habt. Nur die unerschütterlichsten Reiter können die Netherschwin', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10860', 'deDE', 'Ihr fragt Euch sicher, warum ich für einen Oger so gut spreche. Das ist ein Nebeneffekt der Kristalle, die auf der Terrasse um Ogri\'la herum wachsen. Die Oger dort haben mir diesen Stab geschenkt, und', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10862', 'deDE', 'Noch einer, was? Ihr werdet es nie schaffen...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10863', 'deDE', 'Vergesst nicht, dass Ihr pro Tag immer nur eine Sammelaufgabe annehmen könnt. Außerdem suchen wir immer nach weiteren Kristallen. Jeder wertlose Peon kann die zum Basislager zurückbringen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10864', 'deDE', 'Es gibt hier nichts mehr für Euch zu tun, Kind. Körperliche Arbeit ist nichts mehr für Euch...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10866', 'deDE', 'Illidan hat meinen Stamm fest im Griff, $N. Doch das wird alles bald Geschichte sein, so oder so.$B$BSeid Ihr bereit, $N? Die Zukunft meines Volkes liegt in Euren Händen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10877', 'deDE', 'Schweigt still über alles, was Ihr hier hört und seht, $R.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
