INSERT INTO `npc_text_localized` VALUES ('10884', 'deDE', '', 'Ich habe ein Angebot für Euch, $R. Bitte bedenkt Eure Entscheidung gut - es wird nicht so leicht, sie rückgängig zu machen.$B$BEs ist an der Zeit, dass wir Eure Treue zu unserer Organisation offiziell', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10886', 'deDE', 'Willkommen in Keelens tadelloser Schneiderei. Ich glaube, dass mein Laden Eure Bedürfnisse erfüllen kann. Wenn Ihr eine Ausbildung von mir möchtet, dann fragt einfach. Ich bin mehr als erfreut, Euch a', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10887', 'deDE', 'Wie kann ich Euch helfen, $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10888', 'deDE', 'Der Schädelhaufen riecht nach Verfall. Ihr befürchtet, dass die Arakkoa ihn erst vor kurzem erhöht haben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10889', 'deDE', '', 'Wenn Ihr auf der Suche nach Dingsdas, Zeugs, Soundsos, Kram, Krust oder Wasauchimmers seid, dann seid Ihr hier genau richtig!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10892', 'deDE', 'Ihr kommt mir bekannt vor...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10893', 'deDE', 'Mein Blick ist voller Visionen von Ereignissen, die überall auf der Welt stattfinden und vielleicht noch stattfinden werden. Viele von ihnen sind grausam, aber wir dürfen nicht glauben, dass wir sie n', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10894', 'deDE', 'Wenn Ihr Reagenzien benötigt, seid Ihr hier genau richtig. Ihr werdet in ganz Shattrath keine bessere Auswahl finden.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10896', 'deDE', '<Lehmklump poliert seine Stiefel.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10901', 'deDE', 'Steht stramm, Soldat!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10903', 'deDE', 'Tausend Flüche über Euch, $R! Dieses Gefängnis wird mich nicht lange halten!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10904', 'deDE', 'Haut ab, bevor ich Euch mit meinem Schuhmerang verprügle.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10906', 'deDE', 'Oh, meine Augenklappe gefällt Euch wohl, was? Na, schaut lieber zu, dass Ihr selbst nie eine bekommt, $G Bürschchen : Mädel;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10907', 'deDE', '', 'Hm? Was? Ich bin hier ziemlich beschäftigt, Freund!$B$BWenn Ihr jedoch hier seid, um zu helfen, habe ich alle Zeit der Welt für Euch.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
