INSERT INTO `npc_text_localized` VALUES ('10916', 'deDE', 'Wir hoffen, dass Ihr unsere Kopfschmerzen etwas lindern könnt. Versucht Ihr mal zehn Söhne zu haben, die alle etwas tun wollen, für das sie nicht bestimmt sind!$B$BDennoch, als Vater, tun wir, was wir', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
