INSERT INTO `npc_text_localized` VALUES ('10919', 'deDE', 'Als Neuling in Ogri\'la seid Ihr den Kristallen noch nicht lange genug ausgesetzt, um ihre Vorteile genießen zu können. Und dazu habt Ihr nur einen Kopf!$B$BWir dagegen sind schon seit langer Zeit hier', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
