INSERT INTO `npc_text_localized` VALUES ('10924', 'deDE', 'Gahk jetzt schlau! Mich wissen wie machen die Rune der Dunkelheit zu die kristallgeschmiedete Rune der Dunkelheit!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
