INSERT INTO `npc_text_localized` VALUES ('10927', 'deDE', 'Im Namen aller Einwohner von Ogri\'la danken wir Euch für Eure Bemühungen, $N.$B$B<Chu\'a\'lors linker Kopf nickt während sein rechter Kopf Euch anlächelt.>$B$BUnd vergesst nicht, dass Ihr mit Eurer Hilf', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10929', 'deDE', 'Nein. Ihr seid noch nicht stark genug, um an gewerteten Wettkämpfen teilzunehmen. Ihr könnt aber einen Kampfmeister der Arena aufsuchen, um ein paar Übungsrunden einzulegen. Es ist niemals zu früh, mi', 'Nein. Ihr seid noch nicht stark genug, um an gewerteten Wettkämpfen teilzunehmen. Ihr könnt aber einen Kampfmeister der Arena aufsuchen, um ein paar Übungsrunden einzulegen. Es ist niemals zu früh, mi', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10930', 'deDE', 'Seid Ihr hier, um mir wehzutun? Ich habe mit dem Krieg nichts zu tun, $R.$B$BIch bin nur ein kränklicher Gelehrter, bitte lasst mich in Frieden!', 'Seid Ihr hier, um mir wehzutun? Ich habe mit dem Krieg nichts zu tun, $R.$B$BIch bin nur ein kränklicher Gelehrter, bitte lasst mich in Frieden!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10932', 'deDE', 'Das Ei scheint so unverwüstlich zu sein, wie man es von dem schwarzen Drachenschwarm erwarten sollte. Wenn Ihr eine große Anzahl von Apexissplittern daneben legen würdet, könnten die Schwingungen es v', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10934', 'deDE', '', '<Angela gurrt den Teufelsjäger an, bevor sie sich Euch zuwendet.>$B$BJir\'see war schon immer kleiner als die anderen Teufelsjäger, aber das hat ihm nie etwas ausgemacht. Und die Scherbenwelt wird das ', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10936', 'deDE', 'Die Expedition des Cenarius hat mich vor dem sicheren Tod in den Minen bewahrt. Ich schulde ihnen mein Leben! Ich biete meine Dienste für Reparaturen und Reagenzien für alle Verbündeten der Expedition', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10937', 'deDE', 'Ihr werdet hier einiges an Arbeit finden, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10938', 'deDE', 'Ach, Ihr wahrt aber fleißig, was? Ich kann Euch nicht genug dafür danken, dass Ihr die Dämonen weggebombt und uns mit Reittieren versorgt habt.$B$BIhr macht unsere Arbeit viel leichter. Macht weiter s', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10939', 'deDE', '', 'Wir müssen die Minen wieder in unseren Würgegriff bekommen!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10940', 'deDE', '<Der Aufpasser der Finsterblut grunzt.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
