INSERT INTO `npc_text_localized` VALUES ('10948', 'deDE', 'Das Apexisrelikt schimmert, Anzeichen einer versteckten Intelligenz im Inneren.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10949', 'deDE', 'Das Apexismonument ragt vor Euch auf.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10950', 'deDE', 'Stillgestanden, Hauptmann!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10951', 'deDE', 'Brennende Augen in dem Prisma erfüllen Euren Geist mit einem Gefühl von Angst... besonders da Ihr wisst, dass Ihr die fünfunddreißig benötigten Apexissplitter nicht bei Euch habt, um seinen Dämon zu b', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10953', 'deDE', 'Ihr seid eines der Jungtiere, nicht wahr? Meine Augen lassen mich im Stich.$B$BIch nehme an, dass Ihr hier seid, um einen meiner exotischen Texte zu erwerben?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10957', 'deDE', 'Vergesst Vixtons Angebot. Ihr wollt das gute Zeug? Dann redet mit Krixel hier. Ich habe, was Ihr braucht, solange Ihr den richtigen Ruf habt. Ihr wisst schon, in der Arena. Dieses Zeug ist nicht für W', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10965', 'deDE', 'Willkommen, Freund. Was würdet Ihr gerne sehen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10966', 'deDE', 'Grüße, $G Bruder : Schwester;. Womit können wir Euch dienen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10967', 'deDE', 'Wie können wir Euch helfen?', 'Wie können wir Euch helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10974', 'deDE', 'Noch immer hungrig, $G Bruder : Schwester;? Wie können wir helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
