INSERT INTO `npc_text_localized` VALUES ('10977', 'deDE', '', 'He, Kamerad. Wenn Ihr Euch in der Himmelswache bewiesen habt, kann ich Euch einen direkten Flug zum Schattenwindtal in den Skethylbergen anbieten.$B$BGefällt Euch mein schöner Netherrochen? Er ist wun', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10978', 'deDE', '', 'Hallo $N. Müsst Ihr schnell zum Schattenwindlager, unserem Posten in den Skethylbergen? Ich habe einen schönen kleinen Netherrochen, der nur darauf wartet.$B$BWollt Ihr dorthin? Ihr müsst es nur sagen', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10979', 'deDE', 'Hallo, $G Sire : holde Dame;! Wenn Ihr es wünscht, kann Euch mein Netherrochen hier zum Außenposten der Himmelswache oben im Schergrat bringen.$B$BNur, damit Ihr Bescheid wisst.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10980', 'deDE', 'Einen guten Tag wünsche ich Euch. Wenn Ihr bei der Himmelswache der Sha\'tari etwas weiter im Rang aufgestiegen seid, werde ich Euch einen direkten Flug zum Außenposten der Himmelswache, unserem Stützp', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10987', 'deDE', 'Trinkt oder geht!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10988', 'deDE', 'Habt Ihr nichts besseres zu tun, als mich vollzulabern?$B$BNun zieht los und zeigt, was Ihr wert seid!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10992', 'deDE', '', 'Ich merke, dass Ihr einiges an Zeit für uns opfert, $C.$B$BGebt noch mehr, die Dämonen sind noch nicht weg, Neuling.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10994', 'deDE', '', 'Euren Namen hört man überall im Außenposten. Lasst Euch das nicht zu Kopf steigen, ich weiss, dass Ihr noch mehr drauf habt!$B$BIch behalte Euch im Auge, Neuling.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10995', 'deDE', '', 'Also, ich gebe zu, Ihr habt Euch bewiesen, und mehr, $N.$B$BWenn Ich vorher hart zu Euch war, lag das daran, dass ich ein Fünkchen einer wahren Himmelswache in Euch gesehen habe. Wenn ich das so sagen', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10997', 'deDE', '', 'He da, $C!$B$B<Khatie schaut Euch gewitzt an.>$B$BWenn Ihr uns noch mehr Rochen bändigen wollt, dann kommt einfach bei mir vorbei, ja?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11000', 'deDE', 'Wonach sucht Ihr?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11001', 'deDE', '', 'Wisst Ihr, $N, es ist eine große Ehre, mit Euch zusammen zu arbeiten!$B$BWenn Ihr gerade nicht mit Bändigen beschäftigt seid, könnten wir ja vielleicht $G mal irgendwo etwas essen gehen? : mal einen F', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11002', 'deDE', '', 'So, jetzt seid Ihr ja schon eine Weile hier, aber wir scheinen uns immer zu verpassen, um nach der Arbeit was zusammen zu unternehmen.$B$BLiegt es an mir?$B$B<Khatie zwingt sich, heiter auszusehen.>$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11004', 'deDE', '', 'Ich hoffe, dass Ihr nicht denkt, dass ich Euch verfolge, $N.$B$BIch meine, ich weiß, dass Ihr jetzt in der Himmelswache sehr berühmt seid und so... und ich bin mir sicher, dass Ihr Euch nicht mit eine', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11007', 'deDE', 'Die Relikte sind ein wahrhaft großes Mysterium. Wenn Ihr sie regelmäßig studiert, könnt Ihr ihre Fähigkeiten vielleicht besser einschätzen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
