INSERT INTO `npc_text_localized` VALUES ('11010', 'deDE', 'Die Leute hier beginnen, Euren Namen mit Respekt auszusprechen.$B$B<Chu\'a\'lors linkes Auge durchbohrt Euch mit einem steinigen Blick.>$B$BMacht weiter so, $N!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11011', 'deDE', 'Zuerst wurdet Ihr $GKönig:Königin; der Oger dort unten, nun habt Ihr ehrfürchtigen Ruf bei uns von Ogri\'la.$B$B$GKein:Keine:r; $R hat das jemals geschafft, doch Ihr seid der lebende Beweis. Ich danke ', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11016', 'deDE', 'Hier in Ogri\'la haben wir genug Zeit, um das Rätsel des Stahls zu lösen. Aber genug davon, wie können wir Euch helfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
