INSERT INTO `npc_text_localized` VALUES ('11029', 'deDE', 'Gefällt es Euch hier bei uns in Ogri\'la?$B$BEs gibt viele Dinge, die unsere Oger herstellen, Tag und Nacht. Das Beste heben wir für unsere liebsten Gäste auf, wie Euch!$B$BAlso, wenn Ihr da draußen se', 'Gefällt es Euch hier bei uns in Ogri\'la?$B$BEs gibt viele Dinge, die unsere Oger herstellen, Tag und Nacht. Das Beste heben wir für unsere liebsten Gäste auf, wie Euch!$B$BAlso, wenn Ihr da draußen se', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
