INSERT INTO `npc_text_localized` VALUES ('11035', 'deDE', 'Denkt daran, $N, der Krempel steht Euch zur Verfügung, wenn Ihr gute Taten begeht. Ist sowas wie Karma oder so.$B$BAlso, helft wo Ihr könnt, auch bei unseren Freunden von der Himmelswache im Norden.$B', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11036', 'deDE', 'Chu\'a\'lor sagt nette Sachen über Euch, $N. Wenn Ihr wollt, haben wir ein paar Sorten spezielles Ogergebräu für Euch zum Verkauf.$B$BDie sind echt lecker!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
