INSERT INTO `npc_text_localized` VALUES ('11056', 'deDE', 'Wir beginnen einen Angriff auf den Landeplatz von Bash\'ir, um die Kristallschmiede zu studieren. Helft uns, wenn Ihr könnt! Unser Äthertechniker der Himmelswache startet in $4581d.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
