INSERT INTO `npc_text_localized` VALUES ('11078', 'deDE', 'Illidans Lakaien bewachen immer noch diese Hallen, $N. Sobald sie besiegt sind, erlauben mir meine Kräfte, Euch weiter ins Innere des Tempels zu bringen.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11081', 'deDE', 'Ich spüre, dass Ihr den Weg zu meinen Brüdern frei gemacht habt. Meine Verbindung zu ihnen und zum Tempel ist immer noch stark. Möchtet Ihr Euch weiter hineinwagen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
