INSERT INTO `npc_text_localized` VALUES ('11217', 'deDE', 'Ich hoffe doch sehr, dass Ihr wisst, wer ich bin. Trotzdem wollt Ihr mich hier stören?$B$BWeshalb nur sollte ich Euch meine Aufmerksamkeit schenken?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11219', 'deDE', 'Oh, jetzt kommt schon, macht Euch nicht lächerlich! Ich bin der einzig wahre Budd Winterhäldler!$B$BIch habe dieses Land vom Gipfel des Berges Hyjal bis hin zu den Tiefen des Versunkenen Tempels durch', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
