INSERT INTO `npc_text_localized` VALUES ('11224', 'deDE', '', 'Seid gegrüßt, $C. Ich erlerne die Kunst der Greifenhaltung von Baldruc dort drüben. Ich hoffe, dass ich eines Tages eine eigene Greifenzucht besitzen werde.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
