INSERT INTO `npc_text_localized` VALUES ('11231', 'deDE', '', 'Ich? Kochkünste? Ich nehme an, dass das stimmt. Eine Bardame muss bisweilen auch in der Küche arbeiten. Im Laufe der Jahre konnte ich mir ein paar Rezepte aneignen. Nichts besonderes, aber sie sind he', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11234', 'deDE', 'Mein Bruder Krixx beschwert sich ständig über die Zustände in Orgrimmar. Er sollte mal den hiesigen Gestank erleben, der ist wahrlich unerträglich! Zumindest ist der Boss so anständig und zahlt mir ei', 'Mein Bruder Krixx beschwert sich ständig über die Zustände in Orgrimmar. Er sollte mal den hiesigen Gestank erleben, der ist wahrlich unerträglich! Zumindest ist der Boss so anständig und zahlt mir ei', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
